-- ----------------------------
-- Table structure for board
-- ----------------------------
DROP TABLE IF EXISTS `board`;
CREATE TABLE `board` (
  `id` int(9) NOT NULL AUTO_INCREMENT COMMENT 'ไอดีบอร์ด',
  `title` varchar(255) NOT NULL COMMENT 'หัวข้อ',
  `content` text NOT NULL COMMENT 'เนื้อหา',
  `create_time` int(11) NOT NULL COMMENT 'วันที่สร้าง',
  `update_time` int(11) NOT NULL COMMENT 'วันที่ปรับปรุง',
  `author_id` int(9) NOT NULL COMMENT 'เจ้าของกระทู้',
  `views` smallint(5) NOT NULL COMMENT 'จำนวนเข้าชม',
  PRIMARY KEY (`id`),
  KEY `FK_users` (`author_id`),
  CONSTRAINT `board_ibfk_1` FOREIGN KEY (`author_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='เว็บบอร์ด';

-- ----------------------------
-- Records of board
-- ----------------------------

-- ----------------------------
-- Table structure for board_comment
-- ----------------------------
DROP TABLE IF EXISTS `board_comment`;
CREATE TABLE `board_comment` (
  `id` int(7) NOT NULL AUTO_INCREMENT COMMENT 'ไอดีข่าว',
  `topicid` int(9) NOT NULL COMMENT 'รหัสกระทู้',
  `title` varchar(255) NOT NULL COMMENT 'หัวข้อ',
  `content` text NOT NULL COMMENT 'เนื้อหา',
  `create_time` int(11) NOT NULL COMMENT 'วันที่สร้าง',
  `update_time` int(11) NOT NULL COMMENT 'วันที่ปรับปรุง',
  `author_id` int(9) NOT NULL COMMENT 'ผู้ประกาศ',
  PRIMARY KEY (`id`),
  KEY `FK_users` (`author_id`),
  KEY `FK_webboard` (`topicid`),
  CONSTRAINT `board_comment_ibfk_1` FOREIGN KEY (`topicid`) REFERENCES `board` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `board_comment_ibfk_2` FOREIGN KEY (`author_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='เว็บบอร์ด';

-- ----------------------------
-- Records of board_comment
