-- ----------------------------
-- Table structure for learning
-- ----------------------------
DROP TABLE IF EXISTS `learning`;
CREATE TABLE `learning` (
  `id` int(9) NOT NULL AUTO_INCREMENT COMMENT 'ไอดีข่าว',
  `code` varchar(10) NOT NULL COMMENT 'รหัสกลุ่ม',
  `title` varchar(255) NOT NULL COMMENT 'หัวข้อ',
  `content` text NOT NULL COMMENT 'เนื้อหา',
  `create_time` varchar(11) NOT NULL COMMENT 'วันที่สร้าง',
  `update_time` varchar(11) NOT NULL COMMENT 'วันที่ปรับปรุง',
  `icon` varchar(20) NOT NULL COMMENT 'ไอคอน',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COMMENT='กลุ่มสาระการเรียนรู้';

-- ----------------------------
-- Records of learning
-- ----------------------------

-- ----------------------------
-- Table structure for lesson
-- ----------------------------
DROP TABLE IF EXISTS `lesson`;
CREATE TABLE `lesson` (
  `id` int(9) NOT NULL AUTO_INCREMENT COMMENT 'ไอดีบทเรียน',
  `mid` int(9) NOT NULL COMMENT 'รหัสวิชา',
  `chapter` tinyint(2) NOT NULL COMMENT 'บทที่',
  `title` varchar(255) NOT NULL COMMENT 'บทเรียน',
  `content` text NOT NULL COMMENT 'เนื้อหา',
  `create_time` varchar(11) NOT NULL COMMENT 'วันที่สร้าง',
  `update_time` varchar(11) NOT NULL COMMENT 'วันที่ปรับปรุง',
  PRIMARY KEY (`id`),
  KEY `FK_major` (`mid`),
  CONSTRAINT `lesson_ibfk_3` FOREIGN KEY (`mid`) REFERENCES `majors` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8 COMMENT='บทเรียน';

-- ----------------------------
-- Records of lesson
-- ----------------------------

-- ----------------------------
-- Table structure for logging
-- ----------------------------
DROP TABLE IF EXISTS `logging`;
CREATE TABLE `logging` (
  `uid` int(9) NOT NULL DEFAULT '0',
  `atime` varchar(14) DEFAULT NULL,
  `event` enum('LI','LO') DEFAULT NULL,
  `ipaddr` varchar(15) DEFAULT NULL,
  KEY `uid` (`uid`),
  CONSTRAINT `logging_ibfk_1` FOREIGN KEY (`uid`) REFERENCES `users` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='ล็อกอิน';

-- ----------------------------
-- Records of logging
-- ----------------------------

-- ----------------------------
-- Table structure for majors
-- ----------------------------
DROP TABLE IF EXISTS `majors`;
CREATE TABLE `majors` (
  `id` int(9) NOT NULL AUTO_INCREMENT COMMENT 'ไอดีข่าว',
  `lid` int(9) NOT NULL COMMENT 'รหัสกลุ่มสาระการเรียนรู้',
  `code` varchar(15) NOT NULL COMMENT 'รหัสวิชา',
  `name` varchar(255) NOT NULL COMMENT 'ชื่อวิชา',
  `content` text NOT NULL COMMENT 'เนื้อหา',
  `prerequisite` text NOT NULL COMMENT 'วัตถุประสงค์',
  `reference` text NOT NULL COMMENT 'เอกสารอ้างอิง',
  `create_time` varchar(11) NOT NULL COMMENT 'วันที่สร้าง',
  `update_time` varchar(11) NOT NULL COMMENT 'วันที่ปรับปรุง',
  `stdate` date NOT NULL COMMENT 'วันเริ่มต้น',
  `endate` date NOT NULL COMMENT 'วันสิ้นสุด',
  `final` tinyint(2) NOT NULL DEFAULT '2' COMMENT 'ข้อสอบแต่ละบท',
  `status` enum('N','Y') NOT NULL DEFAULT 'N' COMMENT 'สถานะ',
  `author_id` int(9) NOT NULL COMMENT 'ผู้สอน',
  PRIMARY KEY (`id`),
  KEY `FK_users` (`author_id`),
  KEY `FK_learning` (`lid`),
  CONSTRAINT `majors_ibfk_2` FOREIGN KEY (`author_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `majors_ibfk_3` FOREIGN KEY (`lid`) REFERENCES `learning` (`id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COMMENT='วิชา';

-- ----------------------------
-- Records of majors
-- ----------------------------

-- ----------------------------
-- Table structure for majors_room
-- ----------------------------
DROP TABLE IF EXISTS `majors_room`;
CREATE TABLE `majors_room` (
  `id` int(9) NOT NULL COMMENT 'รหัสวิชา',
  `education` enum('ป','ม') NOT NULL COMMENT 'ระดับการศึกษา',
  `class` enum('1','2','3','4','5','6') NOT NULL COMMENT 'ชั้นเรียน',
  `room` tinyint(2) NOT NULL COMMENT 'ห้องเรียน',
  KEY `FK_majors` (`id`),
  CONSTRAINT `majors_room_ibfk_1` FOREIGN KEY (`id`) REFERENCES `majors` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of majors_room
-- ----------------------------

-- ----------------------------
-- Table structure for news
-- ----------------------------
