-- ----------------------------
-- Table structure for test
-- ----------------------------
DROP TABLE IF EXISTS `test`;
CREATE TABLE `test` (
  `id` int(9) NOT NULL AUTO_INCREMENT COMMENT 'รหัสคำถาม',
  `lsid` int(9) NOT NULL COMMENT 'รหัสบทเรียน',
  `title` text NOT NULL COMMENT 'คำถาม',
  `answer` tinyint(1) NOT NULL COMMENT 'คำตอบ',
  `code` enum('A','B') NOT NULL COMMENT 'รหัสแยกหมวดหมู่',
  PRIMARY KEY (`id`),
  KEY `FK_lesson_id` (`lsid`),
  CONSTRAINT `test_ibfk_1` FOREIGN KEY (`lsid`) REFERENCES `lesson` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=108 DEFAULT CHARSET=utf8 COMMENT='ทดสอบหลังเรียน';

-- ----------------------------
-- Records of test
-- ----------------------------

-- ----------------------------
-- Table structure for test_choice
-- ----------------------------
DROP TABLE IF EXISTS `test_choice`;
CREATE TABLE `test_choice` (
  `testid` int(9) NOT NULL COMMENT 'รหัสคำถาม',
  `sort` tinyint(2) NOT NULL COMMENT 'ลำดับ',
  `name` varchar(255) NOT NULL COMMENT 'ตัวเลือก',
  KEY `FK_test_id` (`testid`),
  CONSTRAINT `test_choice_ibfk_1` FOREIGN KEY (`testid`) REFERENCES `test` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='ชุดตัวเลือกหลังเรียน';

