-- Records of test_choice
-- ----------------------------

-- ----------------------------
-- Table structure for users
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `id` int(9) NOT NULL AUTO_INCREMENT COMMENT 'ไอดีผู้ใช้',
  `username` varchar(50) NOT NULL COMMENT 'ชื่อผู้ใช้',
  `password` varchar(40) NOT NULL COMMENT 'รหัสผ่าน',
  `pname` varchar(10) NOT NULL COMMENT 'คำนำหน้าชื่อ',
  `fname` varchar(100) NOT NULL COMMENT 'ชื่อ',
  `lname` varchar(100) NOT NULL COMMENT 'นามสกุล',
  `birthday` date NOT NULL COMMENT 'วันเกิด',
  `sex` enum('ช','ญ') NOT NULL COMMENT 'เพศ',
  `age` tinyint(2) NOT NULL COMMENT 'อายุ',
  `education` enum('ป','ม') NOT NULL COMMENT 'ระดับการศึกษา',
  `class` tinyint(1) NOT NULL COMMENT 'ชั้นเรียน',
  `room` tinyint(1) NOT NULL COMMENT 'ห้องเรียน',
  `email` varchar(40) NOT NULL COMMENT 'อีเมล์',
  `level` enum('A','T','S') NOT NULL COMMENT 'สิทธิ์',
  `status` enum('Y','N') NOT NULL DEFAULT 'Y' COMMENT 'สถานะ',
  `login` enum('F','O') NOT NULL DEFAULT 'F' COMMENT 'สถานะล็อกอิน',
  `icon` varchar(15) NOT NULL DEFAULT 'img (238).gif' COMMENT 'รูปไอคอน',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=83 DEFAULT CHARSET=utf8 COMMENT='ผู้ใช้ระบบ';

-- ----------------------------
-- Records of users
-- ----------------------------

-- ----------------------------
-- Table structure for user_stady
-- ----------------------------
DROP TABLE IF EXISTS `user_stady`;
CREATE TABLE `user_stady` (
  `id` int(9) NOT NULL AUTO_INCREMENT COMMENT 'รหัสที่นักเรียนเรียนวิชา',
  `major_id` int(9) NOT NULL COMMENT 'รหัสวิชา',
  `author_id` int(9) NOT NULL COMMENT 'รหัสนักเรียน',
  `final` varchar(3) NOT NULL COMMENT 'คะแนนสอบ',
  PRIMARY KEY (`id`),
  KEY `FK_majors_id` (`major_id`),
  KEY `FK_user_id` (`author_id`),
  CONSTRAINT `user_stady_ibfk_3` FOREIGN KEY (`major_id`) REFERENCES `majors` (`id`) ON UPDATE CASCADE,
  CONSTRAINT `user_stady_ibfk_4` FOREIGN KEY (`author_id`) REFERENCES `users` (`id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8 COMMENT='ตารางนักเรียนที่เรียนวิชา';

-- ----------------------------
-- Records of user_stady
-- ----------------------------

-- ----------------------------
-- Table structure for user_stady_score
-- ----------------------------
DROP TABLE IF EXISTS `user_stady_score`;
CREATE TABLE `user_stady_score` (
  `id` int(9) NOT NULL COMMENT 'รหัสที่นักเรียนเรียนวิชา',
  `lsid` int(9) NOT NULL COMMENT 'รหัสบทเรียน',
  `aftertest` varchar(3) NOT NULL COMMENT 'คะแนนสอบก่อนเรียน',
  `beforetest` varchar(3) NOT NULL COMMENT 'คะแนนสอบหลังเรียน',
  KEY `id` (`id`),
  KEY `FK_lesson_id` (`lsid`),
  CONSTRAINT `user_stady_score_ibfk_1` FOREIGN KEY (`id`) REFERENCES `user_stady` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `user_stady_score_ibfk_2` FOREIGN KEY (`lsid`) REFERENCES `lesson` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='ตารางเก็บคะแนนสอบ';

