INSERT INTO `learning` VALUES ('9', 'ฟ.', 'กลุ่มสาระการเรียนรู้วิชาฟิสิกส์', '&lt;p 1=&quot;&quot; 2=&quot;&quot; 4=&quot;&quot; a=&quot;&quot; alt=&quot;enlightened&quot; color:=&quot;&quot; font-family:=&quot;&quot; height=&quot;20&quot; href=&quot;\\&quot; img=&quot;&quot; ms=&quot;&quot; p=&quot;&quot; sans=&quot;&quot; span=&quot;&quot; src=&quot;http://202.29.52.213/s49/49042380148/ckeditor/plugins/smiley/images/lightbulb.gif&quot; style=&quot;\\&quot; target=&quot;\\&quot; text-align:=&quot;&quot; text-decoration:=&quot;&quot; title=&quot;enlightened&quot; width=&quot;20&quot;&gt;\r\n	&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;span style=&quot;font-family: arial, helvetica, sans-serif; &quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&amp;lt;a data-cke-saved-href=&amp;quot;\\&amp;quot; href=&amp;quot;\\&amp;amp;quot;http://www.obeclms.com/lesson/20_ElectricDevice/index.html\\&amp;amp;quot;&amp;quot; style=&amp;quot;\\&amp;amp;quot;font-family:&amp;quot; tahoma,=&amp;quot;&amp;quot; geneva,=&amp;quot;&amp;quot; sans-serif;=&amp;quot;&amp;quot; font-size:=&amp;quot;&amp;quot; 16px;=&amp;quot;&amp;quot; color:=&amp;quot;&amp;quot; black;=&amp;quot;&amp;quot; text-decoration:=&amp;quot;&amp;quot; none;=&amp;quot;&amp;quot; \\&amp;quot;=&amp;quot;&amp;quot; target=&amp;quot;\\&amp;amp;quot;_blank\\&amp;amp;quot;&amp;quot;&amp;gt;', '1339516710', '1339758280', '');
INSERT INTO `lesson` VALUES ('8', '10', '1', 'การเคลื่อนที่ 1 มิติ และ 2 มิติ', '&lt;p style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong&gt;การเคลื่อนที่ในหนึ่งและสองมิติ&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp; ในชีวิตประจำวัน เราจะเห็นการเคลื่อนที่ของสิ่งต่าง ๆ มากมาย หลากหลายรูปแบบ เช่นการเคลื่อนที่ของอวัยวะต่าง ๆ ของคนเดิน นกบิน รถยนต์แล่น และใบไม้ร่วง เป็นต้น เมื่อ พิจารณาการเคลื่อนที่เหล่านั้นแล้ว จะพบว่า ส่วนใหญ่มีลักษณะซับซ้อน เช่น มีเส้นทางการ เคลื่อนที่เป็นแนวเส้นโค้งวกวน เพื่อให้ง่ายต่อการเข้าใจการเคลื่อนที่เราจะเริ่มศึกษาจากการ เคลื่อนที่เฉพาะในแนวเส้นตรงก่อน แล้วจึงจะขยายความเข้าใจ กับการเคลื่อนที่ในสองและสามมิติ หรือในลักษณะที่ซับซ้อนในลำดับต่อไป&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong&gt;ตำแหน่งและการกระจัด&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;ตำแหน่ง (position) คือการแสดงออก หรือการบอกให้ทราบว่า วัตถุหรือสิ่งของ ที่เราพิจารณา อยู่ที่ใด เราจะคิดถึงวัตถุที่มีขนาดเล็กก่อน ซึ่งจะสามารถบอกได้ชัดเจนว่ามี ตำแหน่งอยู่ที่ใด โดยเฉพาะ บนเส้นตรงเส้นหนึ่งเมื่อเทียบกับจุดอ้างอิง จุดอ้างอิงเป็นปัจจัย จำเป็นเพื่อความชัดเจน อาจจะเป็นจุด ศูนย์ของโคออร์ดิเนตในพิกัด xy เนื่องจากเราจะ พิจารณากรณีหนึ่งมิติก่อน เราจะใช้เฉพาะแกน x และอาจบอกว่าวัตถุของเราอยู่ที่ตำแหน่ง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002.gif&quot; width=&quot;53&quot; /&gt;ที่เวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004.gif&quot; width=&quot;15&quot; /&gt;&amp;nbsp;อันหมายถึงวัตถุอยู่ที่ระยะทาง &amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;จาก&lt;br /&gt;\r\n	จุด O (จุดอ้างอิง) ที่เวลาดังกล่าว ถ้าวัตถุเลื่อนไปอยู่ที่&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008.gif&quot; width=&quot;18&quot; /&gt;ที่เวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010.gif&quot; width=&quot;16&quot; /&gt;&amp;nbsp;&amp;nbsp;แสดงว่าวัตถุได้มีการเคลื่อนที่ไประหว่างเวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0000.gif&quot; width=&quot;15&quot; /&gt;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010_0000.gif&quot; width=&quot;16&quot; /&gt;&amp;nbsp;ตำแหน่งทั้งสองของวัตถุอาจแสดงดังรูปที่ 1&lt;/span&gt;&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;86&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image014.gif&quot; width=&quot;521&quot; /&gt;&amp;nbsp;&lt;br /&gt;\r\n	&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;รูปที่&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;1&lt;/strong&gt;&amp;nbsp;การแสดงตำแหน่งและการกระจัดของวัตถุบนแกน&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image016.gif&quot; width=&quot;8&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;การเปลี่ยนตำแหน่งของวัตถุจาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0000.gif&quot; width=&quot;53&quot; /&gt;&amp;nbsp;ไปเป็น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image018.gif&quot; width=&quot;53&quot; /&gt;&amp;nbsp;หรือ&amp;nbsp;&lt;img align=&quot;absbottom&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image020.gif&quot; width=&quot;74&quot; /&gt;เรียกว่าการกระจัด (displacement) การกระจัดมีทิศในทีนี้มีทิศจาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006_0000.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0000.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;ดังรูป โดยทั่วไป การกระจัด หมายถึงการเปลี่ยนแปลงของวัตถุไปจากตำแหน่งปกติ&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;ความเร็วเฉลี่ยและอัตราเร็วเฉลี่ย&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;โดยนิยามหรือข้อกำหนด ความเร็ว (Velocity) คือ การเปลี่ยนตำแหน่งต่อเวลา สำหรับ&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;ช่วงเวลาที่ยาว ความเร็วที่คิดจากการเปลี่ยนตำแหน่งในช่วงเวลานั้นหารด้วยช่วงเวลาถือว่า&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;เป็นความเร็วเฉลี่ย (Average Velocity)ความเร็วเฉลี่ยเป็นเสมือนความเร็วที่การเปลี่ยนแปลง &amp;nbsp;&amp;nbsp;ในช่วงเวลาที่วัดมีค่าเดียวที่สม่ำเสมอ ความ เร็วเฉลี่ยที่มีทิศทางจาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0001.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0001.gif&quot; width=&quot;18&quot; /&gt;คือ&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;49&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006_0001.gif&quot; width=&quot;167&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;สำหรับคำว่าอัตราเร็ว (Speed) มีความหมายที่แตกต่างไปจากความเร็วคือ &amp;nbsp;&amp;nbsp;อัตราเร็ว &amp;nbsp;&amp;nbsp;จะคิดจาก ระยะทาง ของการเคลื่อนที่ทั้งหมดโดยไม่คำนึงถึงทิศทางนั่นคือใช้อัตราเร็วใน &amp;nbsp;&amp;nbsp;ลักษณะที่เป็นสเกลาร์ (Scalar) ในขณะที่ ความเร็วเป็นปริมาณเวกเตอร์ (Vector) อัตราเร็ว&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;เฉลี่ย (Average Speed) สำหรับการเคลื่อนที่จาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0002.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0002.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;ดังรูป 2 จะมีขนาดเท่ากับ &amp;nbsp;&amp;nbsp;ระยะทางจาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0003.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0003.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;หารด้วยเวลา ซึ่งจะให้ผลเท่ากับขนาดของความเร็วเฉลี่ยแต่อัตรา &amp;nbsp;&amp;nbsp;เร็วเฉลี่ยอาจ จะแตกต่างไปจากขนาดของความเร็วเฉลี่ยได้โดยเฉพาะกรณีที่วัตถุเคลื่อน&lt;br /&gt;\r\n	&amp;nbsp; ที่เลย&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0004.gif&quot; width=&quot;18&quot; /&gt;ไปถึง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0001.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;&amp;nbsp;แล้วย้อนกลับมา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0005.gif&quot; width=&quot;18&quot; /&gt;ดังรูป 2 โดยใช้เวลาทั้งหมด&amp;nbsp;&lt;img align=&quot;absbottom&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010_0001.gif&quot; width=&quot;53&quot; /&gt;เท่าเดิม &amp;nbsp;&amp;nbsp;ถ้าเป็นเช่นนั้น&amp;nbsp; &amp;nbsp;&amp;nbsp;อัตราเร็วเฉลี่ย &amp;nbsp;&amp;nbsp;ซึ่งเป็นระยะทางทั้งหมดที่เคลื่อนที่ (จาก&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0004.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0002.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;แล้วจาก &amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0003.gif&quot; width=&quot;18&quot; /&gt;กลับมา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0006.gif&quot; width=&quot;18&quot; /&gt;) &amp;nbsp;&amp;nbsp;หารด้วยเวลาจะมีค่า &amp;nbsp;เพิ่มขึ้นมากและต่างจากขนาดของความเร็วอย่างเห็น &amp;nbsp;&amp;nbsp;ได้ชัด&lt;/span&gt;&lt;/p&gt;\r\n&lt;center style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&lt;img align=&quot;absbottom&quot; alt=&quot;&quot; height=&quot;89&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image014_0000.gif&quot; width=&quot;535&quot; /&gt;&lt;/center&gt;\r\n&lt;br style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; /&gt;\r\n&lt;strong style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;รูปที่ 2&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;แสดงการเคลื่อนที่ของวัตถุจาก&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0005.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;18&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;ไป&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0004.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;18&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;&amp;nbsp;และจาก&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0005.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;18&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;กลับมา&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0007.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;18&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; /&gt;\r\n&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;&amp;nbsp;&amp;nbsp;นั่นคือ&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 18px; &quot;&gt;&lt;strong style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;ความเร็วเฉลี่ยและอัตราเร็วเฉลี่ย&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;p style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;โดยนิยามหรือข้อกำหนด ความเร็ว (Velocity) คือ การเปลี่ยนตำแหน่งต่อเวลา สำหรับ&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;ช่วงเวลาที่ยาว ความเร็วที่คิดจากการเปลี่ยนตำแหน่งในช่วงเวลานั้นหารด้วยช่วงเวลาถือว่า&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;เป็นความเร็วเฉลี่ย (Average Velocity)ความเร็วเฉลี่ยเป็นเสมือนความเร็วที่การเปลี่ยนแปลง &amp;nbsp;&amp;nbsp;ในช่วงเวลาที่วัดมีค่าเดียวที่สม่ำเสมอ ความ เร็วเฉลี่ยที่มีทิศทางจาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0001.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0001.gif&quot; width=&quot;18&quot; /&gt;คือ&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;49&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006_0001.gif&quot; width=&quot;167&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;สำหรับคำว่าอัตราเร็ว (Speed) มีความหมายที่แตกต่างไปจากความเร็วคือ &amp;nbsp;&amp;nbsp;อัตราเร็ว &amp;nbsp;&amp;nbsp;จะคิดจาก ระยะทาง ของการเคลื่อนที่ทั้งหมดโดยไม่คำนึงถึงทิศทางนั่นคือใช้อัตราเร็วใน &amp;nbsp;&amp;nbsp;ลักษณะที่เป็นสเกลาร์ (Scalar) ในขณะที่ ความเร็วเป็นปริมาณเวกเตอร์ (Vector) อัตราเร็ว&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;เฉลี่ย (Average Speed) สำหรับการเคลื่อนที่จาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0002.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0002.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;ดังรูป 2 จะมีขนาดเท่ากับ &amp;nbsp;&amp;nbsp;ระยะทางจาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0003.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0003.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;หารด้วยเวลา ซึ่งจะให้ผลเท่ากับขนาดของความเร็วเฉลี่ยแต่อัตรา &amp;nbsp;&amp;nbsp;เร็วเฉลี่ยอาจ จะแตกต่างไปจากขนาดของความเร็วเฉลี่ยได้โดยเฉพาะกรณีที่วัตถุเคลื่อน&lt;br /&gt;\r\n	&amp;nbsp; ที่เลย&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0004.gif&quot; width=&quot;18&quot; /&gt;ไปถึง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0001.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;&amp;nbsp;แล้วย้อนกลับมา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0005.gif&quot; width=&quot;18&quot; /&gt;ดังรูป 2 โดยใช้เวลาทั้งหมด&amp;nbsp;&lt;img align=&quot;absbottom&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010_0001.gif&quot; width=&quot;53&quot; /&gt;เท่าเดิม &amp;nbsp;&amp;nbsp;ถ้าเป็นเช่นนั้น&amp;nbsp; &amp;nbsp;&amp;nbsp;อัตราเร็วเฉลี่ย &amp;nbsp;&amp;nbsp;ซึ่งเป็นระยะทางทั้งหมดที่เคลื่อนที่ (จาก&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0004.gif&quot; width=&quot;18&quot; /&gt;ไป&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0002.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;แล้วจาก &amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0003.gif&quot; width=&quot;18&quot; /&gt;กลับมา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0006.gif&quot; width=&quot;18&quot; /&gt;) &amp;nbsp;&amp;nbsp;หารด้วยเวลาจะมีค่า &amp;nbsp;เพิ่มขึ้นมากและต่างจากขนาดของความเร็วอย่างเห็น &amp;nbsp;&amp;nbsp;ได้ชัด&lt;/span&gt;&lt;/p&gt;\r\n&lt;center style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img align=&quot;absbottom&quot; alt=&quot;&quot; height=&quot;89&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image014_0000.gif&quot; width=&quot;535&quot; /&gt;&lt;/span&gt;&lt;/center&gt;\r\n&lt;br style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; /&gt;\r\n&lt;strong style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;รูปที่ 2&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;แสดงการเคลื่อนที่ของวัตถุจาก&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0005.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;18&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;ไป&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0004.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;18&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;&amp;nbsp;และจาก&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0005.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;18&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;กลับมา&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0007.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;18&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; /&gt;\r\n&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;&amp;nbsp;&amp;nbsp;นั่นคือ&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;br style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; /&gt;\r\n&lt;a id=&quot;OLE_LINK4&quot; name=&quot;OLE_LINK4&quot; style=&quot;font-family: Tahoma; font-size: 14px; color: rgb(102, 102, 102); background-color: rgb(255, 255, 255); &quot;&gt;&lt;/a&gt;&lt;a id=&quot;OLE_LINK3&quot; name=&quot;OLE_LINK3&quot; style=&quot;font-family: Tahoma; font-size: 14px; color: rgb(102, 102, 102); background-color: rgb(255, 255, 255); &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;46&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image016_0000.gif&quot; width=&quot;297&quot; /&gt;&lt;/a&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;ในทีนี้ d คือ&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image018_0000.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;276&quot; /&gt;&lt;/span&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/div&gt;\r\n&lt;p style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;โดยเครื่องหมายขีดสองข้างของปริมาณจะหมายถึง ขนาดของปริมาณนั้นและมีค่าเป็น&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;บวกเท่านั้นสมมติว่า โยนลูกบอลขึ้นในแนวดิ่ง ลูกบอลลอยขึ้นไปได้สูง 5 เมตร แล้วตกกลับ&lt;br /&gt;\r\n	&amp;nbsp; มายังมือในเวลา 2 วินาที จากข้อความนี้แสดงว่า อัตราเร็วเฉลี่ยในช่วง 2 &amp;nbsp;&amp;nbsp;วินาทีเท่ากับระยะ&lt;br /&gt;\r\n	&amp;nbsp; ทาง 5 เมตร + 5 เมตร หารด้วยเวลา 2 วินาที เท่ากับ 5 เมตรต่อวินาทีแต่ความเร็วเฉลี่ย &amp;nbsp;&amp;nbsp;ในช่วงเดียวกันจะเป็นศูนย์เพราะ &amp;nbsp;&amp;nbsp;ลูกบอลได้กลับมาที่เดิมคือ มีการกระจัดทั้งหมดเป็นศูนย์&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;&amp;nbsp; ในทีนี้ d คือ&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image018_0000.gif&quot; style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot; width=&quot;276&quot; /&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; &quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;\r\n&lt;p style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(102, 102, 102); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;โดยเครื่องหมายขีดสองข้างของปริมาณจะหมายถึง ขนาดของปริมาณนั้นและมีค่าเป็น&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;บวกเท่านั้นสมมติว่า โยนลูกบอลขึ้นในแนวดิ่ง ลูกบอลลอยขึ้นไปได้สูง 5 เมตร แล้วตกกลับ&lt;br /&gt;\r\n	&amp;nbsp; มายังมือในเวลา 2 วินาที จากข้อความนี้แสดงว่า อัตราเร็วเฉลี่ยในช่วง 2 &amp;nbsp;&amp;nbsp;วินาทีเท่ากับระยะ&lt;br /&gt;\r\n	&amp;nbsp; ทาง 5 เมตร + 5 เมตร หารด้วยเวลา 2 วินาที เท่ากับ 5 เมตรต่อวินาทีแต่ความเร็วเฉลี่ย &amp;nbsp;&amp;nbsp;ในช่วงเดียวกันจะเป็นศูนย์เพราะ &amp;nbsp;&amp;nbsp;ลูกบอลได้กลับมาที่เดิมคือ มีการกระจัดทั้งหมดเป็นศูนย์&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong&gt;ความเร็วและอัตราเร็วขณะใดขณะหนึ่ง&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;ความเร็วขณะใดขณะหนึ่ง (Instantaneous velocity) ก็คือความเร็วของวัตถุในช่วงเวลาที่ สั้นมากขณะผ่านจุดจุดหนึ่งหรือที่เวลาใดเวลาหนึ่ง หรือพูดสั้น ๆ เป็น&lt;br /&gt;\r\n	ความเร็วในช่วงเวลาที่สั้นมาก นั้นคือ&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;46&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0006.gif&quot; width=&quot;174&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;สัญลักษณ์ในสมการ เป็นสัญลักษณ์ที่ใช้ในวิชาคณิตศาสตร์แสดงกระบวน การทางคณิตศาสตร์ที่ใช้หา ค่า lim เมื่อทราบฟังก์ชันชัดเจนและค่า&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0008.gif&quot; width=&quot;17&quot; /&gt;&amp;nbsp;หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006_0002.gif&quot; width=&quot;53&quot; /&gt;&amp;nbsp;เป็นทำอนุพันธ์ของ X เทียบกับเวลา ซึ่งเป็นสัญลักษณ์แทน&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;37&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0006.gif&quot; width=&quot;97&quot; /&gt;&amp;nbsp;&amp;nbsp;นั่นเอง&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010_0002.gif&quot; width=&quot;15&quot; /&gt;&amp;nbsp;ถือเป็นตัวดำเนิน การตัวหนึ่ง ซึ่งจะกระทำบางอย่างกับสิ่งที่เขียนตามมา กราฟของ ตำแหน่งของวัตถุกับ เวลาอาจเป็นดังรูป&lt;em&gt;&amp;nbsp;&lt;/em&gt;ระหว่าง จุด&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image012.gif&quot; width=&quot;18&quot; /&gt;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image014_0001.gif&quot; width=&quot;22&quot; /&gt;ที่เวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image016_0001.gif&quot; width=&quot;15&quot; /&gt;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image018_0001.gif&quot; width=&quot;16&quot; /&gt;ซึ่งจะเรียกว่าจุด&amp;nbsp;&lt;em&gt;P&amp;nbsp;&lt;/em&gt;และ จุด&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;การเคลื่อนที่อาจ เป็นไปตามเส้นโค้ง&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;em&gt;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;228&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image024.gif&quot; width=&quot;515&quot; /&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;รูป&amp;nbsp;&lt;/strong&gt;ความเร็วเฉลี่ยและความเร็วขณะใดขณะหนึ่ง&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ถ้าตำแหน่งของวัตถุที่ผ่าน&amp;nbsp;&lt;em&gt;P&amp;nbsp;&lt;/em&gt;และ&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;เป็นเส้นโค้งดังรูป&amp;nbsp;ความชันของเส้น&lt;br /&gt;\r\n	ตรงที่ลากผ่าน&amp;nbsp;&lt;em&gt;PQ&amp;nbsp;&lt;/em&gt;คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;36&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image026.gif&quot; width=&quot;39&quot; /&gt;&amp;nbsp;ก็คือ ความเร็วเฉลี่ยระหว่าง&amp;nbsp;&lt;em&gt;P&amp;nbsp;&lt;/em&gt;และ&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;ถ้าจุด&amp;nbsp;&lt;em&gt;P&amp;nbsp;&lt;/em&gt;&lt;br /&gt;\r\n	เลื่อนไปใกล้จุด&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;มากขึ้น ๆ นั้นคือช่วง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image028.gif&quot; width=&quot;53&quot; /&gt;&amp;nbsp;จะสั้นลงจน&amp;nbsp;&lt;em&gt;P&amp;nbsp;&lt;/em&gt;อยู่ใกล้&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;มาก&lt;br /&gt;\r\n	เส้นตรง&amp;nbsp;&lt;em&gt;PQ&amp;nbsp;&lt;/em&gt;ก็จะกลายเป็นเส้นสัมผัสเส้น โค้งที่จุด&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;นั่นเอง และ จะมีระยะหนึ่ง&lt;br /&gt;\r\n	ที่เมื่อ&amp;nbsp;&lt;em&gt;P&amp;nbsp;&lt;/em&gt;เข้าใกล้&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;มากขึ้น ความชันก็ไม่ เปลี่ยนแปลง ความชันนี้คือ ลิมิต (&lt;em&gt;Limit&lt;/em&gt;) หรือขีดจำกัด ของความชัน และถือว่า ค่าความเร็วของวัตถุที่&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;ซึ่งเป็นความเร็ว ขณะใดขณะหนึ่งที่เวลา&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image018_0002.gif&quot; width=&quot;16&quot; /&gt;&amp;nbsp;หรือที่จุด&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;เพื่อให้เข้าใจความหมายดังกล่าวของค่าลิมิต เราสามารถสังเกตได้จากตัวอย่าง ต่อไปนี้ เช่น สมมติว่า เราทราบว่ากราฟตำแหน่งกับ เวลาเป็นไปตามสมการ&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image030.gif&quot; width=&quot;62&quot; /&gt;ในหน่วยเมตรเมื่อ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image032.gif&quot; width=&quot;8&quot; /&gt;&amp;nbsp;เป็นวินาที เราสามารถรู้ค่า&lt;br /&gt;\r\n	ตำแหน่ง&amp;nbsp;&lt;em&gt;X&amp;nbsp;&lt;/em&gt;ที่เวลาต่าง ๆ ได้จากการคำนวณจากสมการ ให้&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;เป็นตำแหน่งที่&amp;nbsp;&lt;em&gt;2.00&amp;nbsp;&lt;/em&gt;&lt;br /&gt;\r\n	วินาที ค่าของตำแหน่ง&amp;nbsp;&lt;em&gt;P&amp;nbsp;&lt;/em&gt;ที่เวลาต่าง ๆ ใกล้&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;และค่าของความชันของเส้น&amp;nbsp;&lt;em&gt;PQ&amp;nbsp;&lt;/em&gt;ดังตาราง ซึ่งจะเห็นลิมิต ของความชันที่&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;เป็น&amp;nbsp;&lt;em&gt;20.00&amp;nbsp;&lt;/em&gt;เมตรต่อวินาที&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;ตาราง&lt;/strong&gt;&amp;nbsp;แสดงค่า&amp;nbsp;&lt;em&gt;X&amp;nbsp;&lt;/em&gt;ของ&amp;nbsp;&lt;em&gt;P&amp;nbsp;&lt;/em&gt;และ&amp;nbsp;&lt;em&gt;Q&amp;nbsp;&lt;/em&gt;ที่เวลาต่าง ๆ และค่าความชันของเส้น&amp;nbsp;&lt;em&gt;PQ&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;table border=&quot;1&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;br /&gt;\r\n				&lt;em&gt;t (s)&lt;/em&gt;&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					&lt;em&gt;P (m)&lt;/em&gt;&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					&lt;em&gt;Q (m)&lt;/em&gt;&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					ความชันของ&amp;nbsp;&lt;em&gt;PQ (m/s)&lt;/em&gt;&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					1.50&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					11.25&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					17.50&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					1.80&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					16.20&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					1.90&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					18.05&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.50&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					1.98&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.602&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.90&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					1.99&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.8005&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.95&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					1.995&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.9001&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.98&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					1.999&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.9800&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					1.9999&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					19.9980&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;154&quot;&gt;\r\n				&lt;p&gt;\r\n					20.00&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ตารางแสดงว่าความชันไม่เปลี่ยนเมื่อ&amp;nbsp;&lt;em&gt;t&amp;nbsp;&lt;/em&gt;เข้าใกล้&amp;nbsp;&lt;em&gt;2.00&amp;nbsp;&lt;/em&gt;วินาทีมาก&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	เมื่อช่วงเวลาสั้นมากวัตถุไม่มีเวลาที่จะกลับไปมา&amp;nbsp;อัตราเร็วที่ขณะใดขณะหนึ่งก็คือขนาด&lt;br /&gt;\r\n	ของความเร็วที่ไม่ต้องคำนึงถึงทิศทางนั่นเอง&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;ความเร่ง&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ความเร่ง (&lt;em&gt;Acceleration&lt;/em&gt;) หมายถึง การเปลี่ยนแปลงความเร็วต่อเวลา นั่นคือถ้าที่เวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0007.gif&quot; width=&quot;16&quot; /&gt;วัตถุมีความเร็ว&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0009.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;&amp;nbsp;และที่เวลาก่อนนั้นคือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006_0003.gif&quot; width=&quot;15&quot; /&gt;&amp;nbsp;วัตถุมีความเร็ว&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0007.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;ถือว่าความเร่งเฉลี่ยใน ช่วงเวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006_0004.gif&quot; width=&quot;15&quot; /&gt;&amp;nbsp;ถึงเวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0008.gif&quot; width=&quot;16&quot; /&gt;คือ&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;49&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010_0003.gif&quot; width=&quot;157&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ความเร่งขณะใดขณะหนึ่ง (&lt;em&gt;Instantaneous acceleration&lt;/em&gt;) โดยใช้สัญลักษณ์ คือ&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;46&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image012_0000.gif&quot; width=&quot;212&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;หากเขียนกราฟของความเร็วกับเวลา&amp;nbsp; ความชันของเส้นสัมผัสที่จุดต่าง ๆ ก็คือความเร่งของ วัตถุที่จุดนั้นๆ ในทำนองเดียวกันกับที่ความเร็วเป็นความชันของ กราฟระหว่างตำแหน่งกับเวลา&amp;nbsp; สำหรับคำว่า&amp;nbsp; อัตราเร่ง&amp;nbsp;ก็จะไม่คิดทิศทางในลักษณะ เดียวกับอัตราเร็ว&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;การเคลื่อนที่ในกรณีความเร่งเป็นค่าคงตัว&lt;/strong&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;กรณีที่ความเร่งมีค่าคงตัว (Constant acceleration) นั่นคือ ความเร็วมีการเปลี่ยนแปลง&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;แบบสม่ำเสมอ กราฟของความเร็วที่เพิ่มขึ้นอย่างสม่ำเสมออาจเป็นดังรูป ซึ่งกราฟความเร็วกับ เวลาเป็นกราฟเส้นตรง&amp;nbsp; ความชันที่ทุกจุดบนเส้นตรงคือ&amp;nbsp; ความชันของเส้นตรงนั่นเอง&lt;/span&gt;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;em&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img alt=&quot;&quot; height=&quot;226&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image007.gif&quot; width=&quot;427&quot; /&gt;&lt;/em&gt;&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;รูป&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;กราฟความเร็วกับเวลา&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\nความชันของเส้นตรงคือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;33&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image009.gif&quot; width=&quot;31&quot; /&gt;&amp;nbsp;ให้เท่ากับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image011.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;ซึ่งเป็นค่าคงตัว&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ดังนั้น&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image013.gif&quot; width=&quot;102&quot; /&gt;&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;หรือ&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image015.gif&quot; width=&quot;102&quot; /&gt;&lt;/span&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;em&gt;&amp;nbsp;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ความหมายในสมการนี้คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0010.gif&quot; width=&quot;11&quot; /&gt;เป็นความเร็วที่เวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0010.gif&quot; width=&quot;33&quot; /&gt;&amp;nbsp;เป็นความเร็วที่เวลา&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006_0005.gif&quot; width=&quot;42&quot; /&gt;&amp;nbsp;หรือความเร็วต้น ,&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0008.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;เป็นความเร่งค่าหนึ่ง และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010_0004.gif&quot; width=&quot;8&quot; /&gt;&amp;nbsp;เป็นเวลา&amp;nbsp;&amp;nbsp;&amp;nbsp; ซึ่งจากสมการ&lt;br /&gt;\r\n	ข้างต้นก็จะเห็นได้ว่า&amp;nbsp; ความเร็วมีค่าเพิ่มขึ้นอย่างสม่ำเสมอ&amp;nbsp; สมการนี้จะใช้ได้ทั้งค่า&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010_0005.gif&quot; width=&quot;8&quot; /&gt;&amp;nbsp;ที่เป็น + และ &amp;ndash; (ค่า &amp;ndash;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image012_0001.gif&quot; width=&quot;12&quot; /&gt;หมายถึงเวลาก่อนที่จะเริ่มนับว่าศูนย์)&amp;nbsp; และยังใช้ได้ทั้ง&lt;br /&gt;\r\n	กรณีที่&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0009.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;&amp;nbsp;เป็นบวกและลบ (ถ้า&amp;nbsp;&lt;img alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0010.gif&quot; width=&quot;19&quot; /&gt;มีค่าเป็นลบ หมายถึงความเร่งไปทางทิศ &amp;ndash;X และ มีผลให้ความเร็ว ลดลงอย่างสม่ำเสมอ โดยที่&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image002_0011.gif&quot; width=&quot;11&quot; /&gt;&amp;nbsp;อาจจะยังมีค่าเป็นบวกอยู่)&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&lt;/span&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;center&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;em&gt;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;226&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image022.gif&quot; width=&quot;412&quot; /&gt;&lt;/em&gt;&lt;/span&gt;&lt;/center&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;รูป&amp;nbsp;&lt;/strong&gt;กราฟระหว่างความเร็วและเวลา สำหรับความเร่งคงตัว&lt;/span&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;การหาระยะทางที่วัตถุเคลื่อนที่ได้&amp;nbsp; อาจจะหาได้โดยใช้กราฟดังรูป 2.5 ถ้าใช้ช่วง&lt;br /&gt;\r\nเวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image024_0000.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;เป็นช่วงเวลาสั้น ๆ ขณะที่วัตถุมีความเร็ว&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image004_0011.gif&quot; width=&quot;11&quot; /&gt;&amp;nbsp;ระยะทางที่วัตถุเคลื่อนที่ได้ในช่วง&lt;br /&gt;\r\nเวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image006_0006.gif&quot; width=&quot;27&quot; /&gt;ก็คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0011.gif&quot; width=&quot;30&quot; /&gt;ซึ่งเป็นช่วงเวลาสั้น ๆ เปลี่ยนแปลงไม่มาก&amp;nbsp; ดังนั้นการรวม&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image008_0012.gif&quot; width=&quot;30&quot; /&gt;&amp;nbsp;&lt;br /&gt;\r\nทั้งหมดโดยเริ่มจาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image010_0006.gif&quot; width=&quot;42&quot; /&gt;&amp;nbsp;จนถึง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/01_Motion/images/clip_image012_0002.gif&quot; width=&quot;47&quot; /&gt;จะได้ระยะทางของการเคลื่อนที่ทั้งหมด&amp;nbsp; และเป็นระยะ&amp', '1342539698', '1342539698');
INSERT INTO `lesson` VALUES ('9', '10', '2', 'แรงมวลและกฏการเคลื่อนที่', '&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span class=&quot;style11&quot; style=&quot;color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 16px; &quot;&gt;&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-weight: bold; font-size: 18px; &quot;&gt;แรง&lt;/span&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;style4&quot;&gt;คือสิ่งที่กระทำต่อวัตถุในรูปของการพยายามดึงหรือดันให้วัตถุนั้นเคลื่อนที่โดยที่วัตถุอาจเคลื่อนที่หรือไม่ก็ได้&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	เพราะมีแรงอื่นกระทำต่อวัตถุอยู่ด้วย แรงถือเป็นปริมาณเวคเตอร์ที่ต้องกำหนดด้วยขนาดและทิศทาง&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-size: 18px; &quot;&gt;กฏการเคลื่อนที่ของนิวตัน&lt;/span&gt;&lt;/strong&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style2&quot; style=&quot;color: rgb(255, 51, 0); font-weight: bold; &quot;&gt;กฏข้อที่ 1&lt;/span&gt;&amp;nbsp;&lt;span class=&quot;style4&quot;&gt;ถ้าแรงลัพธ์ที่กระทำกับวัตถุเป็นศูนย์วัตถุจะรักษาสภาพการเคลื่อนที่เดิมถ้าเดิมวัตถุหยุดนิ่งก็จะ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span class=&quot;style4&quot;&gt;หยุดนิ่งอยู่อย่างนั้น หรือถ้าเดิมเคลื่อนที่ก็จะเคลื่อนที่ต่อไปด้วยความเร็วคงที่ในแนวเส้นตรง จะได้สมการ&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math1.png&quot; width=&quot;117&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span class=&quot;style4&quot;&gt;ซึ่งใช้กับการคำนวณสมดุลกล&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style17&quot;&gt;&lt;span class=&quot;style2&quot; style=&quot;color: rgb(255, 51, 0); font-weight: bold; &quot;&gt;กฏข้อที่ 2&lt;/span&gt;&lt;/span&gt;&lt;span class=&quot;style11&quot; style=&quot;color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 16px; &quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;style4&quot;&gt;ถ้ามีแรงลัพธ์ที่ไม่เป็นศูนย์มากระทำกับวัตถุ วัตถุจะเคลื่อนที่ด้วยความเร่งในทิศเดียวกับแรงลัพธ์&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	โดยความเร่งจะมีขนาดแปรผันตรงกับขนาดของแรงลัพธ์ และแปรผกผันกับมวลของวัตถุ จะได้สมการ&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style11&quot; style=&quot;color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: medium; &quot;&gt;\r\n	&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math2.png&quot; width=&quot;117&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style2&quot; style=&quot;color: rgb(255, 51, 0); font-weight: bold; &quot;&gt;กฏข้อที่ 3&lt;/span&gt;&amp;nbsp;&lt;span class=&quot;style4&quot;&gt;ทุก ๆ แรงกิริยา (Action Force) จะมีแรงปฏิกิริยา (Reaction Force) ที่มีขนาดเท่ากัน&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	และมีทิศทางตรงกันข้ามกันเสมอจะได้สมการ&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math3.png&quot; width=&quot;152&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style11&quot; style=&quot;color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 16px; &quot;&gt;&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-weight: bold; font-size: 18px; &quot;&gt;น้ำหนัก&lt;/span&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;style4&quot;&gt;คือ แรงดึงดูดของโลกซึ่งกระทำต่อวัตถุอยู่ตลอดเวลา คำนวณได้จาก&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;img height=&quot;53&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math4.png&quot; width=&quot;167&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span class=&quot;style4&quot;&gt;โ&lt;/span&gt;&lt;span class=&quot;style4&quot;&gt;ดยที่&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;W&lt;/strong&gt;&amp;nbsp; = &amp;nbsp;น้ำหนักของวัตถุ มีหน่วยเป็นนิวตัน&amp;nbsp;&lt;strong&gt;(N)&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;m&lt;/strong&gt;&amp;nbsp; = &amp;nbsp;มวลของวัตถุ มีหน่วยเป็นกิโลกรัม&amp;nbsp;&lt;strong&gt;(kg)&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;g&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp; ความเร่งเนื่องจากแรงโน้มถ่วงของโลก มีหน่วยเป็นเมตรต่อวินาที&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/th_ms2.gif&quot; width=&quot;80&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	ค่า g ในบริเวณต่างๆ ทั่วโลกมีค่าไม่เท่ากัน โดยทั่วไปมักให้ใช้ค่า&amp;nbsp; g = 10&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/ms2.gif&quot; width=&quot;49&quot; /&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-weight: bold; font-size: 18px; &quot;&gt;แรงเสียดทาน&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;(frictional force) คือ แรงที่ต้านการเคลื่อนที่ของวัตถุซึ่งเกิดขึ้นระหว่างผิววัตถุกับพื้นที่&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;สัมผัสและมีทิศตรงกันข้ามกับทิศการเคลื่อนที่ของวัตถุเสมอ แรงเสียดทานแบ่งเป็น 2 ชนิด คือ&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	1.&amp;nbsp;แรงเสียดทานสถิตเป็นแรงเสียดทานที่เกิดขึ้นในขณะที่วัตถุอยู่นิ่งจนถึงเริ่มต้นเคลื่อนที่ มีความสัมพันธ์&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp; ตามสมการ&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style4&quot;&gt;\r\n	&lt;img height=&quot;64&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math7.png&quot; width=&quot;186&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/fs.gif&quot; width=&quot;27&quot; /&gt;= แรงเสียดทานสถิต หน่วยเป็นนิวตัน&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/n.gif&quot; width=&quot;24&quot; /&gt;&amp;nbsp;= แรงปฏิกิริยาในแนวตั้งฉากกับผิวสัมผัส หน่วยเป็นนิวตัน&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/us.gif&quot; width=&quot;26&quot; /&gt;&amp;nbsp;เรียกว่า สัมประสิทธิ์ความเสียดทานสถิต ไม่มีหน่วย&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2. แรงเสียดทานจลน์เป็นแรงเสียดทานขณะวัตถุกำลังเคลื่อนที่ด้วยความเร็วคงตัว ซึ่งจะมีค่าน้อยกว่าแรง&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;เสียดทานสถิต มีความสัมพันธ์ตามสมการ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;img height=&quot;64&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math8.png&quot; width=&quot;186&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/fk.gif&quot; width=&quot;23&quot; /&gt;&amp;nbsp;= แรงเสียดทานจลน์ หน่วยเป็นนิวตัน&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/n.gif&quot; width=&quot;24&quot; /&gt;&amp;nbsp;= แรงปฏิกิริยาในแนวตั้งฉากกับผิวสัมผัส หน่วยเป็นนิวตัน&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style26&quot; style=&quot;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/uk.gif&quot; width=&quot;27&quot; /&gt;&amp;nbsp;&lt;/span&gt;เรียกว่า สัมประสิทธิ์ความเสียดทานจลน์ ไม่มีหน่วย&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-weight: bold; font-size: 18px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	กราฟแรงเสียดทาน&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style4&quot;&gt;\r\n	&lt;img height=&quot;249&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/pic_con3.png&quot; width=&quot;540&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;เมื่อออกแรง F กระทำกับวัตถุ วัตถุยังคงอยู่นิ่งไม่ขยับเพราะมีแรงเสียดทานสถิต&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/fs.gif&quot; width=&quot;27&quot; /&gt;ต้านอยู่&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	แต่เมื่อออกแรงมากขึ้นเรื่อยๆจนกระทั่งแรง F มีค่าเท่ากับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/fsmax.gif&quot; width=&quot;41&quot; /&gt;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	วัตถุจะอยู่ในสภาพกำลังจะเคลื่อนที่ซึ่งเป็นจุดที่มีแรงเสียดทานสูงสุด (ที่ตำแหน่งA)&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	และเมื่อเราออกแรงมากขึ้นวัตถุจะเคลื่อนที่โดยมีแรงเสียดจลน์&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/fk.gif&quot; width=&quot;23&quot; /&gt;&amp;nbsp;เกิดขึ้น แต่จะมีค่าน้อยกว่า&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/fsmax.gif&quot; width=&quot;41&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-weight: bold; font-size: 18px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	ลักษณะของแรงเสียดทาน&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;1. ไม่ขึ้นกับจำนวนพื้นที่ผิวสัมผัส&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2. ไม่ขึ้นกับความเร็วที่วัตถุเคลื่อนที่ และมีทิศตรงข้ามการเคลื่อนที่ของวัตถุ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;3. ขึ้นอยู่กับแรงที่วัตถุกดพื้นในแนวตั้งฉาก หรือ แรงปฏิกิริยาของ พื้นในแนวตั้งฉาก&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;4. ขึ้นกับพื้นผิวสัมผัส เช่น ขรุขระ หรือเรียบ&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	5. สำหรับผิวสัมผัสคู่หนึ่ง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/us.gif&quot; width=&quot;26&quot; /&gt;&amp;nbsp;มีค่ามากกว่า&amp;nbsp;&lt;span class=&quot;style26&quot; style=&quot;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/uk.gif&quot; width=&quot;27&quot; /&gt;&lt;/span&gt;&amp;nbsp;เสมอ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-weight: bold; font-size: 18px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	ตัวอย่างประโยชน์ของแรงเสียดทาน&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;1. ทำให้ยานพาหนะเคลื่อนที่ไปในทิศทางที่ต้องการได้ เช่น ในขณะที่รถแล่น ล้อกับถนนต้องมีแรงเสียดทาน &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;ถ้าไม่มีแรงเสียดทานรถจะแล่นไม่ได้ ล้อรถจะหมุนอยู่กับที่&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2. แรงเสียดทานช่วยไม่ให้รถลื่นโดยเฉพาะเวลาฝนตก&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;3. ยานพาหนะแต่ละชนิด ต้องมีทั้งการลดแรงเสียดทานในอุปกรณ์บางส่วนและเพิ่มแรงเสียดทานในบางส่วน&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;เพื่อให้ยานพาหนะมีประสิทธิภาพและปลอดภัย เช่น รถ เรือ หรือเครื่องบิน ต้องให้หัวแหลมเพื่อลด&lt;/span&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;ความเสียดทานของอากาศหรือน้ำ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-weight: bold; font-size: 18px; &quot;&gt;การเคลื่อนที่ของวัตถุบนพื้นเอียงที่มีความเสียดทาน&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;ถ้าวัตถุอยู่บนพื้นเอียงแล้ววัตถุลื่นไถลลงมาตามพื้นเอียงด้วย ความเร็วคงที่&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	มุมที่พื้นระนาบเอียงกระทำกับแนวระดับ คือ มุมของความเสียดทาน&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/o.gif&quot; width=&quot;30&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;751&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot; width=&quot;137&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;โดยแบ่งออกเป็น&lt;/span&gt;&lt;/span&gt;&lt;/td&gt;\r\n			&lt;td width=&quot;614&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;34&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/os.gif&quot; width=&quot;29&quot; /&gt;&amp;nbsp;คือ มุมที่พื้นระนาบเอียงกระทำกับแนวระดับขณะที่วัตถุกำลังจะเริ่มเคลื่อนที่&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;37&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/ok.gif&quot; width=&quot;27&quot; /&gt;&amp;nbsp;คือ มุมที่พื้นระนาบเอียงกระทำกับแนวระดับขณะที่วัตถุเคลื่อนที่ด้วยความเร็วคงตัว&lt;/span&gt;&lt;/span&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;500&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;object classid=&quot;clsid:D27CDB6E-AE6D-11cf-96B8-444553540000&quot; codebase=&quot;http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=7,0,19,0&quot; height=&quot;200&quot; hspace=&quot;150&quot; width=&quot;400&quot;&gt;&lt;param name=&quot;quality&quot; value=&quot;high&quot; /&gt;&lt;param name=&quot;movie&quot; value=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/force.swf&quot; /&gt;&lt;embed height=&quot;200&quot; hspace=&quot;150&quot; pluginspage=&quot;http://www.macromedia.com/go/getflashplayer&quot; quality=&quot;high&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/force.swf&quot; type=&quot;application/x-shockwave-flash&quot; width=&quot;400&quot;&gt;&lt;/embed&gt;&lt;/object&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;เมื่อวัตถุอยู่บนพื้นเอียงจะมีแรงเนื่องจากน้ำหนักของวัตถุ (mg) แรงเสียดทาน(f) และแรงที่พื้นเอียงกระทำกับ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;วัตถุ(N) หากแตกแรง mg แล้ว เราจะหาความสัมพันธ์ระหว่างปริมาณต่างๆในขณะที่วัตถุกำลังจะเริ่มเคลื่อนที่ได้ดังนี้&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;img height=&quot;158&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/pic_con3_2.png&quot; width=&quot;430&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ในลักษณะเดียวกัน ขณะที่วัตถุเคลื่อนที่ด้วยความเร็วคงที่ เราสามารถหาค่าสัมประสิทธิ์ความเสียดทานจลน์ได้จากสมการ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style2&quot; style=&quot;color: rgb(255, 51, 0); font-weight: bold; &quot;&gt;\r\n	&lt;img height=&quot;98&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/pic_con3_3.png&quot; width=&quot;263&quot; /&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-size: 18px; &quot;&gt;กฏแรงดึงดูดระหว่างมวลของนิวตัน&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จากกฏข้อที่3&amp;nbsp;&amp;nbsp;&amp;nbsp;นิวตันได้เสนอกฏแรงดึงดูดระหว่างมวลซึ่งมีใจความว่าวัตถุทั้งหลายในเอกภพจะออกแรง&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	ดึงดูดซึ่งกันและกันโดยขนาดของแรงดึงดูดระหว่างวัตถุคู่หนึ่งๆจะแปรผันตรงกับผลคูณระหว่างมวลวัตถุ&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	ทั้งสองและจะแปรผกผันกับกำลังสองของระยะทางระหว่างวัตถุทั้งสองนั้น&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; style=&quot;color: rgb(0, 0, 0); &quot; width=&quot;300&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#638CCE&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style14&quot;&gt;&lt;object align=&quot;middle&quot; classid=&quot;clsid:D27CDB6E-AE6D-11cf-96B8-444553540000&quot; codebase=&quot;http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,28,0&quot; height=&quot;120&quot; style=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/mass.swf&quot; width=&quot;300&quot;&gt;&lt;param name=&quot;quality&quot; value=&quot;high&quot; /&gt;&lt;param name=&quot;movie&quot; value=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/mass.swf&quot; /&gt;&lt;embed align=&quot;middle&quot; height=&quot;120&quot; pluginspage=&quot;http://www.adobe.com/shockwave/download/download.cgi?P1_Prod_Version=ShockwaveFlash&quot; quality=&quot;high&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/mass.swf&quot; style=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/mass.swf&quot; type=&quot;application/x-shockwave-flash&quot; width=&quot;300&quot;&gt;&lt;/embed&gt;&lt;/object&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style14&quot; style=&quot;&quot;&gt;ถ้า&lt;/span&gt;มีมวล&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/m1.gif&quot; width=&quot;29&quot; /&gt;&amp;nbsp;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;19&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/m2.gif&quot; width=&quot;30&quot; /&gt;&amp;nbsp;วางห่างกันเป็นระยะ&amp;nbsp;&lt;strong&gt;R&lt;/strong&gt;&amp;nbsp;จะมีแรงดึงดูดซึ่งกันและกัน(หน่วยเป็นนิวตัน)&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	แรงนี้จะแปรผันตรงกับผลคูณของ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/m1.gif&quot; width=&quot;29&quot; /&gt;&amp;nbsp;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;19&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/m2.gif&quot; width=&quot;30&quot; /&gt;&amp;nbsp;และแปรผกผันไปกับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/r2.gif&quot; width=&quot;22&quot; /&gt;&amp;nbsp;จะได้สมการ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style14&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style20&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;img height=&quot;80&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math5.png&quot; width=&quot;212&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;โดยที่ค่าคงตัวโน้มถ่วงสากล&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;(universal gravitational constant&lt;/span&gt;&amp;nbsp;) &amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/g.gif&quot; width=&quot;170&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จากหลักการดังกล่าวแรงดึงดูดระหว่างมวลของโลกกับมวลของวัตถุก็คือ น้ำหนักของวัตถุ นั่นเอง&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span class=&quot;style11&quot; style=&quot;color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 16px; &quot;&gt;&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-size: 18px; &quot;&gt;แรง&lt;/span&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;คือสิ่งที่กระทำต่อวัตถุในรูปของการพยายามดึงหรือดันให้วัตถุนั้นเคลื่อนที่โดยที่วัตถุอาจเคลื่อนที่หรือไม่ก็ได้&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	เพราะมีแรงอื่นกระทำต่อวัตถุอยู่ด้วย แรงถือเป็นปริมาณเวคเตอร์ที่ต้องกำหนดด้วยขนาดและทิศทาง&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-size: 18px; &quot;&gt;กฏการเคลื่อนที่ของนิวตัน&lt;/span&gt;&lt;/strong&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style2&quot; style=&quot;color: rgb(255, 51, 0); &quot;&gt;กฏข้อที่ 1&lt;/span&gt;&amp;nbsp;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;ถ้าแรงลัพธ์ที่กระทำกับวัตถุเป็นศูนย์วัตถุจะรักษาสภาพการเคลื่อนที่เดิมถ้าเดิมวัตถุหยุดนิ่งก็จะ&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;หยุดนิ่งอยู่อย่างนั้น หรือถ้าเดิมเคลื่อนที่ก็จะเคลื่อนที่ต่อไปด้วยความเร็วคงที่ในแนวเส้นตรง จะได้สมการ&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style4&quot;&gt;\r\n	&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math1.png&quot; width=&quot;117&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;ซึ่งใช้กับการคำนวณสมดุลกล&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style17&quot; style=&quot;&quot;&gt;&lt;span class=&quot;style2&quot; style=&quot;color: rgb(255, 51, 0); &quot;&gt;กฏข้อที่ 2&lt;/span&gt;&lt;/span&gt;&lt;span class=&quot;style11&quot; style=&quot;color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; &quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;ถ้ามีแรงลัพธ์ที่ไม่เป็นศูนย์มากระทำกับวัตถุ วัตถุจะเคลื่อนที่ด้วยความเร่งในทิศเดียวกับแรงลัพธ์&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;โดยความเร่งจะมีขนาดแปรผันตรงกับขนาดของแรงลัพธ์ และแปรผกผันกับมวลของวัตถุ จะได้สมการ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style11&quot; style=&quot;color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; &quot;&gt;\r\n	&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math2.png&quot; width=&quot;117&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style2&quot; style=&quot;color: rgb(255, 51, 0); &quot;&gt;กฏข้อที่ 3&lt;/span&gt;&amp;nbsp;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;ทุก ๆ แรงกิริยา (Action Force) จะมีแรงปฏิกิริยา (Reaction Force) ที่มีขนาดเท่ากัน&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;และมีทิศทางตรงกันข้ามกันเสมอจะได้สมการ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style4&quot;&gt;\r\n	&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math3.png&quot; width=&quot;152&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style11&quot; style=&quot;color: rgb(255, 255, 255); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 16px; &quot;&gt;&lt;span class=&quot;style6&quot; style=&quot;color: rgb(255, 204, 0); font-size: 18px; &quot;&gt;น้ำหนัก&lt;/span&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;คือ แรงดึงดูดของโลกซึ่งกระทำต่อวัตถุอยู่ตลอดเวลา คำนวณได้จาก&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;img height=&quot;53&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/math4.png&quot; width=&quot;167&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;โ&lt;/span&gt;&lt;span class=&quot;style4&quot; style=&quot;&quot;&gt;ดยที่&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;W&lt;/strong&gt;&amp;nbsp; = &amp;nbsp;น้ำหนักของวัตถุ มีหน่วยเป็นนิวตัน&amp;nbsp;&lt;strong&gt;(N)&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;m&lt;/strong&gt;&amp;nbsp; = &amp;nbsp;มวลของวัตถุ มีหน่วยเป็นกิโลกรัม&amp;nbsp;&lt;strong&gt;(kg)&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;g&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp; ความเร่งเนื่องจากแรงโน้มถ่วงของโลก มีหน่วยเป็นเมตรต่อวินาที&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/th_ms2.gif&quot; width=&quot;80&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style4&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ค่า g ในบริเวณต่างๆ ทั่วโลกมีค่าไม่เท่ากัน โดยทั่วไปมักให้ใช้ค่า&amp;nbsp; g = 10&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/02_ForceMassNewtonlaw/images/ms2.gif&quot; width=&quot;49&quot; /&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;color: rgb(0, 0, 0); &quot;&gt;--------------------------------------------------------------------------------------------------------------------------------------------&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;', '1342543958', '1342543958');
INSERT INTO `lesson` VALUES ('10', '10', '3', 'การเคลื่อนที่ แบบต่างๆ', '&lt;p class=&quot;style7&quot; style=&quot;font-family: \'MS sans serif\'; font-size: medium; color: rgb(0, 0, 0); &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;span class=&quot;style6&quot; style=&quot;font-size: 18px; font-weight: bold; &quot;&gt;การเคลื่อนที่แบบโพรเจกไทล์&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style7&quot;&gt;โพรเจกไทล์ภาษาอังกฤษ หมายถึง วัตถุที่ขว้างหรือยิงออกไป โดยจะสังเกตได้ว่ามีแนวการ เคลื่อนที่เป็นวิถีโค้ง โดยในบทเรียนจะถือว่าแรงต้านของอากาศน้อยมากจนไม่ต้องนำมาคิด การเคลื่อนที่แบบโพรเจกไทล์เป็นการเคลื่อนที่ใน 2 มิติ คือเคลื่อนที่ในแนวระดับและแนวดิ่ง พร้อมกัน ในแนวดิ่งเป็นการเคลื่อนที่ที่มีความเร่งเนื่องจากแรงโน้มถ่วงโลกในขณะที่แนวราบ ไม่มีความเร่ง ดังรูป&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;219&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_01.gif&quot; width=&quot;594&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	การเคลื่อนที่แบบโพรเจกไทล์ในแนวระดับด้วยความเร็วคงตัว (ไม่มีความเร่ง a = 0) คำนวณการกระจัด&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_05.gif&quot; width=&quot;39&quot; /&gt;ได้จาก&amp;nbsp;&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; hspace=&quot;200&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/02.gif&quot; width=&quot;77&quot; /&gt;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	โดย&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_02.gif&quot; width=&quot;31&quot; /&gt;แทนความเร็วในแนวระดับ มีหน่วยเป็นเมตรต่อวินาที และ&amp;nbsp;&lt;strong&gt;t&amp;nbsp;&lt;/strong&gt;แทนเวลา มีหน่วยเป็นวินาที&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	การเคลื่อนที่แบบโพรเจกไทล์ในแนวดิ่งเป็นการตกแบบเสรี มีความเร่งเนื่องจากแรงโน้มถ่วงของโลก (g) คำนวณความเร็วในแนวดิ่ง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_03.gif&quot; width=&quot;44&quot; /&gt;ที่ตำแหน่งใดๆ ได้จาก&amp;nbsp;&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; hspace=&quot;200&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/01.gif&quot; vspace=&quot;20&quot; width=&quot;80&quot; /&gt;&lt;br /&gt;\r\n	เนื่องจากความเร็วต้นในแนวดิ่งเป็นศูนย์ (&amp;nbsp;&lt;em&gt;u&lt;/em&gt;&amp;nbsp;= 0) จะได้ &amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; hspace=&quot;15&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/03.gif&quot; vspace=&quot;10&quot; width=&quot;73&quot; /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	ส่วนการกระจัดในแนวดิ่ง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_06.gif&quot; width=&quot;38&quot; /&gt;หาได้จากสมการ &amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;43&quot; hspace=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/04.gif&quot; width=&quot;91&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	เนื่องจากความเร็วต้นในแนวดิ่งเป็นศูนย์ (&amp;nbsp;&lt;em&gt;u&lt;/em&gt;&amp;nbsp;= 0) จะได้ &amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;42&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/05.gif&quot; width=&quot;71&quot; /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	การวิเคราะห์ที่ผ่านมาเกิดจากการเคลื่อนที่ด้วยความเร็วต้นในแนวระดับ&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_07.gif&quot; width=&quot;135&quot; /&gt;&amp;nbsp;ถ้าหากความเร็วต้นทำมุม&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;กับแนวระดับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;33&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_08.gif&quot; width=&quot;137&quot; /&gt;เช่น การพุ่งแหลน จะได้รูปแบบการเคลื่อนที่ดังรูป&lt;/p&gt;\r\n&lt;p style=&quot;font-size: 16px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;208&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_09.gif&quot; width=&quot;566&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;span class=&quot;style4&quot;&gt;พิจารณาการเคลื่อนที่ในแนวระดับ คำนวณการกระจัดในแนวระดับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_05.gif&quot; width=&quot;39&quot; /&gt;ได้จาก&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; hspace=&quot;200&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/06.gif&quot; vspace=&quot;10&quot; width=&quot;93&quot; /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style4&quot;&gt;โดย&amp;nbsp;&amp;nbsp;&lt;strong&gt;&lt;em&gt;u&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;&amp;nbsp;แทนความเร็วต้นของวัตถุ หน่วยเป็นเมตรต่อวินาที และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;เป็นมุมที่ความเร็วต้นกระทำกับแนวระดับ หน่วยเป็นองศา&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;span class=&quot;style4&quot; style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;เนื่องจากการเคลื่อนที่ในแนวดิ่งจะมีทั้งทิศขึ้นและลง จึงมีการกำหนดเครื่องหมายในการคำนวณ ถ้าพิจารณาช่วงเวลา t ที่วัตถุเคลื่อนที่ขึ้นจนกระทั่งตกถึงพื้นระดับ โดยมีความเร็วต้นเป็น&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;style4&quot; style=&quot;font-size: 16px; &quot;&gt;+usin&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;&lt;/span&gt;&lt;span class=&quot;style4&quot; style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;ความเร่งเป็น &amp;ndash;g และการกระจัดเป็นศูนย์&lt;/span&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span class=&quot;style4&quot; style=&quot;font-size: 16px; &quot;&gt;จากสมการ&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;39&quot; hspace=&quot;180&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/07.gif&quot; vspace=&quot;20&quot; width=&quot;96&quot; /&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;font-size: 16px; &quot;&gt;จะได้ สมการของการกระจัดแนวดิ่งเป็น&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;38&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/08.gif&quot; style=&quot;font-size: 16px; &quot; width=&quot;125&quot; /&gt;&lt;span class=&quot;style4&quot; style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\nเมื่อแทนค่าการกระจัดเป็นศูนย์จะได้&amp;nbsp;&lt;br /&gt;\r\nเวลาที่ใช้ในการเคลื่อนที่ขึ้นจนกระทั่งตกถึงพื้นระดับเป็น&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;42&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/09.gif&quot; style=&quot;font-size: 16px; &quot; vspace=&quot;20&quot; width=&quot;92&quot; /&gt;&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;เนื่องจากช่วงเวลา&amp;nbsp;&lt;/span&gt;&lt;strong style=&quot;font-size: 16px; &quot;&gt;t&lt;/strong&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;นี้เป็นช่วงเวลาเดียวกันกับที่วัตถุเคลื่อนที่ในแนวระดับ ดังนั้น เราสามารถคำนวณระยะทางในแนวระดับหรือระยะตกของวัตถุ ได้จาก&lt;/span&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;span class=&quot;style4&quot;&gt;&lt;img height=&quot;213&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/11.gif&quot; width=&quot;204&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style4&quot;&gt;จากสมการ สรุปได้ว่าระยะตกจะมีผลจากมุมของความเร็วต้นที่ทำกับแนวระดับ ซึ่งมุมที่ทำให้ได้ระยะสูงสุดคือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;&amp;nbsp;=&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_04.gif&quot; width=&quot;27&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong class=&quot;style7&quot;&gt;&lt;u&gt;โพรเจกไทล์บนพื้นเอียง&amp;nbsp;&lt;/u&gt;&lt;/strong&gt;โพรเจกไทล์สามารถเกิดขึ้นได้บนพื้นเอียง ดังรูป&lt;br /&gt;\r\n	&lt;img height=&quot;163&quot; hspace=&quot;120&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_10.gif&quot; vspace=&quot;15&quot; width=&quot;246&quot; /&gt;&lt;br /&gt;\r\n	การคำนวณโพรเจกไทล์บนพื้นเอียงจำเป็นต้องมีการแตกค่า g เข้าสู่แกน XY จึงทำให้แกน X มีความเร่งด้วย&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;font-size: 16px; &quot;&gt;\r\n	กรณีที่การเคลื่อนที่เป็นวิถีโค้งดังรูป&lt;br /&gt;\r\n	&lt;img height=&quot;218&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_11.gif&quot; vspace=&quot;10&quot; width=&quot;556&quot; /&gt;&lt;/p&gt;\r\n&lt;table border=&quot;0&quot; style=&quot;font-size: 16px; &quot; width=&quot;100%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#FFCC00&quot; colspan=&quot;2&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;strong&gt;&lt;u&gt;สูตรลัด&lt;/u&gt;&lt;/strong&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td width=&quot;63%&quot;&gt;\r\n				1. เวลาที่วัตถุเคลื่อนที่จาก A ไป D หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_12.gif&quot; width=&quot;28&quot; /&gt;&lt;/td&gt;\r\n			&lt;td width=&quot;37%&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;img height=&quot;67&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_16.gif&quot; vspace=&quot;10&quot; width=&quot;145&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				2. เวลาที่วัตถุเคลื่อนที่จาก A ไปถึง C หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_13.gif&quot; width=&quot;28&quot; /&gt;&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;img height=&quot;70&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_17.gif&quot; vspace=&quot;10&quot; width=&quot;149&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				3. ระยะทางที่วัตถุเคลื่อนที่จาก A ไป C หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_14.gif&quot; width=&quot;32&quot; /&gt;&amp;nbsp;(ระยะไกลสุดตามแนวแกน X)&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;img height=&quot;69&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_18.gif&quot; vspace=&quot;10&quot; width=&quot;152&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				4. ระยะสูงสุดในแนวดิ่งจาก B ไป D หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_15.gif&quot; width=&quot;27&quot; /&gt;&amp;nbsp;(ระยะไกลสุดตามแนวแกน Y)&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;img height=&quot;71&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_19.gif&quot; vspace=&quot;10&quot; width=&quot;148&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				5. ความสัมพันธ์ระหว่าง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_14.gif&quot; width=&quot;32&quot; /&gt;กับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_15.gif&quot; width=&quot;27&quot; /&gt;&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;img height=&quot;69&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con1_20.gif&quot; vspace=&quot;10&quot; width=&quot;150&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style8&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;span class=&quot;style6&quot; style=&quot;font-size: 18px; font-weight: bold; &quot;&gt;การเคลื่อนที่แบบวงกลมมีความเร่งสู่ศูนย์กลาง&lt;/span&gt;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span class=&quot;style7&quot;&gt;การเคลื่อนที่เป็นวงกลมที่มีขนาดความเร็วคงตัวแต่มีการเปลี่ยนทิศของความเร็วตลอดเวลา&lt;br /&gt;\r\n	จึงถือว่าการเคลื่อนที่วงกลมมีความเร่ง ดังรูป&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style8&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;282&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con2_01.gif&quot; width=&quot;311&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	พิจารณาช่วงเวลาที่วัตถุใช้ในการเคลื่อนที่จาก A ไป B ด้วยอัตราเร็วคงตัว v &amp;nbsp;จะได้&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n	&lt;img height=&quot;79&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con2_02.gif&quot; vspace=&quot;20&quot; width=&quot;168&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;font-size: 16px; &quot;&gt;\r\n	จากรูป เราหาความยาวของส่วนโค้ง AB ได้เป็น ( 2&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;)r ดังนั้น&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;48&quot; hspace=&quot;250&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/18.gif&quot; width=&quot;83&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	สำหรับการเคลื่อนที่ของวัตถุบนส่วนโค้ง AB ความเร็วในแกน X ที่ A และที่ B จะมีค่าเท่ากัน&amp;nbsp;&amp;nbsp;คือ&amp;nbsp;&lt;br /&gt;\r\n	&lt;img height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con2_03.gif&quot; width=&quot;250&quot; /&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	ดังนั้น ความเร่งแกน Xจึงเป็นศูนย์ และเราสามารถหาความเร่งเฉลี่ยตามแนวแกน y คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con2_05.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;ได้จาก&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;59&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/19.gif&quot; vspace=&quot;30&quot; width=&quot;261&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	เนื่องจาก&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con2_04.gif&quot; width=&quot;78&quot; /&gt;&amp;nbsp;มีทิศลง(เข้าสู่ศูนย์กลาง)จึงมีค่าเป็นลบ&amp;nbsp;&amp;nbsp;แทนค่าจะได้&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;64&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/20.gif&quot; vspace=&quot;30&quot; width=&quot;451&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	เครี่องหมายลบแสดงว่า&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con2_05.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;ความเร่งมีทิศทาง -y คือเข้าสู่จุดศูนย์กลาง เมื่อให้&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;เล็กมาก เพื่อให้จุด A และ B เข้าใกล้ตำแหน่ง C เราจะได้ sin&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;=&lt;img align=&quot;texttop&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;&amp;nbsp;เพราะ&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;&amp;nbsp;มีค่าน้อยเข้าใกล้ศูนย์&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;64&quot; hspace=&quot;100&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/21.gif&quot; vspace=&quot;20&quot; width=&quot;81&quot; /&gt;&amp;nbsp;ทิศเข้าสู่จุดศูนย์กลางวงกลม&lt;/p&gt;\r\n&lt;p style=&quot;font-size: 16px; &quot;&gt;\r\n	และไม่ว่าจะย้าย C ไปอยู่ตำแหน่งใดๆ ก็จะมีเร่งทิศเข้าสู่ศูนย์กลางเสมอ&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;64&quot; hspace=&quot;100&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/22.gif&quot; vspace=&quot;20&quot; width=&quot;81&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;font-size: 16px; &quot;&gt;\r\n	&amp;nbsp;ตามกฏของนิวตันแสดงว่ามีแรงเข้าสู่ศูนย์กลาง&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con2_06.gif&quot; width=&quot;43&quot; /&gt;คือ&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;64&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/23.gif&quot; width=&quot;119&quot; /&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style6&quot; style=&quot;font-size: 18px; font-weight: bold; &quot;&gt;\r\n	การเคลื่อนที่บนถนนโค้ง&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;span class=&quot;style11&quot;&gt;ในกรณีที่รถเลี้ยวโค้ง แรงเสียดทานที่พื้นถนนกระทำกับด้านข้างของยางรถจะเป็นแรงสู่ศูนย์&lt;br /&gt;\r\n	กลาง&amp;nbsp;ที่ทำให้รถยนต์เลี้ยวโค้งได้ และเนื่องจากแรงเสียดทานมีค่าจำกัดขึ้นกับสภาพถนน และยางรถ ดังนั้นแรงสู่ศูนย์กลางจึงมีค่าจำกัดด้วย&amp;nbsp;&amp;nbsp; หากถนนมีความโค้งขนาดหนึ่งอัตราเร็ว&lt;/span&gt;&lt;span class=&quot;style11&quot;&gt;ที่รถวิ่งขณะเลี้ยวโค้งต้องไม่เกินกว่าแรงเสียดทานทิศสู่ศูนย์กลาง &amp;nbsp;หากอัตราเร็วเกินก็จะทำให้&lt;/span&gt;&lt;span class=&quot;style11&quot;&gt;รถไถลออกนอกโค้งเกิดอันตรายได้&lt;/span&gt;&lt;span class=&quot;style11&quot;&gt;&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	การเลี้ยวโค้งจะง่ายและปลอดภัยขึ้นหากพื้นถนนถูกยกให้ ขอบถนนด้านนอกสูงกว่าขอบด้านใน ดังรูป&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;img height=&quot;180&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con3_01.gif&quot; width=&quot;269&quot; /&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	จาก&lt;img align=&quot;absmiddle&quot; height=&quot;50&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/24.gif&quot; vspace=&quot;20&quot; width=&quot;100&quot; /&gt;&lt;br /&gt;\r\n	ดังนั้น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;50&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/25.gif&quot; vspace=&quot;20&quot; width=&quot;113&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	และ&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/26.gif&quot; vspace=&quot;20&quot; width=&quot;108&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	ดังนั้น &amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;47&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/27.gif&quot; vspace=&quot;30&quot; width=&quot;121&quot; /&gt;หรือ&lt;img align=&quot;absmiddle&quot; height=&quot;47&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/28.gif&quot; vspace=&quot;30&quot; width=&quot;96&quot; /&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span class=&quot;style6&quot; style=&quot;font-size: 18px; font-weight: bold; &quot;&gt;การเคลื่อนที่แบบวงกลมในระนาบดิ่ง&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style12&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;การเคลื่อนที่ในระนาบดิ่งเช่น การเคลื่อนที่ของลูกกลมโลหะตามราง ดังรูป&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style12&quot;&gt;\r\n	&lt;img height=&quot;182&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/graph1.gif&quot; width=&quot;290&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style12&quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style12&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ถ้าให้&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con4_01.gif&quot; width=&quot;29&quot; /&gt;เป็นแรงสู่ศูนย์กลาง จะได้&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;42&quot; hspace=&quot;60&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/29.gif&quot; width=&quot;126&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style14&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;แสดงว่า แรงที่รางดันลูกกลมเหล็กทิศตั้งฉาก&lt;img align=&quot;absmiddle&quot; height=&quot;49&quot; hspace=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/30.gif&quot; width=&quot;130&quot; /&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;การเคลื่อนที่แบบวงกลมในแนวระดับ&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;img height=&quot;310&quot; hspace=&quot;50&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con4_02.gif&quot; vspace=&quot;20&quot; width=&quot;372&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ผูกเชือกยาว&amp;nbsp;&lt;strong&gt;l&lt;/strong&gt;&amp;nbsp;กับวัตถุ&amp;nbsp;&lt;strong&gt;m&lt;/strong&gt;&amp;nbsp;แกว่งให้หมุนทิศทวนเข็มนาฬิกา จะได้สมการดังนี้&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n	แรงตึงเชือก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;70&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con4_03.gif&quot; vspace=&quot;20&quot; width=&quot;147&quot; /&gt;&lt;br /&gt;\r\n	แ&lt;span style=&quot;font-size:16px;&quot;&gt;รงตึงเชือก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;68&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con4_04.gif&quot; vspace=&quot;20&quot; width=&quot;145&quot; /&gt;&lt;br /&gt;\r\n	คาบ&lt;img align=&quot;absmiddle&quot; height=&quot;69&quot; hspace=&quot;80&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con4_05.gif&quot; vspace=&quot;20&quot; width=&quot;147&quot; /&gt;&lt;br /&gt;\r\n	ความถี่&lt;img align=&quot;absmiddle&quot; height=&quot;71&quot; hspace=&quot;65&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con4_06.gif&quot; vspace=&quot;20&quot; width=&quot;147&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span class=&quot;style6&quot; style=&quot;font-size: 18px; font-weight: bold; &quot;&gt;การเคลื่อนที่ของดาวเทียม&lt;/span&gt;&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;หากดาวเทียมมวล m โครจรรอบโลกด้วยอัตราเร็ว v ณ ตำแหน่งวงโครจรซึ่งห่างจากจุดศูนย์&lt;br /&gt;\r\n	กลางของโลกเป็นระยะ r ให้ M เป็นมวลของโลก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con4_01.gif&quot; width=&quot;29&quot; /&gt;เป็นแรงสู่ศูนย์กลาง ซึ่งเป็นแรงดึงดูดที่โลกกระทำกับดาวเทียม และหาค่าของแรงนี้ได้จากกฏแรงดึงดูดระหว่างมวลของนิวตัน&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จะได้&lt;img align=&quot;absmiddle&quot; height=&quot;53&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/31.gif&quot; vspace=&quot;20&quot; width=&quot;130&quot; /&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ดังนั้น&lt;img align=&quot;absmiddle&quot; height=&quot;58&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/32.gif&quot; vspace=&quot;20&quot; width=&quot;140&quot; /&gt;&lt;br /&gt;\r\n	จะได้&lt;img align=&quot;absmiddle&quot; height=&quot;68&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/33.gif&quot; vspace=&quot;20&quot; width=&quot;153&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ได้อัตราเร็วของการโคจรของดาวเทียมเป็น&lt;img align=&quot;absmiddle&quot; height=&quot;68&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con4_07.gif&quot; vspace=&quot;10&quot; width=&quot;153&quot; /&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;strong&gt;การเคลื่อนที่แบบฮาร์โมนิกอย่างง่าย&lt;/strong&gt;&lt;/p&gt;\r\n&lt;span style=&quot;font-size: 16px; &quot;&gt;การเคลื่อนที่แบบฮาร์โมนิกอย่างง่าย(Simple Harmonic Motion : SHM) เป็นการเคลื่อนที่แบบเป็นคาบอย่างหนึ่ง คือ เคลื่อนที่กลับไปมาซ้ำทางเดิมโดยผ่านตำแหน่งสมดุล และมีคาบของการเคลื่อนที่คงตัว เช่น การเคลื่อนที่ของวัตถุติดปลายสปริง เป็นต้น&lt;/span&gt;&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;font-size: 16px; &quot;&gt;ความถี่ (f) คือ จำนวนรอบของการเคลื่อนที่ใน 1 วินาที หน่วยเป็น เฮิรตซ์ (Hz)&lt;/span&gt;&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;font-size: 16px; &quot;&gt;คาบ (T) คือ เวลในการเคลื่อนที่ครบ 1 รอบ หน่วยเป็นวินาที (s)&lt;/span&gt;&lt;br style=&quot;font-size: 16px; &quot; /&gt;\r\n&lt;span class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;ความถี่และคาบมีความสัมพันธ์กันตามสมการ&lt;img align=&quot;absmiddle&quot; height=&quot;66&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/34.gif&quot; vspace=&quot;10&quot; width=&quot;102&quot; /&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n&lt;p class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	การกระจัด x ในรูปฟังก์ชั่นของเวลา t ของ SHM เขียนได้เป็น&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;41&quot; hspace=&quot;200&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/35.gif&quot; vspace=&quot;20&quot; width=&quot;193&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	ซึ่ง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con5_01.gif&quot; width=&quot;31&quot; /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;เป็นการกระจัดสูงสุดหรือแอมพิจูด&amp;nbsp;&lt;br /&gt;\r\n	&lt;img align=&quot;texttop&quot; height=&quot;22&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con5_02.gif&quot; width=&quot;25&quot; /&gt;เป็นความถี่เชิงมุมมีค่าเท่ากับ&lt;img align=&quot;absmiddle&quot; height=&quot;74&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con5_03.gif&quot; width=&quot;44&quot; /&gt;หรือ &amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;53&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con5_04.gif&quot; width=&quot;35&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con5_05.gif&quot; width=&quot;27&quot; /&gt;เป็นค่าคงตัวทางเฟสหมายถึงเฟสเริ่มต้น&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;304&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con5_06.gif&quot; width=&quot;370&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	จากรูป หากอนุภาคเริ่มเคลื่อนที่จากตำแหน่งสมดุล ( x = 0) ซึ่งมีลักษณะเช่นเดียวกับกราฟของ&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;70&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/37.gif&quot; vspace=&quot;10&quot; width=&quot;227&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	จะได้สมการการเคลื่อนที่แบบ SHM รูปทั่วไปเป็น&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;69&quot; hspace=&quot;100&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/38.gif&quot; vspace=&quot;20&quot; width=&quot;227&quot; /&gt;เมื่อ A คือแอมพลิจูด&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	ลักษณะสำคัญประการหนึ่งของการเคลื่อนที่แบบฮาร์มอนิกอย่าง่ายคือ การมีความเร่งแปรผันตรง กับการ กระจัด&amp;nbsp;&amp;nbsp;แต่มีทิศตรงกันข้าม โดยทิศของความเร่งจะเป็นทิศเดียวกับเรง และแรงจะต้องเป็นแรงเข้าหา&lt;br /&gt;\r\n	จุดสมดุลในณะที่การกระจัดมีทิศออกไปจากจุดสมดุล ดังสมการ&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style7&quot; style=&quot;font-size: 16px; &quot;&gt;\r\n	ความเร่งของการเคลื่อนที่แบบฮาร์มอนิก&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con5_07.gif&quot; width=&quot;48&quot; /&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;69&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/39.gif&quot; vspace=&quot;20&quot; width=&quot;227&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span class=&quot;style16&quot;&gt;&lt;span class=&quot;style6&quot; style=&quot;font-size: 18px; font-weight: bold; &quot;&gt;วัตถุติดปลายสปริง&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;เมื่อทดลองวัตถุติดปลายสปริงเราจะพบว่าวัตถุมีการเคลื่อนที่ผ่านตำแหน่งสมดุลไปมา ถือเป็น การเคลื่อนที่แบบฮาร์มอนิกอย่างง่ายประเภทหนึ่งดังรูป&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style12&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img height=&quot;193&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/40.gif&quot; width=&quot;240&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;แรงที่สปริงทำต่อวัตถุมีค่าเป็น&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;&lt;em&gt;&amp;nbsp;F = -kx&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n	เมื่อ k คือค่าคงตัวสปริง และ x คือการกระจัดของวัตถุ วัดจากตำแหน่งสมดุล&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จากกฏของนิวตันจะได้&lt;img align=&quot;absmiddle&quot; height=&quot;44&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/41.gif&quot; width=&quot;263&quot; /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;เมื่อพิจารณาว่าการเคลื่อนที่นั้นอยู่ในแนวแกน&amp;nbsp;&lt;em&gt;x&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จากสมการ&lt;img align=&quot;absmiddle&quot; height=&quot;43&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/42.gif&quot; width=&quot;112&quot; /&gt;จะได้&lt;img align=&quot;absmiddle&quot; height=&quot;52&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/43.gif&quot; width=&quot;121&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ดังนั้น&lt;img align=&quot;absmiddle&quot; height=&quot;61&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/44.gif&quot; vspace=&quot;20&quot; width=&quot;117&quot; /&gt;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;67&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/45.gif&quot; vspace=&quot;20&quot; width=&quot;202&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style5&quot; style=&quot;&quot;&gt;&lt;strong&gt;การแกว่งของลูกตุ้ม&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style7&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ขณะที่ปล่อยลูกตุ้มมวล&amp;nbsp;&lt;strong&gt;m&lt;/strong&gt;&amp;nbsp;ซึ่งผูกกับเส้นเชือกยาว&amp;nbsp;&lt;strong&gt;l&lt;/strong&gt;&amp;nbsp;เอียงทำมุม&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;&amp;nbsp;กับแนวดิ่ง ดังรูป&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style7&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img height=&quot;174&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/46.gif&quot; width=&quot;153&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style7&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;ถ้ามุม&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/seta.gif&quot; width=&quot;17&quot; /&gt;&amp;nbsp;เป็นมุมเล็กๆการเคลื่อนที่โค้งประมาณได้ว่าเป็นเส้นตรงคือการกระจัด x และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;57&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con7_01.gif&quot; width=&quot;109&quot; /&gt;&lt;br /&gt;\r\n	ให้ F เป็นแรงดึงมวลกลับสู่ตำแหน่งสมดุล&lt;img align=&quot;absmiddle&quot; height=&quot;33&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/47.gif&quot; width=&quot;129&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style7&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จะได้&lt;img align=&quot;absmiddle&quot; height=&quot;65&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/48.gif&quot; width=&quot;173&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จาก&lt;img align=&quot;absmiddle&quot; height=&quot;42&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/49.gif&quot; width=&quot;127&quot; /&gt;จะได้&lt;img align=&quot;absmiddle&quot; height=&quot;64&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/50.gif&quot; width=&quot;351&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;style7&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จะเห็นว่าความเร่งของลูกตุ้มแปรผันตรงกับการกระจัดและมีทิศตรงข้ามกับการแกว่งของลูกตุ้ม&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	เนื่องจากอัตราเร่งของการแกว่ง&lt;img align=&quot;absmiddle&quot; height=&quot;34&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con7_02.gif&quot; width=&quot;88&quot; /&gt;แทนค่าใน&lt;img align=&quot;absmiddle&quot; height=&quot;45&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con7_03.gif&quot; width=&quot;83&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style7&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จะได้&lt;img align=&quot;absmiddle&quot; height=&quot;61&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/5.gif&quot; vspace=&quot;20&quot; width=&quot;109&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; class=&quot;style7&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;จาก&lt;img align=&quot;absmiddle&quot; height=&quot;35&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/con7_04.gif&quot; width=&quot;68&quot; /&gt;จะได้&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style7&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img height=&quot;199&quot; src=&quot;http://www.obeclms.com/lesson/03_OtherMotion/images/51.gif&quot; vspace=&quot;20&quot; width=&quot;119&quot; /&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;span style=&quot;color: rgb(0, 0, 0); &quot;&gt;--------------------------------------------------------------------------------------------------------------------------------------------&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;br /&gt;', '1344532354', '1344532354');
INSERT INTO `lesson` VALUES ('11', '10', '4', 'งานและพลังงาน', '&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;58&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top1.gif&quot; width=&quot;222&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;blockquote style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;p align=&quot;justify&quot; class=&quot;style8&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;งาน คือ ผลของแรงที่กระทำบนวัตถุ และทำให้วัตถุเคลื่อนที่ไปตามแนวแรง ซึ่งเป็นปริมาณสเกลาร์ มีหน่วยเป็นนิวตันเมตร (N.m)&amp;nbsp;&lt;br /&gt;\r\n		หน่วยนี้มีชื่อใหม่ว่า จูล (Joule, J)&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ในกรณีแรง F ที่มากระทำเป็นแรงคงตัวและการกระจัด S ของวัตถุอยู่ในแนวเดียวกับแรง ปริมาณงานที่แรง F กระทำจะมีค่าเท่ากับ&amp;nbsp;&lt;br /&gt;\r\n		ผลคูณระหว่างขนาดของแรงและขนาดของการกระจัด&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;50&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml1.jpg&quot; width=&quot;128&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;blockquote style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;177&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_pic1.jpg&quot; width=&quot;500&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;กรณีที่แรง F กระทำต่อวัตถุในแนวทำมุม&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; hspace=&quot;2&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/seta.gif&quot; width=&quot;15&quot; /&gt;กับทิศการเคลื่อนที่ของวัตถุ&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;177&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_pic2.jpg&quot; width=&quot;500&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;55&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml2.jpg&quot; width=&quot;160&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;55&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml3.jpg&quot; width=&quot;252&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;เมื่อ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; hspace=&quot;2&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/seta.gif&quot; width=&quot;15&quot; /&gt;เป็นมุมระหว่างทิศของแรงที่กระทำกับทิศการเคลื่อนที่ของวัตถุ&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;งานที่เกิดจากแรงกระทำไม่อยู่ในแนวเดียวกับการเคลื่อนของวัตถุจะหาได้จากผลคูณระหว่างขนาดของแรงองค์ประกอบในแนว&lt;br /&gt;\r\n		การเคลื่อนที่กับขนาดการกระจัดของวัตถุที่เกิดขึ้นในช่วงที่แรงนี้กระทำดังนั้น&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;102&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml4.jpg&quot; width=&quot;450&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;55&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml5.jpg&quot; width=&quot;173&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-family: \'times new roman\', times, serif; font-size: 16px; text-align: -webkit-right; &quot;&gt;--------------------------------------------------------------------------------&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;58&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top2.gif&quot; width=&quot;252&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;blockquote&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;กำลัง เป็นปริมาณที่ใช้วัดขีดความสามารถหรือประสิทธิภาพของการทำงานของระบบ ระบบใดที่สามารถทำงานอันหนึ่ง ได้เร็วกว่าอีกระบบหนึ่ง ถือว่าระบบนั้นมีกำลังสูงกว่า ดังนั้น&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;กำลัง ก็คือ อัตราการทำงานหรือปริมาณงานที่ทำได้ในหนึ่งหน่วยเวลา&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;79&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml9.jpg&quot; width=&quot;120&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;102&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml10.gif&quot; width=&quot;353&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;หน่วยของกำลังในระบบ SI คือ จูลต่อวินาที (J/s) หรือวัตต์ (Watt, W)&lt;br /&gt;\r\n		ในกรณีที่ช่วงเวลาที่พิจารณาเป็นช่วงสั้น ๆ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml37.jpg&quot; width=&quot;100&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;74&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml11.jpg&quot; width=&quot;312&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;เมื่อ&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml12.jpg&quot; width=&quot;30&quot; /&gt;&amp;nbsp;คงที่ และถ้าแรง&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml12.jpg&quot; width=&quot;30&quot; /&gt;มีทิศทางเดียวกับความเร็ว&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml13.jpg&quot; width=&quot;27&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;50&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml14.jpg&quot; width=&quot;112&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-family: \'times new roman\', times, serif; font-size: 16px; text-align: -webkit-right; &quot;&gt;--------------------------------------------------------------------------------&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;58&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top3.gif&quot; width=&quot;324&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;blockquote&gt;\r\n	&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; พลังงานกล หรือพลังงานทางกลศาสตร์ พลังงานกลของวัตถุมี 2 รูปแบบที่ต่างกันชัดเจน ได้แก่ พลังงานที่ขึ้นกับความเร็วของวัตถุ เรียกว่า พลังงานจลน์ และพลังงานที่ขึ้นกับตำแหน่งของวัตถุ เรียกว่า พลังงานศักย์&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top31.gif&quot; width=&quot;223&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; วัตถุที่กำลังเคลื่อนที่จะนับว่ามีพลังงานจลน์ วัตถุที่อยู่นิ่งไม่มีพลังงานจลน์ พลังงานจลน์ไม่ขึ้นกับทิศทางของการเคลื่อนที่&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;148&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_pic7.jpg&quot; width=&quot;550&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;style30&quot;&gt;&amp;nbsp;สมมติให้มีแรง ๆ เดียวที่คงตัวกระทำต่อวัตถุมวล m ที่อยู่นิ่งให้เคลื่อนที่ด้วยความเร่ง มวล m ย่อมเคลื่อนที่เป็นไปตาม กฎของ&lt;br /&gt;\r\n		นิวตัน คือ F = ma ความเร่งอยู่ในแนวเส้นตรงตามทิศของแรง ความเร่งจะมีค่าคงตัวเพราะแรงคงตัว ให้แรงกระทำอยู่เป็นเวลา t จนวัตถุมีความเร็ว v2 ที่ต้องการ จะหาว่าวัตถุมีพลังงานจลน์เท่าใดจากงานที่แรงคงตัวนั้นกระทำ&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;270&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml18.jpg&quot; width=&quot;529&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;FS คือ งานที่ทำโดยแรงสุทธิ F&lt;br /&gt;\r\n		งาน = การเปลี่ยนแปลงพลังงานจลน์&lt;br /&gt;\r\n		นั่นคือ พลังงานจลน์&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;75&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml19.jpg&quot; width=&quot;177&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;ความหมายของสมการที่ (1) คือ งานของแรงลัพธ์ที่กระทำต่อวัตถุ จะเท่ากับ พลังงานจลน์ของวัตถุที่เปลี่ยนไป&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot; class=&quot;style32&quot; style=&quot;font-family: Tahoma; font-weight: bold; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;ความสัมพันธ์ระหว่างงานและพลังงาน&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;br /&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;212&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml38.gif&quot; width=&quot;329&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;อธิบายได้ว่าวัตถุจะเปลี่ยนแปลงความเร็วและพลังงานจลน์ได้ต่อเมื่อ มีองค์ประกอบของแรงลัพธ์ในแนวการเคลื่อนที่เท่านั้น และงานที่เพิ่มขึ้นของวัตถุก็คือ งานขององค์ประกอบของแรงลัพธ์ในแนวการเคลื่อนที่ซึ่งสอดคล้องกับสูตรของงาน&amp;nbsp;&lt;strong&gt;&lt;em&gt;(W = FS)&lt;/em&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;right&quot;&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;34&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top32.gif&quot; width=&quot;220&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;style30&quot;&gt;พลังงานศักย์ของวัตถุซึ่งเกิดจากการมีตำแหน่งสูงขึ้น เกี่ยวข้องกับแรงโน้มถ่วงที่โลกกระทำต่อวัตถุ เรียกว่า พลังงานศักย์โน้มถ่วง&amp;nbsp;&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;สำหรับการกดสปริงให้หดสั้นลง หรือยืดสปริงให้ยืดออก เมื่อปล่อยมือสปริงจะมีการเคลื่อนที่ แสดงให้เห็นว่ามีการทำงานโดยสปริง พลังงานนี้ก็คือ พลังงานศักย์เช่นกัน พลังงานศักย์ที่เกี่ยวข้องกับสมบัติการยืดหยุ่น เรียกว่า พลังงานศักย์ยืดหยุ่น&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top33.gif&quot; width=&quot;285&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;183&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_pic8.jpg&quot; width=&quot;403&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ในการยกวัตถุมวล m ให้สูงขึ้น h ในแนวดิ่งด้วยความเร็วคงตัว จะต้องออกแรง F ซึ่งมีขนาดเท่ากับน้ำหนักของวัตถุ mg จึงจะยกขึ้นได้ตามต้องการ เนื่องจากงานในการยกวัตถุให้สูงขึ้น h เท่ากับ Fh จูล และจาก F = mg จึงต้องทำงาน&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;48&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml20.jpg&quot; width=&quot;185&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;จากสมการจะเห็นว่า งานของแรงภายนอกที่ใช้ในการยกวัตถุให้สูงขึ้นจากพื้นเป็นระยะ h นั้นมีค่าเท่ากับ mgh ซึ่งจะมีค่าเท่ากับงานที่ทำโดยแรงโน้มถ่วงของโลกต่อวัตถุ แต่งานของแรงโน้มถ่วงจะมีค่าเป็นลบ เพราะทิศของแรงตรงกันข้ามกับทิศของการกระจัด ปริมาณ mgh ซึ่งเป็นงานของแรงภายนอกเอาชนะแรงของสนามโน้มถ่วง ถือว่าเป็นพลังงานศักย์โน้มถ่วงของวัตถุนั่นเอง&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ถ้าใช้สัญลักษณ์ &amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_pic10.jpg&quot; width=&quot;35&quot; /&gt;&amp;nbsp;แทนพลังงานศักย์โน้มถ่วง จะเขียนสมการได้ดังนี้&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;50&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml21.jpg&quot; width=&quot;173&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;ทั้งนี้โดยกำหนดให้พลังงานศักย์โน้มถ่วงมีค่าเท่ากับศูนย์ เมื่อวัตถุอยู่ที่พื้นหรือระดับใดระดับหนึ่งที่ใช้เป็นระดับอ้างอิง สรุปได้ว่า พลังงานศักย์โน้มถ่วงเป็นพลังงานศักย์ที่เปรียบเทียบกับพื้นซึ่งใช้เป็นระดับอ้างอิง พลังงานศักย์ของวัตถุมีค่าเพิ่มขึ้น เมื่อวัตถุ อยู่สูงขึ้นจากระดับอ้างอิง อาจมีค่าลบเมื่ออยู่ต่ำกว่าระดับอ้างอิง&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;right&quot;&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top34.gif&quot; width=&quot;282&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;เกิดจากวัตถุที่ติดอยู่กับสิ่งยืดหยุ่นมีการเปลี่ยนแปลงจากตำแหน่งหนึ่งไปสู่อีกตำแหน่งหนึ่ง เช่น วัตถุที่ติดสปริง พลังงานที่สะสมอยู่ในสปริงที่ทำให้สปริงยืดออกหรือหดเข้าจากตำแหน่งสมดุล&amp;nbsp;&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;จากการทดลองยึดปลายข้างหนึ่งของสปริงไว้ แล้วใช้เครื่องชั่งสปริงเกี่ยวที่ปลายสปริงอีกข้างหนึ่ง วางสปริงและเครื่องชั่งสปริงอยู่ตรงขีดศูนย์ของไม้บรรทัด เพิ่มแรงดึงเครื่องชั่งสปริงให้สปริงยืดออกครั้งละเท่า ๆ กัน บันทึกขนาดของแรงดึงกับระยะที่สปริงยืดออกจากตำแหน่งสมดุล แล้วเขียนกราฟระหว่างขนาดของแรงดึง (F) กับระยะทางที่สปริงยืดออก (x) จะได้ดังนี้&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;166&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_pic9.jpg&quot; width=&quot;223&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;จากกราฟ จะได้&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml22.jpg&quot; width=&quot;137&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;69&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml23.jpg&quot; width=&quot;561&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;จากกราฟจะสังเกตเห็นว่า แรงที่ดึงสปริงให้ยืดออกนั้นไม่คงตัว แต่เพิ่มขึ้นอย่างสม่ำเสมอจนถึงตำแหน่งสุดท้ายที่ดึง สมมติว่าใช้แรงดึงเท่ากับ F ทำให้สปริงยืดออก x งานที่ต้องทำในการยืดสปริงเช่นนั้นเป็นเท่าใด อาจหางานที่กระทำจากแรงเฉลี่ยคูณกับการกระจัดได้ เนื่องจากแรงที่ดึงเพิ่มขึ้นอย่างสม่ำเสมอ ค่าแรงเฉลี่ยจะเท่ากับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;&quot; height=&quot;45&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/content1_clip_image002.gif&quot; width=&quot;57&quot; /&gt;&amp;nbsp;และงานที่ได้จึงเป็น&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;79&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml24.jpg&quot; width=&quot;324&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;แทนค่า&amp;nbsp;&lt;img align=&quot;absbottom&quot; border=&quot;1&quot; height=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml22.jpg&quot; width=&quot;137&quot; /&gt;&amp;nbsp;จะได้&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;69&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml25.jpg&quot; width=&quot;171&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;สมการนี้ก็คือ พื้นที่สามเหลี่ยมใต้กราฟเส้นตรงระหว่าง F และ x นั่นเอง&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ถ้าถือว่าสปริงที่ยังไม่ยืดไม่มีพลังงานศักย์ในตัว ค่า&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;44&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml26.jpg&quot; width=&quot;55&quot; /&gt;&amp;nbsp;ก็คือค่าพลังงานศักย์ในสปริงขณะที่สปริงยืดออกเป็นระยะ x นั่นเอง พลังงานศักย์นี้นับเป็นพลังงานศักย์ยืดหยุ่น&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;span style=&quot;font-family: \'times new roman\', times, serif; font-size: 16px; text-align: -webkit-right; &quot;&gt;------------------------------------------------------------------------------&lt;/span&gt;&lt;span style=&quot;font-family: \'times new roman\', times, serif; font-size: 16px; text-align: -webkit-right; &quot;&gt;--&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;58&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top4.gif&quot; width=&quot;625&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;blockquote&gt;\r\n	&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ถ้าปล่อยวัตถุจากที่สูงระดับหนึ่งให้ตกแบบอิสระ ณ ตำแหน่งความสูงต่าง ๆ ของการเคลื่อนที่ ความเร็วของวัตถุจะเปลี่ยน ทำให้ขณะที่ตกทั้งพลังงานจลน์และพลังงานศักย์โน้มถ่วงเปลี่ยนแปลงตลอดเวลา แต่ผลบวกของพลังงานศักย์และพลังงานจลน์ของวัตถุ ซึ่งจะเรียกว่า พลังงานกลของวัตถุ จะมีค่าคงตัวทุกขณะ&amp;nbsp;&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;สมมติว่าปล่อยวัตถุมวล m ให้ตกลงแบบเสรี ถ้าขณะที่มวลอยู่สูง &amp;nbsp;h&lt;span class=&quot;style12&quot; style=&quot;&quot;&gt;0&lt;/span&gt;&amp;nbsp;จากพื้นดิน วัตถุมีความเร็ว u เมื่อมวลตกลงต่อไปอีกจนอยู่ที่ระดับสูง h จากพื้นดิน วัตถุมีความเร็ว v และเนื่องจากการเคลื่อนที่นี้มีความเร่งคงตัว g จะได้&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;43&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml27.jpg&quot; width=&quot;282&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;เมื่อคูณด้วย&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;54&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml28.jpg&quot; width=&quot;55&quot; /&gt;ทั้งสองข้างจะได้&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;189&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml29.jpg&quot; width=&quot;400&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;จากสมการนี้แสดงให้เห็นว่าพลังงานจลน์ของวัตถุที่เพิ่มขึ้นเท่ากับพลังงานศักย์โน้มถ่วงของวัตถุที่ลดลง&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;สรุปได้ว่า การเคลื่อนที่แบบเสรีของวัตถุภายใต้สนามโน้มถ่วงของโลกโดยไม่มีแรงภายนอกมากระทำ พลังงานกลของวัตถุ ณ ตำแหน่งใดก็ตาม ย่อมมีค่าคงเดิมเสมอ เมื่อวัตถุตกลงพลังงานศักย์โน้มถ่วงจะลดลง ค่าที่ลดลงจะเปลี่ยนไปเป็นพลังงานจลน์ที่เพิ่มขึ้นทุกขณะ&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; text-align: center; &quot;&gt;\r\n	&lt;span style=&quot;font-family: \'times new roman\', times, serif; font-size: 16px; text-align: -webkit-right; &quot;&gt;-------------------------------------------------------&lt;/span&gt;&lt;span style=&quot;font-family: \'times new roman\', times, serif; font-size: 16px; text-align: -webkit-right; &quot;&gt;-------------------------&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;img height=&quot;58&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top5.gif&quot; style=&quot;font-size: 16px; font-family: \'times new roman\', times, serif; color: rgb(0, 0, 0); &quot; width=&quot;731&quot; /&gt;&lt;/p&gt;\r\n&lt;blockquote style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;พิจารณาการเคลื่อนที่ของรถทดลองติดปลายสปริง ซี่งเป็นการเคลื่อนที่แบบฮาร์มอนิกอย่างง่าย สมมติให้รถทดลองเริ่มต้นเคลื่อนที่จากตำแหน่งที่มีการกระจัดมากที่สุด ซึ่งพลังงานศักย์ยืดหยุ่นจะมีค่ามากสุดและพลังงานจลน์เป็นศูนย์ จากนั้นพลังงานศักย์ยืดหยุ่นจะลดลงโดยส่วนที่ลดจะเปลี่ยนรูปไปเป็นพลังงานจลน์ พลังงานศักย์ยืดหยุ่นจะเปลี่ยนไปเป็นพลังงานจลน์ทั้งหมดขณะที่ผ่านที่ตำแหน่งสมดุล ซึ่งพลังงานจลน์จะมีค่าสูงสุด เมื่อผ่านตำแหน่งสมดุลแล้วพลังงานจลน์จะลดลงและทำให้พลังงานศักย์ยืดหยุ่นเพิ่มขึ้น จนถึงตำแหน่งที่มีการกระจัดมากที่สุด ซึ่งเป็นไปตามกฎการอนุรักษ์พลังงานกล&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;39&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml30.jpg&quot; width=&quot;742&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;63&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml31.jpg&quot; width=&quot;319&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;68&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml32.jpg&quot; width=&quot;259&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;จากสมการอาจเขียนความสัมพันธ์ระหว่างความเร็วและการกระจัดที่ตำแหน่งใด ๆ ได้ว่า&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;45&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml33.jpg&quot; width=&quot;247&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;38&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml34.jpg&quot; width=&quot;438&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;ดังนั้น&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;49&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml35.jpg&quot; width=&quot;207&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;และจะได้ว่า ความเร็วสูงสุดหรือแอมพลิจูดของความเร็ว คือ&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style30&quot; style=&quot;font-family: Tahoma; &quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;44&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml36.jpg&quot; width=&quot;142&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;style30&quot; style=&quot;font-family: Tahoma; text-align: center; &quot;&gt;\r\n	&lt;span style=&quot;font-family: \'times new roman\', times, serif; font-size: 16px; text-align: -webkit-right; &quot;&gt;--------------------------------------------------------------------------------&lt;/span&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img height=&quot;73&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_top6.gif&quot; width=&quot;340&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;blockquote style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;style30&quot;&gt;เครื่องกล ได้แก่อุปกรณ์ที่ช่วยให้การทำงานสะดวกขึ้น เช่นงานในการยกของหนักเครื่องกลจะช่วยให้ยกได้โดยใช้แรงที่น้อยลง เป็นต้น การอธิบายว่าเครื่องกลต่าง ๆ ช่วยให้ใช้แรงน้อยลงหรือทำงานได้สะดวกขึ้นนั้น อาจอาศัยหลักการของงาน เนื่องจากงานและพลังงาน เป็นปริมาณที่คงตัว เครื่องกลจะไม่ช่วยให้เราทำงานได้มากกว่าที่เราทำงานให้กับเครื่องกล แต่อาจสูญเสียงานไปเล็กน้อย&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;41&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml15.jpg&quot; width=&quot;756&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;สำหรับระบบเครื่องกลที่ดี งานของแรงเสียดทานจะมีค่าน้อยเมื่อเทียบกับงานที่เครื่องกลกระทำ จึงอาจประมาณได้ว่า&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;42&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml16.jpg&quot; width=&quot;475&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;ประสิทธิภาพของเครื่องกล (Efficiency of machine: E) หาได้จาก&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;41&quot; src=&quot;http://www.obeclms.com/lesson/04_WorkEnergy/images/c_fml17.jpg&quot; width=&quot;621&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-family: \'times new roman\', times, serif; font-size: 16px; text-align: -webkit-right; &quot;&gt;--------------------------------------------------------------------------------&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', '1344538732', '1344538732');
INSERT INTO `lesson` VALUES ('12', '10', '5', 'โมเมนตัมและการดล', '&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&lt;u&gt;โมเมนตัม&lt;/u&gt;&lt;/strong&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;โมเมน เป็นปริมาณที่บอกสภาพการเคลื่อนที่&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;โมเมนตัม เป็นปริมาณปริมาณเวคเตอร์ที่บ่งบอก ขนาดและทิศทาง&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ที่ได้จากผลคูณระหว่างมวล และ ความเร็ว ซึ่งมีทิศเดียวกับทิศของความเร็ว&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;img height=&quot;43&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_pmv.gif&quot; width=&quot;92&quot; /&gt;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;5&quot; cellspacing=&quot;3&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot; width=&quot;434&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot;&gt;\r\n				&lt;div align=&quot;right&quot;&gt;\r\n					&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;เมื่อ&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;29&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_p.gif&quot; width=&quot;25&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot; width=&quot;315&quot;&gt;\r\n				คือ โมเมนตัมของวัตถุ มีหน่วยเป็นกิโลกรัมเมตร/วินาที&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n				&lt;div align=&quot;right&quot; class=&quot;style8&quot; style=&quot;font-size: 18px; font-weight: bold; &quot;&gt;\r\n					&lt;img height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_m.gif&quot; width=&quot;26&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n				คือ มวลของวัตถุ มีหน่วยเป็นกิโลกรัม&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot;&gt;\r\n				&lt;div align=&quot;right&quot;&gt;\r\n					&lt;img height=&quot;33&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_v.gif&quot; width=&quot;22&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n				คือ ความเร็ววัตถุ มีหน่วยเป็นเมตร/ วินาที&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&lt;u&gt;แรงและการเปลี่ยนโมเมนตัม&lt;/u&gt;&lt;/strong&gt;&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;จากกฎการเคลื่อนที่ข้อที่สองของนิวตัน ถ้า F เป็นแรงเดียวที่กระทำต่อมวล หรือแรงลัพธ์&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&lt;img height=&quot;50&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_fma.gif&quot; width=&quot;124&quot; /&gt;&lt;br /&gt;\r\n	ซึ่งหมายถึง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;63&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_favg.gif&quot; width=&quot;144&quot; /&gt;&amp;nbsp;หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;72&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_mdv.gif&quot; width=&quot;107&quot; /&gt;&amp;nbsp;หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;73&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_dp.gif&quot; width=&quot;86&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; color: rgb(0, 0, 0); &quot;&gt;\r\n	โดยที่มวลของวัตถุมีค่าคงตัว (หากไม่คิดที่ความเร็วสูง ตามทฤษฎีสัมพัทธภาพ)&lt;br /&gt;\r\n	จึงอาจจะเขียนได้ว่า&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;img height=&quot;72&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_dpdt.gif&quot; width=&quot;188&quot; /&gt;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot; width=&quot;67%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot; width=&quot;20%&quot;&gt;\r\n				&lt;div align=&quot;right&quot;&gt;\r\n					&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;เมื่อ&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;59&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_01_dpdt_m.gif&quot; width=&quot;31&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot; width=&quot;80%&quot;&gt;\r\n				คือ อัตราการเปลี่ยนโมเมนตัมของวัตถุต่อเวลา&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n	&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;จากสมการแปลความได้ว่า&lt;/span&gt;&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&amp;nbsp;&amp;quot;แรงลัพธ์กระทำกับวัตถุที่ขณะใด ๆ เท่ากับ อัตราการเปลี่ยนโมเมนตัมของวัตถุที่ขณะนั้น ทั้งขนาดและทิศทาง&amp;quot;&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style1&quot;&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&amp;nbsp;&lt;strong&gt;&lt;u&gt;การดลและแรงดล&lt;/u&gt;&lt;/strong&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;แรงดล&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;32&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_f.gif&quot; width=&quot;37&quot; /&gt;คือ แรงที่กระทำต่อวัตถุในช่วงเวลาสั้นๆ เช่น ตีลูกเทนนิส ค้อนตอกตะปู&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;การดล&lt;img align=&quot;absmiddle&quot; height=&quot;34&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_i.gif&quot; width=&quot;32&quot; /&gt;&amp;nbsp;คือ โมเมนตัมที่เปลี่ยนไป&lt;img align=&quot;absmiddle&quot; height=&quot;29&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_p.gif&quot; width=&quot;115&quot; /&gt;&amp;nbsp;เป็นปริมาณเวคเตอร์มีทิศเดียวกับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;33&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_f1.gif&quot; width=&quot;20&quot; /&gt;&lt;br /&gt;\r\n	มีหน่วย นิวตัน.วินาที&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;33&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_ns.gif&quot; width=&quot;55&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;color: rgb(0, 0, 0); &quot; width=&quot;80%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#FFFF66&quot; class=&quot;style1&quot;&gt;\r\n				&lt;p&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;ถ้ามีวัตถุมวล&amp;nbsp;&lt;em&gt;m&lt;/em&gt;&amp;nbsp;เดิมมีความเร็ว&amp;nbsp;&lt;em&gt;u&lt;/em&gt;&amp;nbsp;จะมีโมเมนตัม&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_p1mv.gif&quot; width=&quot;73&quot; /&gt;&amp;nbsp;&lt;br /&gt;\r\n					เมื่อมีแรง F กระทำในช่วงเวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_t.gif&quot; width=&quot;22&quot; /&gt;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n					ทำให้วัตถุเปลี่ยนความเร็วเป็น v มีโมเมนตัมเป็น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_pmv.gif&quot; width=&quot;71&quot; /&gt;&amp;nbsp;จะได้&lt;/span&gt;&lt;/p&gt;\r\n				&lt;p align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;img height=&quot;48&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_formular.gif&quot; width=&quot;368&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;strong&gt;&lt;u&gt;กราฟระหว่างแรงกับเวลา&lt;/u&gt;&lt;/strong&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ถ้าเราวาดกราฟระหว่างแรง (F) กับเวลา (t) ดังรูป พบว่าพื้นที่ใต้กราฟมีค่าเท่ากับขนาดของการดล หรือขนาดของการเปลี่ยนแปลงโมเมนตัม&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&lt;img height=&quot;189&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_graph1.gif&quot; width=&quot;462&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;การหาค่าของการดลจากผลคูณของแรง&amp;nbsp;&lt;strong&gt;F&lt;/strong&gt;&amp;nbsp;กับช่วงเวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; hspace=&quot;2&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_t_white.gif&quot; width=&quot;28&quot; /&gt;นั้นแรงที่ใช้ต้องคงตัว แต่ในการกระทบกันของวัตถุทั่วไปแรงมีขนาดไม่คงตัว ดังนั้นจึงต้องใช้แรงเฉลี่ยในการคำนวณ&lt;br /&gt;\r\n	แรงเฉลี่ยหาได้จากพื้นที่ใต้กราฟระหว่างแรงและเวลาดังนี้&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;img height=&quot;212&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/content_02_graph.gif&quot; width=&quot;454&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span style=&quot;color: rgb(0, 0, 0); &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;จากกราฟ ถ้าให้&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_fav.gif&quot; style=&quot;color: rgb(0, 0, 0); &quot; width=&quot;31&quot; /&gt;&lt;span style=&quot;color: rgb(0, 0, 0); &quot;&gt;เป็นขนาดของแรงเฉลี่ย ที่ทำให้พื้นที่ใต้กราฟของแรงคงตัว&lt;/span&gt;&lt;br style=&quot;color: rgb(0, 0, 0); &quot; /&gt;\r\n&lt;span style=&quot;color: rgb(0, 0, 0); &quot;&gt;เท่ากับพื้นที่ใต้กราฟของแรงไม่คงตัว ในช่วงเวลา&amp;nbsp;&lt;/span&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_t_white.gif&quot; style=&quot;color: rgb(0, 0, 0); &quot; width=&quot;28&quot; /&gt;&lt;span style=&quot;color: rgb(0, 0, 0); &quot;&gt;&amp;nbsp;ซึ่งหมายถึงว่า&lt;/span&gt;&lt;/span&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&amp;quot; การดลภายใต้แรง&lt;span class=&quot;style24&quot; style=&quot;font-size: 18px; &quot;&gt;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_02_fav_b.gif&quot; width=&quot;33&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;เท่ากับการดลที่เกิดขึ้นจริง &amp;quot;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&lt;u&gt;กฎการอนุรักษ์โมเมนตัม&lt;/u&gt;&lt;/strong&gt;&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ถ้าให้วัตถุ a และ b เคลื่อนที่เข้าชนกันดังรูป&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;object classid=&quot;clsid:D27CDB6E-AE6D-11cf-96B8-444553540000&quot; codebase=&quot;http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=7,0,19,0&quot; height=&quot;350&quot; width=&quot;350&quot;&gt;&lt;param name=&quot;quality&quot; value=&quot;high&quot; /&gt;&lt;param name=&quot;movie&quot; value=&quot;http://www.obeclms.com/lesson/05_Momentum/momentum.swf&quot; /&gt;&lt;embed height=&quot;350&quot; pluginspage=&quot;http://www.macromedia.com/go/getflashplayer&quot; quality=&quot;high&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/momentum.swf&quot; type=&quot;application/x-shockwave-flash&quot; width=&quot;350&quot;&gt;&lt;/embed&gt;&lt;/object&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; style=&quot;color: rgb(0, 0, 0); &quot; width=&quot;90%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td width=&quot;23%&quot;&gt;\r\n				&lt;div align=&quot;right&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;จากสมการการดล&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td width=&quot;77%&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img border=&quot;1&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_03_ft.gif&quot; width=&quot;143&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;right&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;จะได้&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_03_fab.gif&quot; width=&quot;181&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;right&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;และ&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_03_fba.gif&quot; width=&quot;185&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td colspan=&quot;2&quot;&gt;\r\n				&lt;div align=&quot;left&quot;&gt;\r\n					&lt;br /&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;จากกฎข้อที่ 3 ของนิวตัน ขณะที่วัตถุชนกัน แรงกิริยาเท่ากับแรงปฏิกิริยา(ทิศตรงข้าม)&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&amp;nbsp;&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;br /&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img align=&quot;absmiddle&quot; border=&quot;1&quot; height=&quot;36&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_03_fab-fba.gif&quot; width=&quot;116&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;right&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;แทนค่า&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_03_mv1.gif&quot; width=&quot;238&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&amp;nbsp;&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_03_mv2.gif&quot; width=&quot;223&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&amp;nbsp;&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_03_mv3.gif&quot; width=&quot;211&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td colspan=&quot;2&quot;&gt;\r\n				&lt;br /&gt;\r\n				&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;จึงสรุปได้ว่า&lt;strong class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&amp;nbsp;ถ้าไม่มีแรงลัพธ์มากระทำต่อระบบแล้วโมเมนตัมของระบบจะคงตัว&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br style=&quot;color: rgb(0, 0, 0); &quot; /&gt;\r\n&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFF99&quot; border=&quot;2&quot; bordercolor=&quot;#FFCC00&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; style=&quot;color: rgb(0, 0, 0); &quot; width=&quot;60%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#FFFFFF&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;strong&gt;จาก&amp;nbsp;&lt;img height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ep.gif&quot; width=&quot;33&quot; /&gt;ก่อนชน =&amp;nbsp;&lt;img height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ep.gif&quot; width=&quot;33&quot; /&gt;&amp;nbsp;หลังชน&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;span class=&quot;style13&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;u&gt;ตัวอย่างในชีวิตประจำวัน&lt;/u&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;การยิงปืนของทหารก็ถือว่าเป็นกฎการอนุรักษ์โมเมนตัม ลูกปืนที่พุ่งจากปากกระบอกปืนไปข้างหน้าด้วยความเร็ว จะทำให้กระบอกปืนเกิดการเคลื่อนที่ทิศตรงกันข้าม(ถอยหลัง) ดังนั้นทหารจึงจำเป็นต้องใช้ไหล่ยันตัวปืนไว้ให้ปืนอยู่กับที่&lt;strong&gt;&lt;u&gt;&amp;nbsp;&lt;/u&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n		&amp;nbsp;&lt;/li&gt;\r\n	&lt;li style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;การระเบิดเป็นเสี่ยงๆของลูกระเบิดนั้นเริ่มต้นจากเดิมที่ลูกระเบิดอยู่กับที่ ดังนั้นเมื่อระเบิดแล้วโมเมนตัมรวมทั้งหมดยังคงต้องเท่ากับศูนย์ นั่นคือ โมเมนตัมก่อนระเบิด = โมเมนตัมหลังระเบิด&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n		&amp;nbsp;&lt;/li&gt;\r\n	&lt;li style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;การเดินบนเรือ จะเห็นว่าเรือเคลื่อนที่ถอยหลังไปในทิศทางตรงข้ามกับทิศทางที่เราเดิน เพื่อรักษาโมเมนตัมรวมของระบบให้มีค่าคงเดิม&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;div style=&quot;color: rgb(0, 0, 0); &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;p style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;strong&gt;&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&lt;u&gt;การชน&lt;/u&gt;&lt;/span&gt;&lt;/strong&gt;&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; หมายถึง การที่วัตถุหนึ่งไปกระทบกับอีกวัตถุหนึ่งในช่วงสั้นๆ หรืออาจจะไม่กระทบกัน &amp;nbsp;&amp;nbsp;แต่มีแรงมากระทำ เช่น ตีลูกบอล ยิงปืน ระเบิด เป็นต้น&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; การชนขณะที่มี&lt;u&gt;แรงภายนอกมากระทำน้อยมาก&lt;/u&gt;เมื่อเทียบกับขนาดแรงดล โมเมนตัมของระบบจะมีค่าคงที่ เช่น การชนของลูกสนุกเกอร์&lt;br /&gt;\r\n		&lt;object classid=&quot;clsid:D27CDB6E-AE6D-11cf-96B8-444553540000&quot; codebase=&quot;http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=7,0,19,0&quot; height=&quot;300&quot; hspace=&quot;100&quot; width=&quot;300&quot;&gt;&lt;param name=&quot;quality&quot; value=&quot;high&quot; /&gt;&lt;param name=&quot;movie&quot; value=&quot;http://www.obeclms.com/lesson/05_Momentum/snook.swf&quot; /&gt;&lt;embed height=&quot;300&quot; hspace=&quot;100&quot; pluginspage=&quot;http://www.macromedia.com/go/getflashplayer&quot; quality=&quot;high&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/snook.swf&quot; type=&quot;application/x-shockwave-flash&quot; width=&quot;300&quot;&gt;&lt;/embed&gt;&lt;/object&gt;&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;การชนขณะที่มี&lt;u&gt;แรงภายนอกมากระทำมาก&lt;/u&gt;เมื่อเทียบกับขนาดแรงดล โมเมนตัมของระบบจะมีค่าไม่คงที่ เช่น รถยนต์ชนต้นไม้&lt;br /&gt;\r\n		&lt;object classid=&quot;clsid:D27CDB6E-AE6D-11cf-96B8-444553540000&quot; codebase=&quot;http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=7,0,19,0&quot; height=&quot;100&quot; hspace=&quot;100&quot; width=&quot;350&quot;&gt;&lt;param name=&quot;quality&quot; value=&quot;high&quot; /&gt;&lt;param name=&quot;movie&quot; value=&quot;http://www.obeclms.com/lesson/05_Momentum/crash.swf&quot; /&gt;&lt;embed height=&quot;100&quot; hspace=&quot;100&quot; pluginspage=&quot;http://www.macromedia.com/go/getflashplayer&quot; quality=&quot;high&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/crash.swf&quot; type=&quot;application/x-shockwave-flash&quot; width=&quot;350&quot;&gt;&lt;/embed&gt;&lt;/object&gt;&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ถ้าหากพิจารณาทิศทางของการชนเป็นหลักแล้ว ในบทเรียนนี้จะมีการชนสองรูปแบบคือ&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;ul style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;strong&gt;การชนแบบ 1 มิต&lt;/strong&gt;ิ คือ การชนของวัตถุที่อยู่ในระนาบเดียวกัน เมื่อมีการชนแล้วการเคลื่อนที่ของวัตถุทั้งสองก็จะอยู่ในแนวเดียวกัน เนื่องจากเป็นการชนผ่านจุดศูนย์กลางมวล&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n			&amp;nbsp;&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;strong&gt;การชนแบบ 2 มิต&lt;/strong&gt;ิ คือ การชนของวัตถุที่อยู่ในระนาบเดียวกัน เมื่อมีการชนแล้วการเคลื่อนที่ของวัตถุทั้งสองจะไม่อยู่ในแนวเดียวกัน&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n	&lt;p style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;การชนในแบบ 2 มิตินั้น จะมีโมเมนตัมคงเดิม แต่พลังงานจลน์จะคงเดิมหรือไม่แล้วแต่กรณี &amp;nbsp;เราจึงแบ่งการชนเป็น 2 ประเภท คือ&lt;/span&gt;&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&amp;nbsp;&amp;nbsp;1. การชนแบบยืดหยุ่น คือ การชนที่ไม่มีการสูญเสียพลังงานจลน์&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFF99&quot; border=&quot;2&quot; bordercolor=&quot;#FF9900&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot; width=&quot;90%&quot;&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#FFFFFF&quot; bordercolor=&quot;#FFFFFF&quot; class=&quot;style5&quot;&gt;\r\n					&lt;div align=&quot;center&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n						&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img align=&quot;texttop&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ep.gif&quot; width=&quot;33&quot; /&gt;ก่อนชน =&lt;img align=&quot;texttop&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ep.gif&quot; width=&quot;33&quot; /&gt;&amp;nbsp;หลังชน&lt;br /&gt;\r\n						และ&lt;br /&gt;\r\n						พลังงานรวมก่อนชน =พลังงานรวมหลังชน&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/tbody&gt;\r\n	&lt;/table&gt;\r\n	&lt;p style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&lt;br /&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;การชนกันของวัตถุเมื่อความเร็วอยู่ในแนวผ่านจุดศูนย์กลางมวล&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img height=&quot;37&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_m1u1m2u2.gif&quot; width=&quot;252&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p class=&quot;style1&quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;การชนในแนวเส้นตรงเมื่อวัตถุถูกชนอยู่นิ่งจะได้&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img height=&quot;69&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_v2.gif&quot; width=&quot;150&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; &quot;&gt;2. การชนแบบไม่ยืดหยุ่น คือ การชนที่พลังงานจลน์ไม่คงที่&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFF99&quot; border=&quot;2&quot; bordercolor=&quot;#FF9900&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot; width=&quot;90%&quot;&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#FFFFFF&quot; bordercolor=&quot;#FFFFFF&quot; class=&quot;style5&quot;&gt;\r\n					&lt;div align=&quot;center&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n						&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img align=&quot;texttop&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ep.gif&quot; width=&quot;33&quot; /&gt;ก่อนชน =&lt;img align=&quot;texttop&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ep.gif&quot; width=&quot;33&quot; /&gt;&amp;nbsp;หลังชน&lt;br /&gt;\r\n						แต่&lt;br /&gt;\r\n						พลังงานรวมก่อนชน&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_notequal.gif&quot; width=&quot;23&quot; /&gt;&amp;nbsp;พลังงานรวมหลังชน&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/tbody&gt;\r\n	&lt;/table&gt;\r\n	&lt;p align=&quot;left&quot; class=&quot;style1&quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;กรณีที่มวล&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_m1.gif&quot; width=&quot;24&quot; /&gt;&amp;nbsp;เคลื่อนที่เข้าชนมวล&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;29&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_m2.gif&quot; width=&quot;29&quot; /&gt;&amp;nbsp;ทำให้มวลเปลี่ยนรูปแล้วเคลื่อนที่ไปด้วยกันด้วยความเร็ว v ดังรูป&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style1&quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img height=&quot;102&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_pic.gif&quot; width=&quot;306&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot; class=&quot;style1&quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;จากกฎการอนุรักษ์โมเมนตัม การชนแบบไม่ยืดหยุ่นมีความสัมพันธ์ ดังสมการ&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFF99&quot; border=&quot;2&quot; bordercolor=&quot;#FF9900&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot; width=&quot;53%&quot;&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#FFFFFF&quot; bordercolor=&quot;#FFFFFF&quot;&gt;\r\n					&lt;div align=&quot;left&quot;&gt;\r\n						&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;จะได้&lt;/span&gt;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;46&quot; hspace=&quot;3&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_formular.gif&quot; width=&quot;230&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/tbody&gt;\r\n	&lt;/table&gt;\r\n	&lt;p style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;\r\n		&lt;br /&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;strong&gt;&amp;nbsp;&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;u&gt;การชนแนวเฉียง&lt;/u&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p class=&quot;style1&quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;การชนในแนวเฉียงหมายถึงการชนที่ไม่ผ่านจุดศูนย์กลางมวลของวัตถุทั้งสองก้อนทำให้การชนอยู่ใน 2 มิติดังรูป&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style1&quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img height=&quot;317&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_pic2.gif&quot; width=&quot;437&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p class=&quot;style1&quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;การชนแนวเฉียงจะมีความสัมพันธ์ตามสมการ&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;span style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n	&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFF99&quot; border=&quot;2&quot; bordercolor=&quot;#FF9900&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot; width=&quot;53%&quot;&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#FFFFFF&quot; bordercolor=&quot;#FFFFFF&quot;&gt;\r\n					&lt;div align=&quot;left&quot;&gt;\r\n						&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;span class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; &quot;&gt;&lt;img height=&quot;72&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_seta.gif&quot; width=&quot;313&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/tbody&gt;\r\n	&lt;/table&gt;\r\n	&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;span style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n	&lt;p class=&quot;style1&quot;&gt;\r\n		&lt;br /&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;u&gt;พลังงานที่สูญหายไปในการชน&lt;/u&gt;&lt;/strong&gt;&amp;nbsp;&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; การชนแบบไม่ยืดหยุ่น พลังงานจลน์รวมของระบบก่อนชนไม่เท่ากับพลังงานจลน์รวมหลังชน ส่วนหนึ่งของพลังงานสูญหายไปเป็นพลังงานความร้อนหรือพลังงานเสียงที่เกิดขึ้นขณะชนกัน&lt;br /&gt;\r\n		&lt;span class=&quot;style1&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;พลังงานจลน์ที่หายไปในการชน = พลังงานจลน์รวมก่อนชน - พลังงานจลน์รวมหลังชน&lt;/span&gt;&amp;nbsp;&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;u&gt;การวัดความเร็วของกระสุนปืนด้วย&amp;nbsp;&lt;/u&gt;&lt;/strong&gt;&lt;strong&gt;&lt;u&gt;ballastic pendulum&lt;/u&gt;&lt;/strong&gt;&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Ballastic pendulum เป็นวัตถุ เช่น ถุงทรายห้อยแขวนด้วยเชือกติดกับเพดานดังรูป ซึ่งสามารถเหวี่ยงตัวได้อย่างอิสระ&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; class=&quot;style1&quot;&gt;\r\n		&lt;br /&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img height=&quot;217&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_pic3.gif&quot; width=&quot;467&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p class=&quot;style1&quot;&gt;\r\n		&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;จากรูป ลูกปืนมวล m วิ่งเข้าชนถุงทรายมวล M ด้วยความเร็ว u ถุงทรายห้อยแขวนด้วยเส้นเชือกติดกับเพดาน&amp;nbsp;&lt;u&gt;การชนเป็นแบบไม่ยืดหยุ่น&lt;/u&gt;&amp;nbsp;โดยลูกปืนฝังเข้าไปในถุงทรายและเคลื่อนที่เหวี่ยงตัวไปด้วยกันด้วยความเร็ว v เราสามารถหาค่าความเร็วของลูกปืนก่อนชนได้จากความสัมพันธ์&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFF99&quot; border=&quot;2&quot; bordercolor=&quot;#FF9900&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; &quot; width=&quot;53%&quot;&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#FFFFFF&quot; bordercolor=&quot;#FFFFFF&quot;&gt;\r\n					&lt;div align=&quot;center&quot;&gt;\r\n						&lt;span class=&quot;style16&quot; style=&quot;font-family: Tahoma; color: rgb(0, 51, 102); font-size: 14px; font-weight: bold; &quot;&gt;&lt;span class=&quot;style17&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;&lt;img height=&quot;67&quot; src=&quot;http://www.obeclms.com/lesson/05_Momentum/images/ebook_04_u.gif&quot; width=&quot;209&quot; /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n					&lt;div&gt;\r\n						&amp;nbsp;&lt;/div&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/tbody&gt;\r\n	&lt;/table&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	--------------------------------------------------------------------------------------------------------------------------------------------&lt;br /&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;', '1344601451', '1344601451');
INSERT INTO `lesson` VALUES ('13', '10', '6', 'การเคลื่อนที่แบบหมุน', '&lt;p class=&quot;head_content&quot; style=&quot;color: rgb(0, 102, 0); font-weight: bold; font-size: 14px; line-height: 16px; font-family: Tahoma; padding: 0px 34px; &quot;&gt;\r\n	1. การหมุน ความเร็วและความเร่งเชิงมุม&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	การเคลื่อนที่แบบวงกลมของวัตถุที่ประกอบด้วยหลายอนุภาค หรืออาจเรียกง่าย ๆ ว่า&amp;nbsp;&lt;span class=&quot;important_word&quot; style=&quot;color: rgb(255, 0, 102); &quot;&gt;การหมุนของวัตถุ&lt;/span&gt;&amp;nbsp;เช่น&amp;nbsp;&lt;span class=&quot;midTxt1&quot;&gt;การหมุนของลูกข่าง การหมุนของพัดลมเพดาน&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เป็นต้น ในบทนี้เราจะพิจารณาเฉพาะการหมุนของวัตถุในอุดมคติ คือวัตถุที่มีรูปร่างแน่นอนสมบูรณ์ โดยไม่เปลี่ยนแปลงรูปร่างและขนาด&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เมื่อวัตถุเปลี่ยนสภาพการเคลื่อนที่ เราเรียกวัตถุในอุดมคตินี้ว่า&amp;nbsp;&lt;span class=&quot;important_word&quot; style=&quot;color: rgb(255, 0, 102); &quot;&gt;วัตถุแข็งเกร็ง&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;26%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;left&quot;&gt;\r\n					&lt;img height=&quot;209&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/pic1.gif&quot; width=&quot;253&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	เช่นเดียวกับกรณีของการศึกษาการเคลื่อนที่แบบ 1 มิติ ของวัตถุ เราจะเริ่มจากการศึกษาถึงลักษณะการหมุนของวัตถุรอบแกนนิ่ง ดังแสดงในรูปที่ 1&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	โดยยังไม่คำนึงถึงสาเหตุที่ทำให้วัตถุเกิดการหมุนจากรูปที่ 1 ถ้าจุด p เป็นจุดจุดหนึ่งบนวัตถุและจุดนี้อยู่ในระนาบ xy ตำแหน่งของจุด p แสดงด้วย&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เวกเตอร์บอกตำแหน่ง&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;16&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_r.gif&quot; width=&quot;12&quot; /&gt;&amp;nbsp;เพื่อง่ายต่อการบอกตำแหน่ง ณ เวลาใด ๆ ของวัตถุ เราจะบอกตำแหน่งของวัตถุในพิกัดเชิงขั้ว ซึ่งอยู่ในรูปของ (r,&lt;img align=&quot;absbottom&quot; height=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_1.gif&quot; width=&quot;13&quot; /&gt;)&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	โดยที่ตำแหน่งอยู่ห่างจากจุดศูนย์กลางการหมุนเท่ากับ r และทำมุม&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_1.gif&quot; width=&quot;13&quot; /&gt;&amp;nbsp;กับแกน x มุม &amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_1.gif&quot; width=&quot;13&quot; /&gt;&amp;nbsp;นี้อาจเรียกว่า เป็น&lt;span class=&quot;important_word&quot; style=&quot;color: rgb(255, 0, 102); &quot;&gt;ตำแหน่งเชิงมุมของจุด p&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	&lt;span class=&quot;important_word&quot; style=&quot;color: rgb(255, 0, 102); &quot;&gt;ข้อควรระวัง&lt;/span&gt;&amp;nbsp; การวัดค่ามุม&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_1.gif&quot; width=&quot;13&quot; /&gt;&amp;nbsp;ที่เป็นธรรมชาติที่สุด คือการวัดในหน่วยเรเดียน (rad)&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;1 รอบวงกลม = 2&lt;img align=&quot;absbottom&quot; height=&quot;14&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_3.gif&quot; width=&quot;13&quot; /&gt;&amp;nbsp;เรเดียน&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	ถ้าวัตถุมีการหมุนรอบแกน z ซึ่งเป็นแนวที่ตั้งฉากกับระนาบกระดาษ ตำแหน่งเชิงมุมของวัตถุจะมีการเปลี่ยนแปลงไป (เมื่อใช้จุด p เป็นตัวแทนของวัตถุ)&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	จะทำให้เกิดการกระจัดเชิงมุม&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;16&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_4.gif&quot; width=&quot;23&quot; /&gt;&amp;nbsp;ในช่วงเวลา&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;15&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_5.gif&quot; width=&quot;22&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;head_sub&quot; style=&quot;font-size: 14px; font-family: Tahoma; color: rgb(153, 0, 0); padding: 0px 70px; &quot;&gt;\r\n	- ขนาดความเร็วเชิงมุมเฉลี่ยของวัตถุหาได้จาก&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;576&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;45&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_6.gif&quot; width=&quot;82&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot; width=&quot;200&quot;&gt;\r\n				rad/s&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;head_sub&quot; style=&quot;font-size: 14px; font-family: Tahoma; color: rgb(153, 0, 0); padding: 0px 70px; &quot;&gt;\r\n	- และขนาดความเร็วเชิงมุมขณะใดขณะหนึ่งของวัตถุ คือ&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;576&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;47&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_7.gif&quot; width=&quot;125&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot; width=&quot;200&quot;&gt;\r\n				rad/s&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	ถ้าเราหาการเปลี่ยนแปลงความเร็วเชิงมุมขณะใดขณะหนึ่ง เราจะได้ปริมาณที่เรียกว่า&amp;nbsp;&lt;span class=&quot;important_word&quot; style=&quot;color: rgb(255, 0, 102); &quot;&gt;ความเร่งเชิงมุม&lt;/span&gt;&amp;nbsp;(&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;14&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_8.gif&quot; width=&quot;14&quot; /&gt;&amp;nbsp;อ่านว่า อัลฟา)&lt;/p&gt;\r\n&lt;p class=&quot;head_sub &quot; style=&quot;font-size: 14px; font-family: Tahoma; color: rgb(153, 0, 0); padding: 0px 70px; &quot;&gt;\r\n	- ความเร่งเชิงมุมเฉลี่ยของวัตถุหาได้จาก&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;576&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;39&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_9.gif&quot; width=&quot;76&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot; width=&quot;200&quot;&gt;\r\n				&lt;img height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/rad.gif&quot; width=&quot;41&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;head_sub&quot; style=&quot;font-size: 14px; font-family: Tahoma; color: rgb(153, 0, 0); padding: 0px 70px; &quot;&gt;\r\n	- และความเร่งเชิงมุมขณะใดขณะหนึ่ง หาได้จาก&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;576&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;45&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_10.gif&quot; width=&quot;128&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot; width=&quot;200&quot;&gt;\r\n				&lt;img height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/rad.gif&quot; width=&quot;41&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	เนื่องจากความเร็วเชิงมุม และความเร่งเชิงมุมต่างก็เป็นปริมาณเวกเตอร์เราสามารถหาทิศของความเร็วเชิงมุมได้จากกฎมือขวา&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	โดยกำนิ้วทั้งสี่ไปในทิศที่วัตถุกำลังหมุน ทิศที่นิ้วหัวแม่มือชี้ไปแสดงถึงทิศทางของความเร็วเชิงมุม&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;200&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;img height=&quot;211&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/right_hand.png&quot; width=&quot;194&quot; /&gt;&lt;/td&gt;\r\n			&lt;td&gt;\r\n				&lt;img height=&quot;219&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/right_hand2.png&quot; width=&quot;226&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td colspan=&quot;2&quot;&gt;\r\n				&lt;div align=&quot;center&quot; class=&quot;heading&quot; style=&quot;font-weight: bold; line-height: 16px; &quot;&gt;\r\n					รูปที่ 2&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	เวกเตอร์ของความเร่งเชิงมุมจะมีทิศทางเดียวกับทิศทางของความเร็วเชิงมุมก็ต่อเมื่อขนาดของความเร็วเชิงมุมเพิ่มขึ้น&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	และความเร่งเชิงมุมจะมีทิศทางตรงข้ามกับทิศทางของความเร็วเชิงมุม เมื่อขนาดของความเร็วเชิงมุมลดลง&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	&lt;span class=&quot;head_content&quot; style=&quot;color: rgb(0, 102, 0); font-weight: bold; line-height: 16px; padding: 0px 34px; &quot;&gt;การหมุนด้วยความเร่งเชิงมุมคงตัว&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	ถ้าการหมุนของวัตถุมีความเร็วเชิงมุมต้นที่เวลา t = 0 เป็น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq11.gif&quot; width=&quot;22&quot; /&gt;&amp;nbsp;ที่เวลา t ใด ๆ มี&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;19&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_14.gif&quot; width=&quot;92&quot; /&gt;&amp;nbsp;มีความเร็วเชิงมุมเป็น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_15.gif&quot; width=&quot;16&quot; /&gt;&amp;nbsp;ในช่วงเวลานั้น&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	ความเร่งเชิงมุมคงที่เท่ากับ จะได้สมการการเคลื่อนที่แบบหมุน เมื่อพิจารณาขนาดของปริมาณต่าง ๆ จะได้&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;576&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;44&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_16.gif&quot; width=&quot;87&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot; width=&quot;200&quot;&gt;\r\n				(1)&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_17.gif&quot; width=&quot;86&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot;&gt;\r\n				(2)&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เมื่อพิจารณาการกระจัดเชิงมุม โดยใช้ความเร็วเชิงมุมเฉลี่ย&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;567&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;48&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_18.gif&quot; width=&quot;98&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot; width=&quot;200&quot;&gt;\r\n				(3)&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เมื่อนำ t จาก (1) แทนใน (3) จะได้&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;567&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img height=&quot;50&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_19.gif&quot; width=&quot;153&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;right&quot; width=&quot;200&quot;&gt;\r\n				&amp;nbsp;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img height=&quot;47&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_20.gif&quot; width=&quot;96&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;right&quot;&gt;\r\n				&amp;nbsp;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;34&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_21.gif&quot; width=&quot;118&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot;&gt;\r\n				(4)&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เมื่อนำ จากสมการ (2) แทนใน (4) จะได้&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;567&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img height=&quot;47&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_22.gif&quot; width=&quot;139&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot; width=&quot;200&quot;&gt;\r\n				&amp;nbsp;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;43&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_23.gif&quot; width=&quot;107&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;left&quot;&gt;\r\n				&amp;nbsp;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	สมการสำหรับการเคลื่อนที่แบบหมุนที่มีแกนหมุนคงที่มีรูปแบบคล้ายกันมากกับสมการการเคลื่อนที่ในแนวเส้นตรงที่มีความเร่งคงที่ เมื่อเปรียบเทียบกัน จะได้ดังนี้&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;567&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; bgcolor=&quot;#FF9999&quot; class=&quot;heading&quot; style=&quot;font-weight: bold; line-height: 16px; &quot; width=&quot;459&quot;&gt;\r\n				สมการการเคลื่อนที่แบบเส้นตรง&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; bgcolor=&quot;#FF9999&quot; class=&quot;heading&quot; style=&quot;font-weight: bold; line-height: 16px; &quot; width=&quot;387&quot;&gt;\r\n				สมการการเคลื่อนที่แบบหมุน&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;459&quot;&gt;\r\n				&lt;img height=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_t1.gif&quot; width=&quot;74&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;387&quot;&gt;\r\n				&lt;img height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_t11.gif&quot; width=&quot;89&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;43&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_t2.gif&quot; width=&quot;86&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;45&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_t22.gif&quot; width=&quot;106&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;44&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_t3.gif&quot; width=&quot;95&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;43&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_t33.gif&quot; width=&quot;118&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_t4.gif&quot; width=&quot;96&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;29&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_t44.gif&quot; width=&quot;112&quot; /&gt;&lt;br /&gt;\r\n				&amp;nbsp;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p class=&quot;head_content&quot; style=&quot;color: rgb(0, 102, 0); font-weight: bold; font-size: 14px; line-height: 16px; font-family: Tahoma; padding: 0px 34px; &quot;&gt;\r\n	2. ทอร์กกับการเคลื่อนที่แบบหมุน&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	ในการเคลื่อนที่เชิงเส้น เมื่อมีการกระทำต่อวัตถุจะทำให้วัตถุมีความเร่งในทิศของแรง ในการเคลื่อนที่แบบหมุนทอร์กที่กระทำต่อวัตถุก็เกี่ยวข้อง&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	กับความเร่งเชิงมุมของวัตถุด้วย ซึ่งจะได้ศึกษาเชิงวิเคราะห์ดังต่อไปนี้&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	เมื่อพิจารณาชิ้นส่วนเล็ก ๆ ของแผ่นกลมที่หมุนดังรูปที่ 3&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;42%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot;&gt;\r\n				&lt;img height=&quot;173&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_pic.gif&quot; width=&quot;189&quot; /&gt;\r\n				&lt;p class=&quot;midTxt1 style1&quot; style=&quot;line-height: 18px; padding: 0px 38px 10px; &quot;&gt;\r\n					รูปที่ 3 วัตถุมวล&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;32&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c21.gif&quot; width=&quot;31&quot; /&gt;&amp;nbsp;เคลื่อนที่ในแนววงกลมรัศมี&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_0.gif&quot; width=&quot;23&quot; /&gt;&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	ให้ชิ้นส่วนเล็ก ๆ มีมวล&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;32&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c21.gif&quot; width=&quot;31&quot; /&gt;อยู่ที่ระยะ จากแกนหมุน และให้มีแรงย่อย กระทำต่อมวลนั้น (ซึ่งอาจ เป็นแรงที่ถ่ายทอดมาจากชิ้นส่วนอื่น ๆ ของแผ่น)&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เมื่อคิดการเคลื่อนที่เชิงเส้นของส่วนมวล&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;32&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c21.gif&quot; width=&quot;31&quot; /&gt;มวลนั้นจะมีความเร่ง&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/c2_ai.gif&quot; width=&quot;19&quot; /&gt;ในแนวตั้งฉากกับรัศมี เป็นไปตามสมการ&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;308&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;298&quot;&gt;\r\n				&lt;img class=&quot;style1&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c25.gif&quot; style=&quot;color: rgb(255, 51, 102); &quot; width=&quot;81&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เราจะศึกษาทอร์กที่กระทำต่อมวลย่อยและการเคลื่อนที่เชิงมุมรอบแกนหมุน โดยนิยามทอร์กที่กระทำกับวัตถุมีค่าดังนี้&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;308&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;298&quot;&gt;\r\n				&lt;img height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c26.gif&quot; width=&quot;73&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	เมื่อ&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;16&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_r.gif&quot; width=&quot;12&quot; /&gt;&amp;nbsp;เป็นเวกเตอร์ตำแหน่งของจุดที่แรงกระทำจากจุดอ้างอิงบนแกนหมุน&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	ในกรณีนี้ ทอร์กที่เกิดจากแรง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c23.gif&quot; width=&quot;17&quot; /&gt;&amp;nbsp;รอบแกนหมุน คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c27.gif&quot; width=&quot;71&quot; /&gt;&amp;nbsp;เพราะมุมระหว่าง&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c28.gif&quot; width=&quot;16&quot; /&gt;&amp;nbsp;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c29.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;เป็น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/c2_90.gif&quot; width=&quot;22&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	จากการเคลื่อนที่เป็นวงกลมของ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;32&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c21.gif&quot; width=&quot;31&quot; /&gt;&amp;nbsp;ซึ่งจะมีความสัมพันธ์ระหว่างความเร่งในการเคลื่อนที่เชิงเส้นและเชิงมุม คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_1.gif&quot; width=&quot;69&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;&quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;308&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td style=&quot;&quot; width=&quot;298&quot;&gt;\r\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_2.gif&quot; width=&quot;160&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	หรือ&lt;/p&gt;\r\n&lt;div style=&quot;&quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;&quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;308&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;298&quot;&gt;\r\n				&lt;img height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_3.gif&quot; width=&quot;92&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	ถ้ารวมการคิดเช่นเดียวกันของ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;32&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_c21.gif&quot; width=&quot;31&quot; /&gt;ทุกชิ้นให้เป็นแผ่นกลมทั้งหมด จะได้&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;366&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;29&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_4.gif&quot; width=&quot;163&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				(5)&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	ทั้งนี้ความเร่งเชิงมุม&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;33&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_11.gif&quot; width=&quot;15&quot; /&gt;&amp;nbsp;เป็นค่าเดียวกันทั่วทั้งแผ่น ปริมาณ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_5.gif&quot; width=&quot;76&quot; /&gt;&amp;nbsp;คือค่าโมเมนต์ความเฉื่อย ของวัตถุ(Moment of Inertia) ใช้สัญลักษณ์&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_12.gif&quot; width=&quot;16&quot; /&gt;&amp;nbsp;ค่า&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;34&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_13.gif&quot; width=&quot;16&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เป็นทอร์กลัพธ์ที่กระทำต่อวัตถุ ซึ่งอาจจะมาจากแรงเดียวกระทำที่จุดใดจุดหนึ่งบนวัตถุแข็งก็ได้ เพราะการยึดกันภายในของแข็งจะกระจายผลกระทำไปทั่วแผ่น&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	สมการ (5) จึงอาจเขียนได้ใหม่เป็นดังนี้&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;366&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;229&quot;&gt;\r\n				&lt;img height=&quot;16&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_7.gif&quot; width=&quot;47&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;127&quot;&gt;\r\n				(6)&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	สมการ (6) เป็นสมการของการเคลื่อนที่แบบหมุนเทียบได้กับ&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;19&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_8.gif&quot; width=&quot;56&quot; /&gt;&amp;nbsp;สำหรับการเคลื่อนที่เชิงเส้นนั่นเอง โมเมนต์ของความเฉื่อย&lt;span class=&quot;midTxt1&quot;&gt;มีบทบาทสำหรับ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	การหมุนคล้ายมวล หรือ อาจกล่าวได้ว่าเป็นมวลของความเฉื่อยสำหรับการเคลื่อนที่เชิงเส้น&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	ทอร์กมีทิศพุ่งออกตั้งฉากกับกระดาษหรือตั้งฉากกับระนาบของ xy ในรูป 3 หรืออยู่ในทิศเดียวกับทิศของ&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq_15.gif&quot; width=&quot;16&quot; /&gt;จากสมการ (6) แสดงว่าทอร์ก&lt;span class=&quot;midTxt1&quot;&gt;ควรมีทิศ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	เดียวกับความเร่ง เชิงมุม เช่นเดียวกับแรงลัพธ์มีทิศเดียวกับความเร่ง ทั้งนี้ถ้าโมเมนต์ของความเฉื่อยเป็นปริมาณสเกลาร์โมเมนต์ความเฉื่อยของวัตถุอาจเป็น&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	ปริมาณที่ซับซ้อนได้ ถ้าการหมุนไม่เป็นการหมุนรอบแกนของความสมมาตรของวัตถุในบทเรียน เราจะจำกัดเฉพาะการหมุนที่รอบแกนสมมาตรของวัตถุเท่านั้น&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 70px; &quot;&gt;\r\n	สมการเชิงเวกเตอร์ของสมการ (6) จะเขียนได้ว่า&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;color: rgb(51, 51, 51); font-family: Tahoma; font-size: 14px; line-height: 24px; &quot; width=&quot;308&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;298&quot;&gt;\r\n				&lt;img height=&quot;19&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq2_9.gif&quot; width=&quot;50&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;font-size: 14px; line-height: 18px; font-family: Tahoma; color: rgb(51, 51, 51); padding: 0px 38px 10px; &quot;&gt;\r\n	โดยทอร์กและความเร่งเชิงมุมมีทิศเดียวกัน&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;color: rgb(0, 102, 0); font-weight: bold; line-height: 16px; &quot;&gt;3. โมเมนต์ความเฉื่อย&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	ค่าโมเมนต์ความเฉื่อยของวัตถุรูปต่าง ๆ รอบแกนสมมาตร สามารถคำนวณได้โดยวิธีแคลคูลัส คือ&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;line-height: 24px; &quot; width=&quot;308&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;298&quot;&gt;\r\n				&lt;img height=&quot;32&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_1.gif&quot; width=&quot;155&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p align=&quot;center&quot; class=&quot;head_content&quot; style=&quot;color: rgb(0, 102, 0); font-weight: bold; line-height: 16px; padding: 0px 34px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	โมเมนต์ความเฉื่อยของวัตถุรูปต่าง ๆ ที่น่ารู้ มีดังต่อไปนี้&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1 &quot; bordercolor=&quot;#FF3366&quot; style=&quot;line-height: 24px; width: 596px; &quot; width=&quot;800&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr bgcolor=&quot;#FFCCCC&quot;&gt;\r\n			&lt;td align=&quot;center&quot; class=&quot;heading&quot; style=&quot;font-weight: bold; line-height: 16px; width: 122px; &quot; width=&quot;200&quot;&gt;\r\n				รูปร่างวัตถุ&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; class=&quot;heading&quot; style=&quot;font-weight: bold; line-height: 16px; width: 165px; &quot; width=&quot;200&quot;&gt;\r\n				แกนหมุน&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; class=&quot;heading&quot; style=&quot;font-weight: bold; line-height: 16px; width: 152px; &quot; width=&quot;200&quot;&gt;\r\n				รูป&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; class=&quot;heading&quot; style=&quot;font-weight: bold; line-height: 16px; width: 129px; &quot; width=&quot;200&quot;&gt;\r\n				โมเมนต์ความเฉื่อย I&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 122px; &quot;&gt;\r\n				ทรงกลมตัน มวล m รัศมี R&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 165px; &quot;&gt;\r\n				รอบแกนผ่านจุดศูนย์กลาง&lt;/td&gt;\r\n			&lt;td style=&quot;width: 152px; text-align: center; &quot;&gt;\r\n				&lt;img height=&quot;81&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/content03_pic1.gif&quot; width=&quot;108&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 129px; &quot;&gt;\r\n				&lt;img height=&quot;41&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_2.gif&quot; width=&quot;72&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 122px; &quot;&gt;\r\n				ทรงกลมกลวง มวล m รัศมี R&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 165px; &quot;&gt;\r\n				รอบแกนผ่านจุดศูนย์กลาง&lt;/td&gt;\r\n			&lt;td style=&quot;width: 152px; text-align: center; &quot;&gt;\r\n				&lt;img height=&quot;88&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/content03_pic2.gif&quot; width=&quot;106&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 129px; &quot;&gt;\r\n				&lt;img height=&quot;42&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_3.gif&quot; width=&quot;72&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 122px; &quot;&gt;\r\n				ทรงกระบอกตัน มวล m รัศมี R&amp;nbsp;ยาว L&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 165px; &quot;&gt;\r\n				รอบแกนของทรงกระบอก&lt;/td&gt;\r\n			&lt;td style=&quot;width: 152px; text-align: center; &quot;&gt;\r\n				&lt;img height=&quot;84&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/content03_pic3.gif&quot; width=&quot;130&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 129px; &quot;&gt;\r\n				&lt;img height=&quot;42&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_4.gif&quot; width=&quot;75&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 122px; &quot;&gt;\r\n				แผ่นกลมบาง มวล m รัศมี R&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 165px; &quot;&gt;\r\n				รอบแกนผ่านศูนย์กลาง\r\n				&lt;p&gt;\r\n					ตั้งฉากกับแผ่น&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td style=&quot;width: 152px; text-align: center; &quot;&gt;\r\n				&lt;img height=&quot;82&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/content03_pic4.gif&quot; width=&quot;109&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 129px; &quot;&gt;\r\n				&lt;img height=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_5.gif&quot; width=&quot;73&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 122px; &quot;&gt;\r\n				แผ่นกลมบาง มวล m รัศมี R&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 165px; &quot;&gt;\r\n				รอบแกนผ่านศูนย์กลาง\r\n				&lt;p&gt;\r\n					บนระนาบของแผ่น&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td style=&quot;width: 152px; text-align: center; &quot;&gt;\r\n				&lt;img height=&quot;79&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/content03_pic5.gif&quot; width=&quot;95&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 129px; &quot;&gt;\r\n				&lt;img height=&quot;41&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_6.gif&quot; width=&quot;73&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 122px; &quot;&gt;\r\n				แท่งวัตถุเล็ก มวล m ยาว L&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 165px; &quot;&gt;\r\n				รอบแกนผ่านศูนย์กลางมวล\r\n				&lt;p&gt;\r\n					ตั้งฉากกับแท่ง&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td style=&quot;width: 152px; text-align: center; &quot;&gt;\r\n				&lt;img height=&quot;79&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/content03_pic6.gif&quot; width=&quot;121&quot; /&gt;&lt;/td&gt;\r\n			&lt;td align=&quot;center&quot; style=&quot;width: 129px; &quot;&gt;\r\n				&lt;img height=&quot;42&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_7.gif&quot; width=&quot;74&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	การหมุนของวัตถุทั้งหมดในตารางข้างบนเป็นการหมุนรอบแกนผ่านจุดศูนย์กลางมวลและเป็นแกนสมมาตรของวัตถุ มีหลักที่สามารถพิสูจน์ได้อยู่ว่า&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	ถ้าเลื่อนแกนหมุนไปเป็นระยะ&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;15&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_9.gif&quot; width=&quot;17&quot; /&gt;ให้ขนานแกนสมมาตรเดิม โมเมนต์ความเฉื่อยจะเพิ่มขึ้น&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_8.gif&quot; width=&quot;32&quot; /&gt;เท่ากับ(ต้องนำค่า&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq3_8.gif&quot; width=&quot;32&quot; /&gt;มาบวกค่าในตาราง)&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;color: rgb(0, 102, 0); font-weight: bold; line-height: 16px; &quot;&gt;4. พลังงานจลน์ของการหมุน&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1 &quot; bordercolor=&quot;#FF3366&quot; style=&quot;line-height: 24px; &quot; width=&quot;316&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;249&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/content04_pic1.gif&quot; width=&quot;262&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	วัตถุแข็งเกร็งที่เคลื่อนที่แบบหมุนรอบแกนนิ่ง จะมีพลังงานจลน์ ซึ่งพลังงานจลน์ทั้งหมดจะเป็นผลรวมของพลังงานจลน์ของแต่ละอนุภาค&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	จากรูปที่ 4 วัตถุแข็งเกร็งกำลังหมุนรอบแกน z อย่างสม่ำเสมอ โดยมวล m ใด ๆ มีความเร็วในการหมุนเป็น v และระยะตั้งฉากจากแกนนิ่ง&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	ถึงตำแหน่งของมวลดังกล่าว คือ r เพื่อความสะดวกเราจะใช้ i เป็นดัชนีของมวล m ใด ๆ ซึ่งสามารถหาค่าพลังงานจลน์รวมของวัตถุที่กำลังหมุน ได้เป็น&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;line-height: 24px; &quot; width=&quot;366&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;366&quot;&gt;\r\n				&lt;img height=&quot;45&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_1.gif&quot; width=&quot;312&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	เนื่องจากแต่ละมวลย่อยต่างก็หมุนไปกับวัตถุ นั่นคือ ทุกมวลย่อมมีการเคลื่อนที่ในแนววงกลมด้วยความเร็วเชิงมุมเท่ากับความเร็วเชิงมุมของวัตถุ&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	โดยใช้ความสัมพันธ์&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_2.gif&quot; width=&quot;45&quot; /&gt;&amp;nbsp;วัตถุประกอบด้วยมวลย่อย ๆ จะได้&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_3.gif&quot; width=&quot;53&quot; /&gt;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;17&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_4.gif&quot; width=&quot;56&quot; /&gt;ดังนั้นจะเขียน&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_5.gif&quot; width=&quot;20&quot; /&gt;ได้ใหม่เป็น&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;line-height: 24px; &quot; width=&quot;308&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;298&quot;&gt;\r\n				&lt;img height=&quot;46&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_6.gif&quot; width=&quot;128&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;298&quot;&gt;\r\n				&lt;img height=&quot;48&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_7.gif&quot; width=&quot;139&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1 &quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	นั่นคือ&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;line-height: 24px; &quot; width=&quot;308&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;298&quot;&gt;\r\n				&lt;img height=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_8.gif&quot; width=&quot;77&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	เมื่อ&amp;nbsp;&lt;img align=&quot;middle&quot; height=&quot;49&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_9.gif&quot; width=&quot;97&quot; /&gt;คือ โมเมนต์ความเฉื่อย หน่วยของพลังงานจลน์ของการหมุน คือ จูล&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	โปรดสังเกตว่ารูปแบบของพลังงานจลน์ของการหมุนมีลักษณะเดียวกับพลังงานจลน์ของการเลื่อนตำแหน่ง คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;41&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_10.gif&quot; width=&quot;76&quot; /&gt;(หมุน) เทียบกับ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq4_11.gif&quot; width=&quot;76&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	(เลื่อน) ซึ่งอยู่ในรูปแบบเดียวกัน และสำหรับการเคลื่อนที่เช่นการกลิ้ง พลังงานจลน์ทั้งหมดจะประกอบด้วยพลังงานจลน์ของการเลื่อนตำแหน่ง&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	ของจุดศูนย์กลางมวลและพลังงานจลน์ของการหมุนรอบจุดศูนย์กลางมวล&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;color: rgb(0, 102, 0); font-weight: bold; line-height: 16px; &quot;&gt;5. โมเมนตัมเชิงมุมและอัตราการเปลี่ยนโมเมนตัมเชิงมุม&lt;/span&gt;&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	หลักสมการสำคัญของการหมุน เปรียบเสมือนกฎของนิวตันสำหรับการหมุน คือ&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq5_1.gif&quot; width=&quot;56&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	จากการคิดว่า&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq5_2.gif&quot; width=&quot;58&quot; /&gt;จะได้&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;41&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq5_3.gif&quot; width=&quot;65&quot; /&gt;ซึ่งมีค่าเท่ากับ&lt;img align=&quot;absmiddle&quot; height=&quot;41&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq5_4.gif&quot; width=&quot;47&quot; /&gt;&amp;nbsp;อันจะมีความหมายเหมือนกับ อัตราการเปลี่ยนของปริมาณ&amp;nbsp;&lt;img align=&quot;absbottum&quot; height=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq5_5.gif&quot; width=&quot;51&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	ปริมาณ&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq5_8.gif&quot; width=&quot;16&quot; /&gt;คือ โมเมนตัมเชิงมุม หรือโมเมนตัมในเชิงการหมุน เมื่อเทียบกับโมเมนตัมธรรมดา&amp;nbsp;&lt;img align=&quot;absbottom&quot; height=&quot;16&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq5_6.gif&quot; width=&quot;28&quot; /&gt;จะเห็นว่ามีลักษณะคล้ายคลึงกัน และอาจจะเขียนสมการใหม่&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	ในทำนองเดียวกับแรงเท่ากับอัตราการเปลี่ยนโมเมนตัม คือ&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#FF3366&quot; style=&quot;line-height: 24px; &quot; width=&quot;236&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;center&quot; width=&quot;226&quot;&gt;\r\n				&lt;img height=&quot;47&quot; src=&quot;http://www.obeclms.com/lesson/06_RotationMotion/images/eq5_7.gif&quot; width=&quot;60&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	สมการนี้เป็นสมการที่เป็นจริงทั่วไป โดยที่ทิศของทอร์กอาจจะเปลี่ยนแปลงและไม่จำเป็นต้องเป็นทิศเดียวกับแกนหมุนแต่ทิศของการเปลี่ยนแปลง&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	โมเมนตัมเชิงมุมจะเป็นทิศเดียวกับทอร์กเสมอ ซึ่งเป็นไปตามการบังคับของสมการ การส่ายแบบควงของลูกข่างเมื่อมีทอร์กกระทำ เป็นไปตามสมการนี้ทุกประการ&lt;/p&gt;\r\n&lt;p class=&quot;indent&quot; style=&quot;padding: 0px 70px; &quot;&gt;\r\n	นอกจากนี้ยังบอกอีกว่า หากไม่มีทอร์กจากภายนอกมากระทำต่อระบบ โมเมนตัมเชิงมุมของระบบจะไม่มีการเปลี่ยนแปลง&lt;span class=&quot;midTxt1&quot;&gt;หมายถึงไม่เปลี่ยนทั้งขนาด&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	และทิศของโมเมนตัมเชิงมุม กรณีการหมุนตัวของนักสเกตน้ำแข็ง เมื่อกางแขนแล้วหดแขนซึ่งมีการเปลี่ยนแปลงโมเมนต์ความเฉื่อยในทางลดลง&lt;/p&gt;\r\n&lt;p class=&quot;midTxt1&quot; style=&quot;padding: 0px 38px 10px; &quot;&gt;\r\n	ความเร็วเชิงมุมจะเพิ่มขึ้นเพราะโมเมนตัมเชิงมุมจะต้องคงที่&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p class=&quot;head_content&quot; style=&quo', '1344603681', '1344603681');
INSERT INTO `lesson` VALUES ('14', '10', '7', 'สภาพสมดุลและสภาพยืดหยุ่น', '&lt;strong class=&quot;style3&quot; style=&quot;font-size: 16px; color: rgb(153, 0, 0); font-family: \'MS Sans Serif\'; &quot;&gt;&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 22px; &quot;&gt;&lt;span style=&quot;font-family: \'times new roman\', times, serif; &quot;&gt;สภาพสมดุล&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\nหลักการของสมดุล&lt;/strong&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;สภาพสมดุล&lt;/strong&gt;&lt;span style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; &quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; &quot;&gt;คือ วัตถุที่อยู่นิ่งในกรอบอ้างอิงเฉื่อย ในที่นี้เสมือนว่าตัวเราที่อยู่บนผิวโลกอยู่ในกรอบอ้างอิงเฉื่อยและวัตถุที่สังเกตเป็นวัตถุแข็งเกร็ง คือ&amp;nbsp;ไม่มีการเปลี่ยนรูปเมื่อมีแรงมากระทำ&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; &quot;&gt;วัตถุจะอยู่ในสภาพสมดุลได้&amp;nbsp;ต้องอยู่ในเงื่อนไข 2 ข้อดังนี้&lt;/span&gt;&lt;/span&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;3&quot; width=&quot;82%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#339900&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;65&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_01.gif&quot; width=&quot;104&quot; /&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;เมื่อ&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_02.gif&quot; width=&quot;31&quot; /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;แทนแรงต่างๆที่กระทำต่อวัตถุ&lt;/span&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;53&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_03.gif&quot; width=&quot;57&quot; /&gt;คือ ผลรวมของแรงต่างๆที่กระทำต่อวัตถุ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;55&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_04.gif&quot; width=&quot;224&quot; /&gt;&lt;/span&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;ul style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	1.&amp;nbsp;แรงลัพธ์เป็นศูนย์ (ไม่เคลื่อนที่)&amp;nbsp;เขียนเป็นสมการได้เป็น&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	2.&amp;nbsp;ทอร์กลัพธ์หรือโมเมนต์ลัพธ์เป็นศูนย์ (ไม่หมุน)\r\n&lt;/ul&gt;\r\n&lt;table align=&quot;center&quot; cellpadding=&quot;0&quot; cellspacing=&quot;3&quot; width=&quot;80%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#339900&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;68&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_05.gif&quot; width=&quot;104&quot; /&gt;หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;65&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_06.gif&quot; width=&quot;110&quot; /&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;เมื่อ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;32&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_07.gif&quot; width=&quot;35&quot; /&gt;แทนโมเมนต์ของแรงต่างๆที่กระทำต่อวัตถุ&lt;/span&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;33&quot; hspace=&quot;15&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_08.gif&quot; width=&quot;37&quot; /&gt;แทนโมเมนต์ของแรงรอบแกนใดแกนหนึ่ง&lt;/span&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;ul style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n&lt;/ul&gt;\r\n&lt;br /&gt;\r\n&lt;strong class=&quot;style3&quot; style=&quot;font-size: 16px; color: rgb(153, 0, 0); font-family: \'MS Sans Serif\'; &quot;&gt;เงื่อนไขของสมดุล&lt;/strong&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;1.&amp;nbsp;&lt;u&gt;กรณีที่มีสองแรงกระทำ&lt;/u&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;97%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td width=&quot;23%&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;img align=&quot;absmiddle&quot; height=&quot;129&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/pc2_01.gif&quot; width=&quot;88&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td width=&quot;77%&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong class=&quot;style7&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;ตัวอย่างเช่น&lt;/strong&gt;&amp;nbsp;&lt;br /&gt;\r\n				กรณีที่กล่องอยู่นิ่งบนพื้นจะมีแรงที่กระทำกับกล่อง&amp;nbsp;คือ&amp;nbsp;&lt;br /&gt;\r\n				แรงโน้มถ่วงของโลก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_w.gif&quot; width=&quot;38&quot; /&gt;&amp;nbsp;กับแรงที่พื้นดันกล่อง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_n.gif&quot; width=&quot;35&quot; /&gt;&lt;br /&gt;\r\n				จากกฏข้อที่ 1 ของนิวตันแรงลัพธ์เป็นศูนย์ ได้&lt;/span&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;table width=&quot;70%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bgcolor=&quot;#339900&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img align=&quot;absmiddle&quot; height=&quot;34&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_01.gif&quot; width=&quot;112&quot; /&gt;หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;33&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_02.gif&quot; width=&quot;99&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2.&amp;nbsp;&lt;u&gt;กรณีที่มีสามแรงกระทำขนานกัน&lt;/u&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;98%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td width=&quot;32%&quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;img height=&quot;105&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/pc2_02.gif&quot; width=&quot;172&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n			&lt;td width=&quot;68%&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style7&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;ตัวอย่างเช่น&lt;/span&gt;&amp;nbsp;&lt;br /&gt;\r\n				กรณีที่ไม้คานพาดอยู่บนหัวเสา 2 ต้น&amp;nbsp;จะมีแรงที่กระทำกับคาน&amp;nbsp;คือ&amp;nbsp;แรงจากน้ำหนักของคาน&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con1_w.gif&quot; width=&quot;38&quot; /&gt;ซึ่งเกิดจากแรงโน้มถ่วงของโลกกับแรงที่หัวเสาทั้งสองดันคาน (&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_n1.gif&quot; width=&quot;25&quot; /&gt;และ&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_n2.gif&quot; width=&quot;23&quot; /&gt;) จากกฏข้อที่ 1 ของนิวตันแรงลัพธ์เป็นศูนย์ ดังนั้น&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;3&quot; width=&quot;30%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bgcolor=&quot;#339900&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img align=&quot;absmiddle&quot; height=&quot;39&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_03.gif&quot; width=&quot;175&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;3.&amp;nbsp;&lt;u&gt;กรณีที่มีสามแรงไม่ขนานกัน&lt;/u&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;100%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;img height=&quot;173&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/pc2_03.gif&quot; width=&quot;210&quot; /&gt;&lt;/td&gt;\r\n			&lt;td width=&quot;66%&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span class=&quot;style7&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;ตัวอย่างเช่น&lt;/span&gt;&lt;br /&gt;\r\n				การขึงวัตถุด้วยเชือกจากมุมต่างๆ 3 มุมดังรูป โดยที่&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_f1.gif&quot; width=&quot;21&quot; /&gt;,&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_f2.gif&quot; width=&quot;25&quot; /&gt;และ&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_f3.gif&quot; width=&quot;23&quot; /&gt;คือแรงที่กระทำต่อวัตถุ จากกฏข้อที่ 1 ของนิวตันแรงลัพธ์เป็นศูนย์ ได้&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;3&quot; width=&quot;30%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bgcolor=&quot;#339900&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img align=&quot;absmiddle&quot; height=&quot;38&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_04.gif&quot; width=&quot;161&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;100%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;img height=&quot;246&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/pc2_04.gif&quot; width=&quot;276&quot; /&gt;&lt;/td&gt;\r\n			&lt;td width=&quot;60%&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;เมื่อ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; hspace=&quot;3&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_fix.gif&quot; width=&quot;46&quot; /&gt;&amp;nbsp;และ&lt;img align=&quot;absmiddle&quot; height=&quot;33&quot; hspace=&quot;3&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_fiy.gif&quot; width=&quot;46&quot; /&gt;แทนแรงองค์ประกอบในแนวแกน x และ y ตามลำดับ เมื่อวัตถุอยู่นิ่งผลรวมของแรงองค์ประกอบทั้งในแนวแกน x และแกน y ต้องเป็นศูนย์ นั่นคือ&lt;/span&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;3&quot; width=&quot;96%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bgcolor=&quot;#339900&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img align=&quot;absmiddle&quot; height=&quot;71&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_05.gif&quot; width=&quot;140&quot; /&gt;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;74&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_06.gif&quot; width=&quot;127&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;ul style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	เมื่อมีแรงสามแรงกระทำต่อวัตถุเดียวกัน&amp;nbsp;โดยที่แรงทั้งสามไม่ขนานกัน เราจะเป็นต้องแตกแรงองค์ประกอบเพื่อหาแรงลัพธ์โดยจะได้แรงในแนวแกน x และ y ดังรูป\r\n&lt;/ul&gt;\r\n&lt;strong class=&quot;style3&quot; style=&quot;font-size: 16px; color: rgb(153, 0, 0); font-family: \'MS Sans Serif\'; &quot;&gt;โมเมนต์ของแรง&lt;/strong&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;span class=&quot;style7&quot; style=&quot;font-family: \'MS Sans Serif\'; color: rgb(0, 51, 102); font-size: 16px; font-weight: bold; &quot;&gt;การหมุนของวัตถุจะขึ้นกับโมเมนต์ของแรงหรือทอร์ก&amp;nbsp;(Torque)&lt;/span&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;145&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/pc3_01.gif&quot; width=&quot;240&quot; /&gt;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#339933&quot; cellpadding=&quot;0&quot; cellspacing=&quot;3&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; width=&quot;82%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;strong&gt;โดยนิยาม ทอร์ก&amp;nbsp;&lt;/strong&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;39&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_01.gif&quot; width=&quot;103&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				เมื่อ&amp;nbsp;&lt;span class=&quot;style7&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_f.gif&quot; width=&quot;20&quot; /&gt;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;คือ แรงที่กระทำต่อวัตถุ&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; hspace=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_r.gif&quot; width=&quot;16&quot; /&gt;คือ เวกเตอร์จากจุดหมุนถึงจุดที่แรง&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; hspace=&quot;3&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_f.gif&quot; width=&quot;20&quot; /&gt;กระทำ&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#339933&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; width=&quot;82%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;strong&gt;โดยที่ขนาดของทอร์ก (ขนาดของโมเมนตัม, M)&lt;/strong&gt;&amp;nbsp;มีค่าเท่ากับ&amp;nbsp;ผลคูณระหว่างขนาดของแรง (F) กับระยะทางจากแกนหมุนไปตั้งฉากแนวแรง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_dsin.gif&quot; width=&quot;103&quot; /&gt;&lt;br /&gt;\r\n				นั่นคือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_02.gif&quot; width=&quot;150&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;ในสภาพสมดุล&amp;nbsp;คือ&amp;nbsp;ไม่มีการหมุน โมเมนต์ลัพธ์ต้องเป็นศูนย์ เราสามารถเขียนความสัมพันธ์เพื่อให้ง่ายต่อการคำนวณได้เป็น&lt;/span&gt;&lt;span class=&quot;style7&quot; style=&quot;font-family: \'MS Sans Serif\'; color: rgb(0, 51, 102); font-size: 16px; font-weight: bold; &quot;&gt;โมเมนต์ทวนเข็มนาฬิกา = โมเมนต์ตามเข็มนาฬิกา&lt;/span&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;br /&gt;\r\n&lt;strong style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;ตัวอย่างเช่น&lt;/strong&gt;&lt;span style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;&amp;nbsp;ตุ้มน้ำหนัก 3 ก้อนแขวนไว้กับราวไม้&amp;nbsp;ดังรูป&lt;/span&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;img height=&quot;135&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/pc3_02.gif&quot; width=&quot;455&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	จาก โมเมนต์ทวนเข็มนาฬิกา = โมเมนต์ตามเข็มนาฬิกา จะได้&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#339933&quot; cellpadding=&quot;0&quot; cellspacing=&quot;3&quot; width=&quot;80%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_03.gif&quot; width=&quot;159&quot; /&gt;&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;เมื่อ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;29&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_wi.gif&quot; width=&quot;29&quot; /&gt;&amp;nbsp;คือ แรงจากน้ำหนักของลูกตุ้ม&lt;/span&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con2_di.gif&quot; width=&quot;28&quot; /&gt;&amp;nbsp;&amp;nbsp;คือ ระยะทางจากแกนหมุนไปตั้งฉากแนวแรง&lt;/span&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;ul style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n&lt;/ul&gt;\r\n&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;ในกรณีที่มีแรงสองแรงขนาดเท่ากัน แนวแรงขนานกัน แต่ทิศทางตรงข้ามกันกระทำต่อวัตถุ เราจะเรียกแรงคู่นี้ว่า&lt;/span&gt;&lt;strong class=&quot;style7&quot; style=&quot;font-family: \'MS Sans Serif\'; color: rgb(0, 51, 102); font-size: 16px; &quot;&gt;&amp;nbsp;แรงคู่ควบ&lt;/strong&gt;&lt;span style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; /&gt;\r\n&lt;span style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;ดังรูป&lt;/span&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	&lt;img height=&quot;121&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/pc3_03.gif&quot; width=&quot;217&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_04.gif&quot; width=&quot;23&quot; /&gt;และ&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_05.gif&quot; width=&quot;25&quot; /&gt;&amp;nbsp;คือ แรงที่มีขนาดเท่ากันแนวแรงขนานกัน กระทำ ณ จุด A และ B ตามลำดับ&lt;br /&gt;\r\n	&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_l1.gif&quot; width=&quot;21&quot; /&gt;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_l2.gif&quot; width=&quot;22&quot; /&gt;&amp;nbsp;คือ ระยะทางจากแกนหมุนไปตั้งฉากแนวแรง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_04.gif&quot; width=&quot;23&quot; /&gt;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_05.gif&quot; width=&quot;25&quot; /&gt;&amp;nbsp;ตามลำดับ&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	โมเมนต์ของแรงคู่ควบ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_04.gif&quot; width=&quot;23&quot; /&gt;และ&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_05.gif&quot; width=&quot;25&quot; /&gt;รอบแกนหมุน&amp;nbsp;&lt;strong&gt;O&amp;nbsp;&lt;/strong&gt;เป็นโมเมนต์ตามเข็มนาฬิกา&amp;nbsp;และเนื่องจากขนาดของแรงคู่ควบเท่ากัน&lt;br /&gt;\r\n	(&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_04.gif&quot; width=&quot;23&quot; /&gt;&amp;nbsp;=&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_05.gif&quot; width=&quot;25&quot; /&gt;) ดังนั้น&amp;nbsp;ถ้าให้ F เป็นขนาดของแรงคู่ควบแต่ละแรง&amp;nbsp;จะหาผลรวมของโมเมนต์ได้เป็น&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#339900&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot; width=&quot;50%&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;img height=&quot;72&quot; hspace=&quot;150&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_06.gif&quot; vspace=&quot;10&quot; width=&quot;135&quot; /&gt;&lt;br /&gt;\r\n				ดังนั้น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;29&quot; hspace=&quot;115&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con3_07.gif&quot; width=&quot;99&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#99CC99&quot;&gt;\r\n				&lt;div align=&quot;left&quot;&gt;\r\n					เมื่อ&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;l&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;เป็นระยะทางตั้งฉากระหว่างแนวแรงทั้งสอง&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p class=&quot;style6&quot; style=&quot;font-family: \'MS Sans Serif\'; font-weight: bold; font-size: 16px; color: rgb(0, 102, 0); &quot;&gt;\r\n	ดังนั้น สรุปได้ว่าโมเมนต์ของแรงคู่ควบใดๆมีขนาดเท่ากับผลคูณของขนาดของแรงใดแรงหนึ่ง&lt;br /&gt;\r\n	กับระยะทางตั้งฉากระหว่างแนวแรงทั้งสอง สังเกตว่า แรงลัพธ์ของแรงคู่ควบที่กระทำต่อวัตถุดังกล่าวเป็นศูนย์ ทำให้ไม่มีการเลื่อนตำแหน่งจุดศูนย์กลางของมวลของวัตถุที่เป็นอิสระ ในขณะที่ผลรวมของขนาดของทอร์ก&lt;br /&gt;\r\n	หรือขนาดของโมเมนต์ไม่เป็นศูนย์ ซึ่งส่งผลให้วัตถุเกิดการหมุนขึ้น&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	&lt;strong class=&quot;style3&quot; style=&quot;color: rgb(153, 0, 0); &quot;&gt;วัตถุพอดีล้มและตำแหน่งของแรง N&lt;/strong&gt;&lt;/p&gt;\r\n&lt;blockquote style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	&lt;p&gt;\r\n		&lt;u&gt;วัตถุพอดีล้มบนพื้นราบ&lt;/u&gt;&lt;br /&gt;\r\n		&lt;img height=&quot;178&quot; hspace=&quot;60&quot; src=&quot;http://www.obeclms.com/lesson/07_StaticEquilibrium/images/con7_01.gif&quot; width=&quot;241&quot; /&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		N = แรงปฏิกิริยาแนวตั้งฉาก (พื้นดันวัตถุ)&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'MS Sans Serif\'; font-size: 16px; &quot;&gt;\r\n	จากรูป ถ้าแรง F ทำให้วัตถุพอดีล้ม ตำแหน่งแรง N จะเลื่อนไปอยู่ที่ขอบฐานของวัตถุพอดี&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	----------------------------------------------------------------------------------------------------------------&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;', '1344604289', '1344604289');
INSERT INTO `lesson` VALUES ('15', '10', '8', 'ของไหล', '&lt;blockquote&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFFFF&quot; border=&quot;0&quot; cellpadding=&quot;5&quot; cellspacing=&quot;10&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; width: 560px; &quot; width=&quot;1000&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#999999&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 526px; &quot;&gt;\r\n				&lt;div class=&quot;style6&quot; style=&quot;font-size: 16px; color: rgb(153, 0, 0); font-weight: bold; text-align: center; &quot;&gt;\r\n					&lt;strong&gt;::. ความหนาแน่น .::&lt;/strong&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 526px; &quot;&gt;\r\n				&lt;p align=&quot;left&quot;&gt;\r\n					&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;ความหนาแน่นของวัตถุ&lt;/span&gt;(ใช้สัญลักษณ์&lt;img align=&quot;absmiddle&quot; alt=&quot;โรห์&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pcon01.gif&quot; width=&quot;20&quot; /&gt;อ่านว่า โรห์ rho)&amp;nbsp;ที่มีสสารองค์ประกอบแบบสม่ำเสมอ&amp;nbsp;คือ&amp;nbsp;อัตราส่วนระหว่างค่ามวลต่อหน่วยปริมาตร&lt;/p&gt;\r\n				&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; width=&quot;47%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bordercolor=&quot;#FFFFFF&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;strong&gt;สูตรความสัมพันธ์&lt;/strong&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;64&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c1_01.gif&quot; width=&quot;96&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot;&gt;\r\n								เมื่อ&amp;nbsp;&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;m&lt;/strong&gt;&amp;nbsp;คือมวลของสาร (กิโลกรัม),&amp;nbsp;&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;V&lt;/strong&gt;&amp;nbsp;คือปริมาตรของสาร (ลูกบาศก์เมตร)&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n				&lt;p align=&quot;left&quot;&gt;\r\n					&lt;br /&gt;\r\n					&lt;strong class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;ความหนาแน่นสัมพัทธ์(ความถ่วงจำเพาะ)&lt;/strong&gt;&amp;nbsp;คือ&amp;nbsp;อัตราส่วนระหว่างความหนาแน่นของสารนั้นกับความหนาแน่นของน้ำที่อุณหภูมิ 4 องศาเซลเซียส&amp;nbsp;ซึ่งมีค่าเป็น 1000&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c1_kgm.gif&quot; width=&quot;45&quot; /&gt;&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;color: rgb(0, 0, 0); font-family: Tahoma; font-size: 14px; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: Tahoma; font-size: 14px; text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFFFF&quot; border=&quot;0&quot; cellpadding=&quot;5&quot; cellspacing=&quot;10&quot; style=&quot;font-family: \'Times New Roman\'; font-size: medium; width: 732px; &quot; width=&quot;1000&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 698px; &quot;&gt;\r\n				&lt;strong style=&quot;color: rgb(153, 0, 0); font-size: 16px; text-align: -webkit-center; background-color: rgb(153, 153, 153); &quot;&gt;::. ความดันในของเหลว .::&lt;/strong&gt;&lt;br /&gt;\r\n				&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;100%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td class=&quot;style16&quot; colspan=&quot;2&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;\r\n								--&amp;gt; ความดันของของไหล&amp;nbsp;คือ&amp;nbsp;อัตราส่วนของแรงที่กระทำต่อวัตถุต่อหน่วยพื้นที่ที่สัมผัสกับของไหล&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td width=&quot;361&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img height=&quot;196&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pcon2-1.gif&quot; width=&quot;157&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n							&lt;td width=&quot;609&quot;&gt;\r\n								&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; style=&quot;width: 313px; &quot; width=&quot;97%&quot;&gt;\r\n									&lt;tbody&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 303px; &quot;&gt;\r\n												&lt;div align=&quot;center&quot;&gt;\r\n													&lt;strong&gt;สูตรความสัมพันธ์&amp;nbsp;&lt;/strong&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;68&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_01.gif&quot; width=&quot;99&quot; /&gt;&lt;/div&gt;\r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 303px; &quot;&gt;\r\n												เมื่อ&amp;nbsp;&lt;span class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; font-weight: bold; &quot;&gt;P&lt;/span&gt;&amp;nbsp;คือ ความดัน มีหน่วยเป็น&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_02.gif&quot; width=&quot;40&quot; /&gt;&amp;nbsp;หรือพาสคัล&amp;nbsp;(pascal:Pa)&lt;strong&gt;&amp;nbsp;&lt;br /&gt;\r\n												&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;F&lt;/span&gt;&lt;/strong&gt;&amp;nbsp;คือ&amp;nbsp;แรงที่ของเหลวกระทำต่อวัตถุ (นิวตัน)&amp;nbsp;&lt;br /&gt;\r\n												&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;A&lt;/strong&gt;&amp;nbsp;คือ&amp;nbsp;พื้นที่(ตารางเมตร) และเป็นพื้นที่ราบ (Flat area)&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n									&lt;/tbody&gt;\r\n								&lt;/table&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td colspan=&quot;2&quot;&gt;\r\n								&lt;br /&gt;\r\n								&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;--&amp;gt; ความดันในของเหลวจะแปรผันตรงกับความลึกและความหนาแน่นของของเหลว&lt;/span&gt;&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img height=&quot;247&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pcon2-2.gif&quot; width=&quot;175&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n							&lt;td&gt;\r\n								&lt;p align=&quot;left&quot;&gt;\r\n									&lt;br /&gt;\r\n									หากพิจารณาของเหลวที่มีความหนาแน่น&lt;img align=&quot;absmiddle&quot; alt=&quot;โรห์&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pcon01.gif&quot; width=&quot;25&quot; /&gt;อยู่นิ่งในภาชนะเปิดสู่บรรยากาศ&lt;br /&gt;\r\n									&lt;br /&gt;\r\n									W&amp;nbsp;เป็นน้ำหนักของของเหลวบนพื้นที่ A (หน้าตัดของทรงกระบอก) ดังนั้น&lt;/p&gt;\r\n								&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;250&quot;&gt;\r\n									&lt;tbody&gt;\r\n										&lt;tr&gt;\r\n											&lt;td&gt;\r\n												&lt;div align=&quot;center&quot;&gt;\r\n													&lt;img height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2-03.gif&quot; width=&quot;207&quot; /&gt;&lt;/div&gt;\r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n									&lt;/tbody&gt;\r\n								&lt;/table&gt;\r\n								&lt;br /&gt;\r\n								ให้ความดันบรรยากาศ คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;28&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_04w.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;เนื่องจากของเหลวอยู่ในสมดุล หรือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;36&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_05.gif&quot; width=&quot;93&quot; /&gt;&lt;br /&gt;\r\n								&lt;br /&gt;\r\n								ดังนั้นที่ก้นแก้ว\r\n								&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;200&quot;&gt;\r\n									&lt;tbody&gt;\r\n										&lt;tr&gt;\r\n											&lt;td&gt;\r\n												&lt;div align=&quot;center&quot;&gt;\r\n													&lt;img height=&quot;27&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_06.gif&quot; width=&quot;154&quot; /&gt;&lt;/div&gt;\r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n									&lt;/tbody&gt;\r\n								&lt;/table&gt;\r\n								&lt;br /&gt;\r\n								จะได้\r\n								&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;width: 342px; &quot; width=&quot;97%&quot;&gt;\r\n									&lt;tbody&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 326px; &quot;&gt;\r\n												&lt;div align=&quot;center&quot;&gt;\r\n													&lt;strong&gt;สูตรความดันสัมบูรณ์&amp;nbsp;&lt;/strong&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;39&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_09.gif&quot; width=&quot;153&quot; /&gt;&lt;/div&gt;\r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 326px; &quot;&gt;\r\n												&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_08.gif&quot; width=&quot;21&quot; /&gt;คือ ผลรวมของความดันบรรยากาศกับความดันเกจ&amp;nbsp;เรียกว่า&amp;nbsp;&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&lt;em&gt;ความดันสัมบูรณ์&lt;/em&gt;&lt;/span&gt;&amp;nbsp;(Absolute pressure)&lt;br /&gt;\r\n												&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_04.gif&quot; width=&quot;23&quot; /&gt;&amp;nbsp;คือ ความดันที่ผิวของเหลวเท่ากับความดันบรรยากาศ&amp;nbsp;&lt;br /&gt;\r\n												&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_07.gif&quot; width=&quot;41&quot; /&gt;&amp;nbsp;เป็นความดันเนื่องจากน้ำหนักของของเหลวที่ระดับความลึก h เรียกว่า&amp;nbsp;&lt;em class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;ความดันเกจ&lt;/em&gt;&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n									&lt;/tbody&gt;\r\n								&lt;/table&gt;\r\n								&lt;br /&gt;\r\n								&amp;nbsp;&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td colspan=&quot;2&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;span class=&quot;style15&quot; style=&quot;font-weight: bold; color: rgb(0, 102, 0); &quot;&gt;จากสูตร&amp;nbsp;สรุปได้ว่า&amp;nbsp;&lt;u&gt;ความดันในของเหลวชนิดเดียวกันที่ระดับความลึกเดียวกันมีค่าเท่ากัน โดยรูปทรงของภาชนะไม่มีผลต่อความดัน&lt;/u&gt;&lt;/span&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td colspan=&quot;2&quot;&gt;\r\n								&lt;br /&gt;\r\n								&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;--&amp;gt; แรงดันน้ำเหนือเขื่อน&lt;/span&gt;&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;br /&gt;\r\n									&lt;img height=&quot;196&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_dam.gif&quot; width=&quot;286&quot; /&gt;&lt;br /&gt;\r\n									&amp;nbsp;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n							&lt;td&gt;\r\n								จากรูป&amp;nbsp;&lt;strong&gt;แรงดันของน้ำเหนือเขื่อน&lt;/strong&gt;&amp;nbsp;คำนวณได้จาก&lt;br /&gt;\r\n								&lt;br /&gt;\r\n								&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;width: 286px; &quot; width=&quot;97%&quot;&gt;\r\n									&lt;tbody&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 270px; &quot;&gt;\r\n												&lt;div align=&quot;center&quot;&gt;\r\n													&lt;img align=&quot;absmiddle&quot; height=&quot;68&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_12.gif&quot; width=&quot;148&quot; /&gt;&lt;/div&gt;\r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 270px; &quot;&gt;\r\n												&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;F&lt;/strong&gt;&amp;nbsp;คือ &amp;nbsp;แรงดันเฉลี่ยของน้ำที่กระทำกับเขื่อน&lt;br /&gt;\r\n												&lt;img align=&quot;absmiddle&quot; alt=&quot;โรห์&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_13.gif&quot; width=&quot;24&quot; /&gt;คือ &amp;nbsp;ความหนาแน่นของน้ำ&lt;br /&gt;\r\n												&amp;nbsp;&lt;strong&gt;&amp;nbsp;&lt;span class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;l&amp;nbsp;&lt;/span&gt;&amp;nbsp;&lt;/strong&gt;&amp;nbsp;คือ &amp;nbsp;ความยาวของตัวเขื่อน&lt;br /&gt;\r\n												&amp;nbsp;&amp;nbsp;&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;h&lt;/strong&gt;&amp;nbsp;&amp;nbsp;คือ &amp;nbsp;ความสูงของระดับน้ำ&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n									&lt;/tbody&gt;\r\n								&lt;/table&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td colspan=&quot;2&quot;&gt;\r\n								&lt;br /&gt;\r\n								&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;--&amp;gt;หลอดแก้วรูปตัวยู&lt;/span&gt;&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img height=&quot;367&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_u.gif&quot; width=&quot;250&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n							&lt;td&gt;\r\n								ของเหลวสองชนิดมีความหนาแน่น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;25&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_14.gif&quot; width=&quot;23&quot; /&gt;&amp;nbsp;และ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_15.gif&quot; width=&quot;26&quot; /&gt;&amp;nbsp;ไม่ผสมกันและไม่ทำปฏิกิริยากัน ใส่เข้าไปในหลอดแก้วรูปตัวยู ดังรูป&lt;br /&gt;\r\n								&lt;br /&gt;\r\n								&lt;p align=&quot;left&quot;&gt;\r\n									ขาทั้งสองข้างจะเท่ากันหรือไม่ก็ตาม แต่ปลายทั้งสองต้องเปิดสู่อากาศเดียวกัน จะได้&lt;/p&gt;\r\n								&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;width: 258px; &quot; width=&quot;97%&quot;&gt;\r\n									&lt;tbody&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 242px; &quot;&gt;\r\n												&lt;div align=&quot;center&quot;&gt;\r\n													&lt;img height=&quot;45&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_16.gif&quot; width=&quot;131&quot; /&gt;&lt;/div&gt;\r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n									&lt;/tbody&gt;\r\n								&lt;/table&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td colspan=&quot;2&quot;&gt;\r\n								&lt;br /&gt;\r\n								&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;--&amp;gt; เครื่องมือวัดความดันของของไหล&lt;/span&gt;&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td colspan=&quot;2&quot;&gt;\r\n								&lt;ul&gt;\r\n									&lt;br /&gt;\r\n									&lt;li class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;\r\n										&lt;strong&gt;แมนอมิเตอร์&lt;/strong&gt;&lt;/li&gt;\r\n								&lt;/ul&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img height=&quot;326&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_gas.gif&quot; width=&quot;251&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n							&lt;td&gt;\r\n								แมนอมิเตอร์ เป็นเครื่องมือวัดความดันของของไหลที่มีลักษณะดังรูป ส่วนสำคัญคือ หลอดรูปตัวยูมีของเหลวซึ่งมีความหนาแน่น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; alt=&quot;โรห์&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pcon01.gif&quot; width=&quot;20&quot; /&gt;&amp;nbsp;บรรจุอยู่ คำนวณความดันได้จาก&lt;br /&gt;\r\n								&lt;br /&gt;\r\n								&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;width: 324px; &quot; width=&quot;97%&quot;&gt;\r\n									&lt;tbody&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 308px; &quot;&gt;\r\n												&lt;div align=&quot;center&quot;&gt;\r\n													&lt;img height=&quot;33&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_17.gif&quot; width=&quot;158&quot; /&gt;&lt;/div&gt;\r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n										&lt;tr&gt;\r\n											&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 308px; &quot;&gt;\r\n												&amp;nbsp;&lt;span class=&quot;style18&quot; style=&quot;font-size: 16px; font-weight: bold; &quot;&gt;&amp;nbsp;P&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;คือ ความดันแก๊สในถัง&lt;br /&gt;\r\n												&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;26&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_18.gif&quot; width=&quot;27&quot; /&gt;&amp;nbsp;&amp;nbsp;คือ ความดันบรรยากาศ&lt;br /&gt;\r\n												&lt;img align=&quot;absmiddle&quot; height=&quot;29&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_19.gif&quot; width=&quot;47&quot; /&gt;&amp;nbsp;คือ ความดันเกจของของเหลวสูง d&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n									&lt;/tbody&gt;\r\n								&lt;/table&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td colspan=&quot;2&quot;&gt;\r\n								&lt;ul&gt;\r\n									&lt;br /&gt;\r\n									&lt;li&gt;\r\n										&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;บารอมิเตอร์&lt;/span&gt;&lt;/li&gt;\r\n								&lt;/ul&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img height=&quot;242&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pic_con02-3.jpg&quot; vspace=&quot;10&quot; width=&quot;159&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n							&lt;td&gt;\r\n								&lt;p align=&quot;left&quot;&gt;\r\n									บารอมิเตอร์ เป็นเครื่องมือวัดความดันประเภทหนึ่งที่ใช้หลอดยาวปลายข้างหนึ่งปิด และปลายข้างที่เปิดคว่ำลงในอ่างปรอท&lt;br /&gt;\r\n									ความดัน 1 บรรยากาศ&amp;nbsp;เป็นความดันเนื่องจากน้ำหนักของลำปรอทที่สูง 760 มิลลิเมตร&lt;br /&gt;\r\n									&lt;br /&gt;\r\n									&lt;strong&gt;คำนวณความดันบรรยากาศได้จาก&lt;/strong&gt;&lt;br /&gt;\r\n									&lt;br /&gt;\r\n									&lt;img height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_10.gif&quot; width=&quot;460&quot; /&gt;&lt;br /&gt;\r\n									&lt;br /&gt;\r\n									&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_11.gif&quot; width=&quot;231&quot; /&gt;&lt;/p&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFFFF&quot; border=&quot;0&quot; cellpadding=&quot;5&quot; cellspacing=&quot;10&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; width: 664px; &quot; width=&quot;1000&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#999999&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 630px; &quot;&gt;\r\n				&lt;div align=&quot;center&quot;&gt;\r\n					&lt;span class=&quot;style6&quot; style=&quot;font-size: 16px; color: rgb(153, 0, 0); font-weight: bold; &quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;strong style=&quot;color: rgb(153, 0, 0); font-size: 16px; &quot;&gt;::.&lt;/strong&gt;&lt;strong style=&quot;color: rgb(153, 0, 0); font-size: 16px; &quot;&gt;&amp;nbsp;&lt;/strong&gt;&lt;span style=&quot;color: rgb(153, 0, 0); font-size: 16px; font-weight: bold; &quot;&gt;กฎของพาสคัลและเครื่องอัดไฮดรอลิก .::&lt;/span&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 630px; &quot;&gt;\r\n				&lt;strong class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;พาสคัล&lt;/strong&gt;&amp;nbsp;ได้ค้นพบว่า&amp;nbsp;การเปลี่ยนแปลงความดันที่กระทำต่อของไหลในภาชนะปิดจะมีการส่งผ่านแรงทั้งหมดไปยังทุกจุดของของไหลและผนังของภาชนะ&lt;br /&gt;\r\n				ด้วยหลักการนี้ทำให้เกิดการประยุกต์ใช้เครื่องผ่อนแรงที่เรียกว่า&amp;nbsp;&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&amp;quot;เครื่องอัดไฮดรอลิก&amp;quot;&lt;/span&gt;&amp;nbsp;ซึ่งประกอบด้วยกระบอกสูบและลูกสูบสองชุดที่มีขนาดต่างกัน ดังรูป&lt;br /&gt;\r\n				&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; style=&quot;width: 618px; &quot; width=&quot;100%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td style=&quot;width: 345px; &quot; width=&quot;44%&quot;&gt;\r\n								&lt;img height=&quot;252&quot; hspace=&quot;50&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pic_con03-1.jpg&quot; width=&quot;298&quot; /&gt;&lt;/td&gt;\r\n							&lt;td style=&quot;width: 267px; &quot; width=&quot;56%&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;p&gt;\r\n										&lt;strong&gt;กฏของพาสคัล&lt;/strong&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;58&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c3_01.gif&quot; width=&quot;108&quot; /&gt;&lt;/p&gt;\r\n									&lt;p align=&quot;left&quot;&gt;\r\n										จากสูตรเมื่อ&amp;nbsp;A2&amp;nbsp;&amp;gt;&amp;nbsp;A1&amp;nbsp;จะทำให้&amp;nbsp;F2&amp;nbsp;&amp;gt;&amp;nbsp;F1 เครื่องมือที่ทำงานโดยอาศัยหลักการเดียวกันนี้&amp;nbsp;ได้แก่ แม่แรงรถยนต์ รถแทรกเตอร์ เก้าอี้ทำฟัน และระบบเบรครถยนต์ เป็นต้น&amp;nbsp;&lt;br /&gt;\r\n										&lt;br /&gt;\r\n										เครื่องมือเหล่านี้จะมีการได้เปรียบเชิงกลดังสมการ&lt;/p&gt;\r\n									&lt;p align=&quot;center&quot;&gt;\r\n										&lt;strong&gt;การได้เปรียบเชิงกล&lt;/strong&gt;&lt;img align=&quot;absmiddle&quot; height=&quot;58&quot; hspace=&quot;10&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c3_02.gif&quot; width=&quot;90&quot; /&gt;&lt;/p&gt;\r\n								&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFFFF&quot; border=&quot;0&quot; cellpadding=&quot;5&quot; cellspacing=&quot;10&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; width: 570px; &quot; width=&quot;1000&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#999999&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 536px; &quot;&gt;\r\n				&lt;div align=&quot;center&quot; class=&quot;style6&quot; style=&quot;font-size: 16px; color: rgb(153, 0, 0); font-weight: bold; &quot;&gt;\r\n					&lt;strong&gt;::.&lt;/strong&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;แรงลอยตัวและหลักของอาร์คิมีดิส .::&lt;/strong&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 536px; &quot;&gt;\r\n				หลักเกี่ยวกับแรงลอยตัวของวัตถุซึ่งอยู่ในของเหลวกล่าวว่า&amp;nbsp;&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&amp;ldquo;แรงลอยตัวจะมีค่าเท่ากับน้ำหนักของของเหลวซึ่งมีปริมาตรเท่าวัตถุส่วนที่จม&amp;rdquo;&lt;/span&gt;&amp;nbsp;มีค่าดังสมการ&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;width: 299px; &quot; width=&quot;60%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 283px; &quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img height=&quot;35&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c4_13.gif&quot; width=&quot;111&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 283px; &quot;&gt;\r\n								&lt;div align=&quot;left&quot;&gt;\r\n									&amp;nbsp;&amp;nbsp;&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;B&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;คือ แรงลอยตัว( buoyant force )&lt;br /&gt;\r\n									&lt;img align=&quot;absmiddle&quot; alt=&quot;โรห์&quot; height=&quot;24&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c2_13.gif&quot; width=&quot;24&quot; /&gt;&amp;nbsp;คือ ความหนาแน่นของของเหลว&lt;br /&gt;\r\n									&lt;strong&gt;&lt;em&gt;&amp;nbsp;V&lt;/em&gt;&lt;/strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;คือ ปริมาตรของวัตถุส่วนที่จม&lt;/div&gt;\r\n								&lt;div&gt;\r\n									&amp;nbsp;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFFFF&quot; border=&quot;0&quot; cellpadding=&quot;5&quot; cellspacing=&quot;10&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; width: 766px; &quot; width=&quot;1000&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 732px; &quot;&gt;\r\n				&lt;strong style=&quot;color: rgb(153, 0, 0); font-size: 16px; text-align: -webkit-center; background-color: rgb(153, 153, 153); &quot;&gt;::.&lt;/strong&gt;&lt;strong style=&quot;color: rgb(153, 0, 0); font-size: 16px; text-align: -webkit-center; background-color: rgb(153, 153, 153); &quot;&gt;&amp;nbsp;&lt;/strong&gt;&lt;strong style=&quot;color: rgb(153, 0, 0); font-size: 16px; text-align: -webkit-center; background-color: rgb(153, 153, 153); &quot;&gt;&amp;nbsp;ความตึงผิว .::&lt;/strong&gt;&lt;br /&gt;\r\n				แรงระหว่างโมเลกุลของของเหลวที่ดึงกันไว้ทำให้ผิวของเหลวราบเรียบและตึงเรียกว่า&amp;nbsp;&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&amp;quot;แรงดึงผิว&amp;quot;&lt;/span&gt;&amp;nbsp;&lt;u class=&quot;style1&quot;&gt;แรงดึงผิวนี้จะมีทิศขนานกับผิวของเหลวและตั้งฉากกับขอบที่ของเหลวสัมผัส&lt;/u&gt;&amp;nbsp;&amp;nbsp;ดังรูป&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;100%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td width=&quot;40%&quot;&gt;\r\n								&lt;img align=&quot;absmiddle&quot; height=&quot;115&quot; hspace=&quot;100&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pcon5-1.gif&quot; width=&quot;191&quot; /&gt;&lt;/td&gt;\r\n							&lt;td width=&quot;60%&quot;&gt;\r\n								&lt;div align=&quot;left&quot;&gt;\r\n									&lt;strong&gt;ความตึงผิว&lt;/strong&gt;&amp;nbsp;(surface tension:&lt;img align=&quot;absmiddle&quot; height=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_09.gif&quot; width=&quot;19&quot; /&gt;อ่านว่า แกมมา) เป็นสมบัติเฉพาะตัวของของเหลว&amp;nbsp;คำนวณได้จาก&lt;br /&gt;\r\n									&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; style=&quot;width: 290px; &quot; width=&quot;80%&quot;&gt;\r\n										&lt;tbody&gt;\r\n											&lt;tr&gt;\r\n												&lt;td bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 274px; &quot;&gt;\r\n													&lt;div align=&quot;center&quot;&gt;\r\n														&lt;img height=&quot;51&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_01.gif&quot; width=&quot;58&quot; /&gt;&lt;/div&gt;\r\n												&lt;/td&gt;\r\n											&lt;/tr&gt;\r\n											&lt;tr&gt;\r\n												&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot; style=&quot;width: 274px; &quot;&gt;\r\n													เมื่อ&amp;nbsp;&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;F&lt;/strong&gt;&amp;nbsp;คือขนาดของแรงดึงผิว (นิวตัน) ,&amp;nbsp;&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;L&lt;/strong&gt;&amp;nbsp;คือความยาวของผิวสัมผัส(เมตร)&lt;/td&gt;\r\n											&lt;/tr&gt;\r\n										&lt;/tbody&gt;\r\n									&lt;/table&gt;\r\n								&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td colspan=&quot;2&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;br /&gt;\r\n									&lt;br /&gt;\r\n									เมื่อพิจารณาแรง&amp;nbsp;F&amp;nbsp;ที่ดึงให้เกิดระยะเคลื่อนที่&lt;img align=&quot;absmiddle&quot; height=&quot;18&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_06.gif&quot; width=&quot;31&quot; /&gt;ทำให้ผิวของเหลวมีพื้นที่มากขึ้น&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_07.gif&quot; width=&quot;45&quot; /&gt;งานที่ใช้ในการเพิ่มพื้นที่ผิวหาได้ดังนี้&lt;br /&gt;\r\n									&lt;img align=&quot;absmiddle&quot; height=&quot;29&quot; hspace=&quot;100&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_02.gif&quot; width=&quot;82&quot; /&gt;&lt;br /&gt;\r\n									&lt;img height=&quot;30&quot; hspace=&quot;100&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_03.gif&quot; width=&quot;88&quot; /&gt;&lt;br /&gt;\r\n									&lt;img height=&quot;29&quot; hspace=&quot;100&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_04.gif&quot; width=&quot;83&quot; /&gt;&lt;br /&gt;\r\n									&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; width=&quot;30%&quot;&gt;\r\n										&lt;tbody&gt;\r\n											&lt;tr&gt;\r\n												&lt;td bordercolor=&quot;#FFFFFF&quot;&gt;\r\n													&lt;div align=&quot;left&quot;&gt;\r\n														นั่นคือ&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;51&quot; hspace=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_08.gif&quot; width=&quot;66&quot; /&gt;&lt;/div&gt;\r\n												&lt;/td&gt;\r\n											&lt;/tr&gt;\r\n											&lt;tr&gt;\r\n												&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot;&gt;\r\n													&lt;div align=&quot;left&quot;&gt;\r\n														เมื่อ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c5_05.gif&quot; width=&quot;31&quot; /&gt;&amp;nbsp;เป็นพื้นที่ผิวที่เพิ่มขึ้น&lt;/div&gt;\r\n												&lt;/td&gt;\r\n											&lt;/tr&gt;\r\n										&lt;/tbody&gt;\r\n									&lt;/table&gt;\r\n									&lt;p align=&quot;left&quot;&gt;\r\n										ความตึงผิวของของเหลวแต่ละชนิดที่อุณหภูมิเดียวกันมีค่าไม่เท่ากัน สำหรับของเหลวชนิดหนึ่งความตึงผิวจะเปลี่ยนไปเมื่อของเหลวมีสารเจือ เช่น น้ำเกลือหรือน้ำสบู่จะมีความตึงผิวน้อยกว่าน้ำ และความตึงผิวจะลดลงเมื่ออุณหภูมิของของเหลวเพิ่มขึ้น&lt;/p&gt;\r\n								&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n				&lt;br /&gt;\r\n				&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;--&amp;gt;ความโค้งของผิวของเหลว&amp;nbsp;&lt;/span&gt;ของเหลวในภาชนะจะมีผิวลักษณะโค้งนูนหรือโค้งเว้า&amp;nbsp;ขึ้นกับแรงระหว่างแรงเชื่อมแน่น(cohesive force)ที่เกิดขึ้นระหว่างโมลุกุลชนิดเดียวกัน &amp;nbsp;กับแรงยึดติด(adhesive)ที่เกิดขึ้นระหว่างโมเลกุลต่างชนิดกัน ดังรูป\r\n				&lt;div&gt;\r\n					&lt;img height=&quot;265&quot; hspace=&quot;200&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pic_con05-2.jpg&quot; width=&quot;433&quot; /&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFFFF&quot; border=&quot;0&quot; cellpadding=&quot;5&quot; cellspacing=&quot;10&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; width: 732px; &quot; width=&quot;1000&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#999999&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 698px; &quot;&gt;\r\n				&lt;div align=&quot;center&quot; class=&quot;style6&quot; style=&quot;font-size: 16px; color: rgb(153, 0, 0); font-weight: bold; &quot;&gt;\r\n					&lt;strong&gt;::.&lt;/strong&gt;&lt;strong&gt;&amp;nbsp;ความหนืด .::&lt;/strong&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 698px; &quot;&gt;\r\n				ของไหลที่มีความหนืดมากจะมีแรงต้านการเคลื่อนที่อันเนื่องมาจากความหนืดของของไหล เรียกว่า&amp;nbsp;&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&amp;quot;แรงหนืด&amp;quot;&lt;/span&gt;&lt;br /&gt;\r\n				&lt;span class=&quot;style15&quot; style=&quot;font-weight: bold; color: rgb(0, 102, 0); &quot;&gt;แรงหนืดที่กระทำต่อวัตถุขึ้นอยู่กับขนาดความเร็วของวัตถุและแรงนี้มีทิศตรงกันข้ามกับการเคลื่อนที่ของวัตถุ&lt;/span&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;จอร์จ กาเบรียล สโตกส์&lt;/span&gt;์&amp;nbsp;ได้ทดลองหาแรงหนืดและพบว่า&amp;nbsp;&lt;span class=&quot;style15&quot; style=&quot;font-weight: bold; color: rgb(0, 102, 0); &quot;&gt;แรงหนืดแปรผันตรงกับความเร็วของวัตถุทรงกลมตัน&lt;/span&gt;&amp;nbsp;ตามสมการ&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;table align=&quot;center&quot; border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; width=&quot;545&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bordercolor=&quot;#FFFFFF&quot;&gt;\r\n								&lt;p&gt;\r\n									&lt;img height=&quot;28&quot; hspace=&quot;200&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c6_01.gif&quot; width=&quot;105&quot; /&gt;&lt;br /&gt;\r\n									&amp;nbsp;&lt;/p&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bgcolor=&quot;#FFCC99&quot; bordercolor=&quot;#FFFFFF&quot;&gt;\r\n								&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;F&lt;/strong&gt;&amp;nbsp;คือ&amp;nbsp;แรงหนืดของของไหล (นิวตัน)&lt;br /&gt;\r\n								&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;r&lt;/strong&gt;&amp;nbsp;คือ&amp;nbsp;รัศมีของวัตถุทรงกลม (เมตร)&lt;br /&gt;\r\n								&lt;strong class=&quot;style19&quot; style=&quot;font-family: \'Times New Roman\', Times, serif; font-size: 16px; &quot;&gt;v&lt;/strong&gt;&amp;nbsp;คือ&amp;nbsp;ความเร็วของวัตถุทรงกลม&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;23&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c6_03.gif&quot; width=&quot;84&quot; /&gt;&lt;br /&gt;\r\n								&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c6_02.gif&quot; width=&quot;16&quot; /&gt;(อ่านว่า&amp;nbsp;ETA) คือ&amp;nbsp;ความหนืดของของไหล (นิวตันวินาที/ตารางเมตร หรือ พาสคัลวินาที)&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;table align=&quot;center&quot; bgcolor=&quot;#FFFFFF&quot; border=&quot;0&quot; cellpadding=&quot;5&quot; cellspacing=&quot;10&quot; style=&quot;color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: medium; width: 676px; &quot; width=&quot;1000&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td bgcolor=&quot;#999999&quot; class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 642px; &quot;&gt;\r\n				&lt;div align=&quot;center&quot; class=&quot;style6&quot; style=&quot;font-size: 16px; color: rgb(153, 0, 0); font-weight: bold; &quot;&gt;\r\n					&lt;strong&gt;::.&lt;/strong&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;พลศาสตร์ของของไหล .::&lt;/strong&gt;&lt;/div&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;style1&quot; style=&quot;font-family: Tahoma; font-size: 14px; width: 642px; &quot;&gt;\r\n				&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;--&amp;gt;&amp;nbsp;ของไหลในอุดมคต&lt;/span&gt;&lt;strong&gt;ิ&amp;nbsp;&lt;/strong&gt;มีสมบัติดังนี้&lt;br /&gt;\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						มีการไหลอย่างสม่ำเสมอ หมายถึงความเร็วของทุกอนุภาค ณ ตำแหน่งต่างๆของของไหลมีค่าคงตัว&lt;/li&gt;\r\n					&lt;li&gt;\r\n						มีการไหลโดยไม่หมุน คืออนุภาคจะไม่เคลื่อนที่ด้วยความเร็วเชิงมุม&lt;/li&gt;\r\n					&lt;li&gt;\r\n						มีการไหลโดยไม่มีแรงต้านเนื่องจากความหนืด หมายถึงไม่มีแรงต้านใดๆในเนื้อของของไหล&lt;/li&gt;\r\n					&lt;li&gt;\r\n						ไม่สามารถอัดได้ หมายความว่าของไหลมีปริมมาตรคงตัวมีความหนาแน่นเท่าเดิมตลอด&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n				&lt;strong class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); &quot;&gt;--&amp;gt; สมการความต่อเนื่อง&lt;/strong&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;table align=&quot;center&quot; border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;80%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td width=&quot;58%&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;img height=&quot;133&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pcon7-1.gif&quot; width=&quot;343&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n							&lt;td width=&quot;42%&quot;&gt;\r\n								ให้&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_17.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;คือ พื้นที่หน้าตัดของท่อที่ของไหลไหลเข้า&lt;br /&gt;\r\n								&lt;img align=&quot;absmiddle&quot; height=&quot;20&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_18.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;คือ พื้นที่หน้าตัดของท่อที่ของไหลไหลออก&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n				&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				จากรูป&amp;nbsp;เมื่อของไหลอุดมคติไหลอย่างสม่ำเสมอผ่านหลอดที่มีเส้นผ่านศูนย์กลางไม่เท่ากัน&amp;nbsp;&lt;br /&gt;\r\n				ปริมาตรที่ไหลผ่านพื้นที่ตัดขวาง&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_17.gif&quot; width=&quot;18&quot; /&gt;ในเวลา&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_01.gif&quot; width=&quot;25&quot; /&gt;จะเท่ากับปริมาตรของของไหลที่ผ่านพื้นที่หน้าตัด&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_18.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;ในเวลา&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;21&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_01.gif&quot; width=&quot;25&quot; /&gt;ที่เท่ากัน&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				มวลของไหลที่ผ่านพื้นที่&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_17.gif&quot; width=&quot;18&quot; /&gt;&amp;nbsp;คือ&lt;img align=&quot;absmiddle&quot; height=&quot;27&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_02.gif&quot; width=&quot;206&quot; /&gt;&lt;br /&gt;\r\n				มวลของไหลที่ผ่านพื้นที่&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_18.gif&quot; width=&quot;19&quot; /&gt;&amp;nbsp;คือ&lt;img align=&quot;absmiddle&quot; height=&quot;30&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_03.gif&quot; width=&quot;220&quot; /&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				มวลที่ไหลผ่านแต่ละส่วนมีค่าเท่ากัน จะได้&lt;br /&gt;\r\n				&lt;img height=&quot;30&quot; hspace=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_04.gif&quot; width=&quot;95&quot; /&gt;&lt;br /&gt;\r\n				&lt;img height=&quot;30&quot; hspace=&quot;40&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_05.gif&quot; width=&quot;170&quot; /&gt;\r\n				&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; width=&quot;22%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bordercolor=&quot;#FFFFFF&quot; width=&quot;55%&quot;&gt;\r\n								&lt;div align=&quot;left&quot;&gt;\r\n									&lt;img height=&quot;30&quot; hspace=&quot;30&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_06.gif&quot; width=&quot;95&quot; /&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bordercolor=&quot;#FFFFFF&quot;&gt;\r\n								&lt;div align=&quot;center&quot;&gt;\r\n									&lt;strong&gt;จะพบว่า&amp;nbsp;&lt;em&gt;Av&lt;/em&gt;&amp;nbsp;= ค่าคงตัว&lt;/strong&gt;&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n				&lt;br /&gt;\r\n				&lt;span class=&quot;style1&quot;&gt;เราเรียกสมการนี้ว่า&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&amp;nbsp;สมการความต่อเนื่อง&lt;/span&gt;&amp;nbsp;ซึ่งสรุปได้ว่า&lt;/span&gt;&lt;br /&gt;\r\n				&lt;span class=&quot;style15&quot; style=&quot;font-weight: bold; color: rgb(0, 102, 0); &quot;&gt;&lt;u&gt;ผลคูณระหว่างพื้นที่หน้าตัดกับอัตราเร็วของของไหลอุดมคติ&amp;nbsp;ไม่ว่าจะอยู่ที่ตำแหน่งใดในหลอดจะมีค่าคงตัวเสมอ&lt;/u&gt;&lt;/span&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;--&amp;gt;สมการของแบร์นูลลี&lt;/span&gt;&lt;br /&gt;\r\n				&lt;img height=&quot;218&quot; hspace=&quot;150&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/pcon7-2.gif&quot; width=&quot;386&quot; /&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;strong&gt;พิจารณาที่ท่อส่วนล่าง&lt;/strong&gt;&lt;br /&gt;\r\n				งานที่กระทำโดยแรง&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_19.gif&quot; width=&quot;21&quot; /&gt;&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;35&quot; hspace=&quot;20&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_07.gif&quot; width=&quot;231&quot; /&gt;&lt;br /&gt;\r\n				พลังงานศักย์&lt;img align=&quot;absmiddle&quot; height=&quot;32&quot; hspace=&quot;80&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_08.gif&quot; width=&quot;102&quot; /&gt;&lt;br /&gt;\r\n				พลังงานจลน์&amp;nbsp;คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;48&quot; hspace=&quot;55&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_09.gif&quot; width=&quot;111&quot; /&gt;&amp;nbsp;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;strong&gt;พิจารณาที่ท่อส่วนบน&lt;/strong&gt;&lt;br /&gt;\r\n				งานที่กระทำโดยแรง&amp;nbsp;&lt;img align=&quot;texttop&quot; height=&quot;22&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_20.gif&quot; width=&quot;22&quot; /&gt;(ทิศตรงข้าม)&lt;img align=&quot;absmiddle&quot; height=&quot;34&quot; hspace=&quot;15&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_10.gif&quot; width=&quot;294&quot; /&gt;&lt;br /&gt;\r\n				พลังงานศักย์&lt;img align=&quot;absmiddle&quot; height=&quot;31&quot; hspace=&quot;150&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_11.gif&quot; width=&quot;100&quot; /&gt;&lt;br /&gt;\r\n				พลังงานจลน์&amp;nbsp;คือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;50&quot; hspace=&quot;120&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_12.gif&quot; width=&quot;112&quot; /&gt;&lt;br /&gt;\r\n				&lt;br /&gt;\r\n				&lt;strong&gt;งานจากแรงดัน = การเปลี่ยนพลังงานกล&lt;/strong&gt;&lt;br /&gt;\r\n				&lt;img height=&quot;52&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_13.gif&quot; width=&quot;400&quot; /&gt;&lt;br /&gt;\r\n				จาก&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;50&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_14.gif&quot; width=&quot;66&quot; /&gt;แทนค่าได้&amp;nbsp;&lt;br /&gt;\r\n				&lt;table border=&quot;1&quot; bordercolor=&quot;#CC3333&quot; cellpadding=&quot;3&quot; cellspacing=&quot;3&quot; width=&quot;50%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bordercolor=&quot;#FFFFFF&quot; width=&quot;58%&quot;&gt;\r\n								&lt;img height=&quot;52&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_15.gif&quot; width=&quot;328&quot; /&gt;&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n						&lt;tr&gt;\r\n							&lt;td bordercolor=&quot;#FFFFFF&quot;&gt;\r\n								&lt;strong&gt;นั่นคือ&amp;nbsp;&lt;img align=&quot;absmiddle&quot; height=&quot;51&quot; src=&quot;http://www.obeclms.com/lesson/08_Fluid/images/c7_16.gif&quot; width=&quot;151&quot; /&gt;= ค่าคงตัว&lt;/strong&gt;&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n				&lt;br /&gt;\r\n				&lt;span class=&quot;style1&quot;&gt;สมการนี้เรียกว่า&lt;/span&gt;&lt;span class=&quot;style16&quot; style=&quot;color: rgb(0, 51, 102); font-weight: bold; &quot;&gt;&amp;nbsp;สมการของแบร์นูลลี&lt;/span&gt;&amp;nbsp;&lt;span class=&quot;style1&quot;&gt;ซึ่งกล่าวว่า&lt;/span&gt;&lt;span class=&quot;style15&quot; style=&quot;font-weight: bold; color: rgb(0, 102, 0); &quot;&gt;&amp;nbsp;&lt;br /&gt;\r\n				&lt;u&gt;ผลรวมของความดันพลังงานจลน์ต่อหนึ่งหน่วยปริมาตร และพลังงานศักย์โน้มถ่วงต่อหนึ่งหน่วยปริมาตร ณ ตำแหน่งใดๆภายในท่อที่ของไหลผ่าน มีค่าคงตัว&lt;/u&gt;&lt;/span&gt;ด้วยหลักการนี้จึงเกิดการประยุกต์ใช้ในการทำงานของเครื่องพ่นสี และการออกแบบปีกเครื่องบิน&amp;nbsp;เป็นต้น&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;color: rgb(0, 0, 0); &quot;&gt;--------------------------------------------------------------------------------------------------------------------------------------------&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '1344605044', '1344605044');
