INSERT INTO `news` VALUES ('6', 'เฟอร์กี้กลัวนะเนี่ย แมนยูเสียเปรียบเยือนบิลเบา', '&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; &quot;&gt;\r\n	&lt;font color=&quot;#000040&quot; size=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;&amp;nbsp;เอพีรายงานวันที่ 14 มี.ค. ว่า ในการแข่งขันยูโรปา ลีก วันพฤหัสบดีนี้เป็นนัดที่สองทีมคู่แข่งในเมืองแมนเชสเตอร์ของอังกฤษอยู่ภายใต้ภาวะกดดันว่าอาจตกรอบ แม้ว่าในพรีเมียร์ลีกแล้วจะอยู่ต้นตารางแข่งกันชิงจ่าฝูง แต่กับลีกยุโรป ทั้งสองทีมพ่ายมาแล้วทั้งคู่ โดยเฉพาะแมนเชสเตอร์ ยูไนเต็ด ที่แพ้นัดแรก 3-2 นัดที่สอง ต้องออกไปเยือนสเปน พบกับทีมแอธเลติก บิลเบาที่มีภาษีดีกว่า และลุ้นจะเข้ารอบ 8 ทีมสุดท้ายเป็นครั้งแรกหลังจากฤดูกาล 1976-77&lt;/font&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; &quot;&gt;\r\n	&lt;font color=&quot;#000040&quot; size=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;&amp;nbsp;&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13317445071331744770l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: right; width: 260px; height: 280px; &quot; /&gt;&lt;/font&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; &quot;&gt;\r\n	&lt;font color=&quot;#000040&quot; size=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;&amp;nbsp;เซอร์อเล็กซ์ เฟอร์กูสัน โค้ชใหญ่แมนยู กล่าวว่า ปีนี้&amp;nbsp;เราไม่ดีนักในถ้วยยุโรป อาจจะเป็นเพราะปกติแล้ว เรามักทำแต้มสะสมความได้เปรียบเวลาเล่นในบ้าน นัดล่าสุดนี้ นานี่ อันแดร์สัน อันโตนิโอ วาเลนเซีย และทอม เคลเวอร์ลีย์ ยังไม่รู้ว่าจะลงเล่นได้หรือไม่ แม้แต่เวย์น รูนีย์ก็เสี่ยงที่จะลงในเมื่อนัดหน้าเราต้องออกไปเยือนวูล์ฟส์&lt;/font&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; &quot;&gt;\r\n	&lt;font color=&quot;#000040&quot; size=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;&amp;nbsp;&lt;/font&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; &quot;&gt;\r\n	&lt;font color=&quot;#000040&quot; size=&quot;3&quot; style=&quot;font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;&amp;nbsp;ด้านแมนเชสเตอร์ ซิตี้ จะเล่นในบ้านรับการมาเยือนของปอร์โต้ และเป็นที่จับตาว่า คาร์ลอส เตเบซจะได้หวนกลับมาเล่นให้ทีมหรือไม่ หลังจากมีปัญหาตีรวนจะย้ายทีมมาข้ามปี&lt;/font&gt;&lt;/p&gt;', '1331768778', '1331768778', '1', '2');
INSERT INTO `news` VALUES ('7', 'เลิกซะที \"ภาษีผัว-เมีย\"', '&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;font color=&quot;#aa0000&quot; face=&quot;Tahoma&quot;&gt;&lt;strong&gt;ทวี มีเงิน&lt;/strong&gt;&lt;/font&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;font face=&quot;Tahoma&quot;&gt;ในระหว่างนี้คนทำงานกินเงินเดือนต้องยื่นแบบเสียภาษี มีคนใกล้ชิดหลายคนบ่นให้ฟังว่าภาษีเมืองไทยไม่เป็นธรรมกับคนทำงานหาเช้ากินค่ำ อย่างมนุษย์เงินเดือนเสียจริงๆ อัตราภาษีแบบก้าวหน้าก็แพงมหาโหด ช่องว่างก็ห่างกันแค่ 4 อัตรา 10%, 20%, 30% และ 37% เท่านั้น&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;b&gt;คนที่มีรายได้ต่ำสุดของอัตราภาษีสูงๆ จึงเสียเปรียบเช่นมีเงินเดือน 1,000,001 ต่อปีแต่เสียภาษีเท่ากับคนมีรายได้ 4,000,000 บาทต่อปี รายได้ห่างกันถึง 3 ล้านบาทต่อปี แต่เสียภาษีอัตรา 30% เท่ากัน&lt;/b&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	ซ้ำร้ายคนกลุ่มนี้มีจำนวนไม่น้อยยังถูกนำรายได้ของภรรยามารวมไว้ ตามประมวลรัษฎากรมาตรา 42(2) ให้รายได้สามีที่มีเงินเดือนกับรายได้ภรรยาที่ไม่เป็นเงินเดือนเช่น ค่าธรรมเนียม นายหน้า มารวมเป็นรายได้สามี ?ซึ่งในการจัดเก็บของสรรพากรกำหนดว่ารายได้ภรรยาให้ถือว่าเป็นรายได้สามี โดยสามีต้องนำรายได้ภรรยามาคำนวณภาษีด้วย?&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	ทำให้ฐานเงินได้ของสามีที่ใช้ในการคำนวณภาษีสูงขึ้น ภาระภาษีอัตราก้าวหน้าสูงขึ้นด้วย เรียกว่าซวย 2 เด้ง&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;b&gt;เจตนากฎหมายเขียนขึ้นเพื่อล้อมกรอบคนชั่ว แต่มันไปกระทบคนดีๆ กล่าวคือ กลัวว่าคนมีรายได้สูงๆ หาทางเลี่ยงภาษีด้วยการโอนรายได้บางส่วนเอามาไว้ที่ภรรยาเพื่อจะเสียภาษีต่ำๆ กลายเป็นว่าคนที่ได้รับผลกระทบมากที่สุดคือคนที่กำลังสร้างเนื้อสร้างตัว อาจจะให้แม่บ้านออกมาเลี้ยงดูลูก มีรายได้พิเศษมาจุนเจือครอบครัวแต่กลับถูกนำมาคิดภาษี รวมกับสามีภาระภาษีเพิ่มจากที่ต้องเสียปกติแบบก้าวกระโดด&lt;/b&gt;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	จึงไม่เป็นธรรมกับคนทำงานกินเงินเดือน&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	บางคนบ่นให้ฟังว่าเงินเดือนแสนต้นๆ เสียภาษี 30% เท่ากับคนเงินเดือน 4 แสนถือว่าเสียเปรียบอยู่แล้วภรรยาทำงานอยู่บ้านมีรายได้เดือนละ 5 หมื่น ถูกมาคิดรวมทำให้ต้องเสียภาษีเกือบๆ 3 แสนบาท รายได้จริงๆ จึงเหลือแค่เดือนละ 8 หมื่นกว่าบาท เหลือน้อยกว่าเงินเดือนลูกน้องเสียอีก&amp;nbsp;&lt;br /&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13317379081331738045l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: right; width: 260px; height: 190px; &quot; /&gt;&lt;br /&gt;\r\n	กรมสรรพากรเคยคิดจะยกเลิกไม่ให้นำมาคิดรวม จนป่านนี้ไม่รู้ว่าถึงไหนเห็นแต่รัฐบาลกระตือรือร้นจะลดภาษีนิติบุคคลจาก 30% เหลือ แค่ 23% อ้างว่าเพื่อเป็นแม่เหล็กให้นักลงทุนต่างชาติเข้ามาลงทุนมากขึ้น&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	แต่ไม่มองคนทำงานมนุษย์เงินเดือนที่เป็นฟันเฟืองเล็กๆ ของระบบเศรษฐกิจ หากรัฐบาลเลิกนำรายได้ภรรยามารวมกับเงินเดือนสามี นอกจากจะสร้างความเป็นธรรมแล้ว&amp;nbsp;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;b&gt;ยังจะมีรายได้ส่วนหนึ่งมากระตุ้นการบริโภคเป็นการกระตุ้นเศรษฐกิจอีกทางด้วย&lt;/b&gt;&lt;/font&gt;&lt;/p&gt;', '1331769078', '1331769078', '1', '2');
INSERT INTO `news` VALUES ('8', '\"แจ๊บ\"ควง\"จูน\"ลองชุดวิวาห์ ฤกษ์25มี.ค. คบ6เดือนรีบแต่ง กลัวมีลูกไม่ทันใช้', '&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เมื่อวันที่ 14 มี.ค. เวลา 14.00 น. ที่ ฟินาเล่ เวดดิ้ง สตูดิโอ ย่านเหม่งจ๋าย นักแสดงหนุ่ม &amp;prime;แจ๊บ&amp;prime;เพ็ญเพ็ชร เพ็ญกุล อายุ 37 ปี จูงมือว่าที่เจ้าสาว &amp;prime;จูน&amp;prime;ธัญญานุช แก้วประสงค์ อายุ 27 ปี ลองชุดแต่งงาน เตรียมจัดงานแบบเงียบๆ วันที่ 25 มี.ค. ที่ จ.เพชรบูรณ์ เชื่ออายุที่ห่างกัน 10 ปี ไม่เป็นอุปสรรค เผยชอบฝ่ายหญิงตรงที่นิสัยคล้ายกัน&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13317205761331720630l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: left; width: 260px; height: 336px; &quot; /&gt;&lt;br /&gt;\r\n	&amp;nbsp;&amp;prime;แจ๊บ-เพ็ญเพ็ชร&amp;prime; เล่าถึงความรักครั้งนี้ว่า รู้จักกับจูนเพราะเพื่อนของน้องแนะนำให้รู้จักกัน ซึ่งจูนเขาทำบริษัทออแกไนซ์ของครอบครัวอยู่ ส่วนเหตุผลที่ตกลงแต่งงานกันในครั้งนี้ เพราะว่าพร้อมแล้วทุกอย่างเลยขอเขาแต่งงาน และวันที่ขอแต่งงานตอนนั้นก็ไม่ได้โรแมนติกอะไรมาก เรียกว่าเป็นการขอแต่งงานธรรมดาๆ เพราะเราคบกันมาสักพักนึงประมาณ 6 เดือนได้แล้วและคิดตรงกันว่าถึงเวลาแล้ว&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;ผู้สื่อข่าวถามต่ออีกว่าเตรียมงานเรื่องงานแต่งงานไปถึงไหนแล้ว นักแสดงหนุ่มตอบว่า จริงๆ เรื่องแต่งงานยังไม่ได้บอกสื่ออย่างเป็นทางการ คิดแค่ว่าจะไปจัดงานแต่งกันเงียบๆ วันที่ 25 มี.ค. ก่อนที่บ้านน้องเขาที่ จ.เพชรบูรณ์ และรอพร้อมๆ ในเรื่องสถานที่ก่อน เพราะเดือน เมษายน คนแต่งงานเยอะ เลยตั้งใจรอสักระยะนึงก่อนแล้วค่อยมาจัดเลี้ยงที่กรุงเทพฯ น่าจะประมาณกลางๆ ปีนี้ ส่วนเรื่องสินสอดทางฝ่ายหญิงเขาเรียกไม่เยอะ&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;ต่อข้อซักถามที่ว่าประทับใจกันและกันอย่างไรบ้าง หนุ่ม &amp;prime;แจ๊บ&amp;prime; กล่าวว่า ชอบตรงที่จูนเป็นคนไม่ซีเรียส ไม่ค่อยเครียด อยู่ด้วยแล้วแฮปปี้ และน้องเขาน่ารักอยู่ด้วยยังไม่เคยโกรธอะไรเลย ซึ่งปกติบางทีตนทำงานเยอะๆ แล้วหายไปบ้าง จากประสบการณ์คนที่เคยคบกันเมื่อก่อนอาจมีโทรตามบ้าง แต่กับจูนไม่มีตามเลย ถามว่าคบกันแค่ 6 เดือนเร็วไปมั้ยกับการแต่งงาน คิดว่าไม่เร็วไป แต่ช้าไปด้วยซ้ำ เพราะเดี๋ยวมีลูกไม่ทันใช้&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;ด้านว่าที่เจ้าสาว กล่าวต่อว่า ส่วนตัวชอบพี่แจ๊บตรงที่นิสัยเหมือนกัน อารมณ์ดีอยู่ด้วยกันแล้วหัวเราะตลอด ไม่มีเรื่องทะเลาะเลย แล้ววันที่พี่เขาขอแต่งงานก็จำไม่ได้แล้วว่าขอยังไง แต่ก็คุยกันแบบนี้ตลอดว่าถึงเวลาแล้วก็แต่งงาน ส่วนเรื่องอายุที่ห่างกัน 10 ปี ตนว่าไม่น่ามีปัญหา&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13317205761331720639l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: right; width: 260px; height: 195px; &quot; /&gt;&lt;br /&gt;\r\n	&amp;nbsp;ผู้สื่อข่าวถามต่ออีกว่าแต่งงานแล้วจะมีลูกเลยหรือเปล่า นักแสดงหนุ่มตอบ ถ้ามีเลยก็ดี เพราะเป็นปีมังกรทองด้วย&amp;nbsp;อยากได้ลูกผู้ชาย แต่จูนเขาอยากได้ลูกผู้หญิง ส่วนเรื่องฮันนีมูนน่าจะเป็นประเทศญี่ปุ่นเพราะจูนเขาอยากไปพอดี&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;เพื่อนๆ ในวงการว่าอย่างไรเมื่อรู้ข่าวจะแต่งงาน &amp;prime;แจ๊บ&amp;prime; ตอบ ยังไม่มีใครรู้เท่าไหร่ว่าจะแต่งงาน จะมีแค่เพื่อนสนิทและเพื่อนที่เตะฟุตบอลเท่านั้นที่รู้ เรียกว่าแต่งแบบสายฟ้าแล้บก็ได้ ก็ฝากบอกเพื่อนๆ พี่ๆ และผู้ใหญ่ไว้ ณ ที่นี้เลยว่าจะแต่งงานแล้ว ไว้มีงานเลี้ยงที่กรุงเทพฯ แล้วจะเชิญ ส่วนเรื่องการเตรียมงานแต่งที่ จ.เพชรบูรณ์ เสร็จเรียบร้อยแล้ว&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;นักแสดงหนุ่ม พร้อมกับให้สัญญาว่า &amp;quot;ผมเคยสัญญากับจูนไปก่อนหน้านั้นแล้วว่าที่ผ่านมาผมอาจมีข่าวเยอะหน่อย และตั้งแต่นี้ต่อไปแต่งงานแล้วก็จะไม่มีข่าวอีกครับ&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;ด้าน &amp;prime;จูน&amp;prime; เผย &amp;quot;อยากให้เขาเหมือนเดิมอย่างนี้ตลอด ให้อารมณ์ดีแบบนี้ไปตลอด อยู่ด้วยกันหัวเราะกันแบบนี้ไปตลอด แค่นี้ก็มีความสุขแล้วไม่ว่าจะเป็นอย่างไรค่ะ&amp;quot;&lt;br /&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13317205761331720645l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: left; width: 260px; height: 195px; &quot; /&gt;&lt;/p&gt;', '1331769412', '1331769412', '1', '8');
INSERT INTO `news` VALUES ('9', 'จีนถกผลกระทบละคร ช็อกกรณี 2 น.ร.หญิงฆ่าตัวหวังย้อนยุค', '&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เมื่อ 13 มี.ค.&amp;nbsp;อินเตอร์เนชั่นแนล บิสิเนส ไทมส์ รายงานถึงเหตุการณ์ช็อกสังคมจีน กรณีนักเรียนหญิงสองคนชั้นป.5 วัย 12 ปีที่เป็นเพื่อนสนิทกัน&amp;nbsp;กระโดดน้ำฆ่าตัวตายคู่ เพื่อหนีปัญหาจากโลกปัจจุบัน ด้วยความหวังจะหลุดข้ามภพเข้าไปในยุคอดีต ตามที่ได้ดูในละคร ว่ายังคงเป็นประเด็นถกเถียงในวงการสื่อและการศึกษาของจีนอย่างต่อเนื่อง&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13316592511331659443l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: left; width: 260px; height: 185px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เหตุการณ์ดังกล่าวเกิดขึ้นที่หมู่บ้านถิงเจิ้ง ในเขตจางผู่ เมืองจางโจว มณฑลฝูเจี้ยน ทางภาคตะวันออกของจีน มีผู้พบศพเด็กนักเรียนทั้งสองที่สระน้ำลึก 2 เมตรในวันที่ 1 มี.ค. สอบสวนทราบว่า&amp;nbsp;ด.ญ.เสี่ยวหัวทำกุญแจบ้านหาย กลัวว่าจะต้องถูกดุว่า จึงปรึกษากับเพื่อนสนิทชื่อเสี่ยวเหมย จากนั้นทั้งสองก็กระโดดน้ำฆ่าตัวตายด้วยกัน โดยทิ้งจดหมายลาตายไว้ให้พ่อแม่ตนเอง&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เสี่ยวเหมยเขียนว่า &amp;quot;พ่อและแม่ หนูคงอยู่ดูแลทั้งสองไม่ได้แล้ว หนูต้องจากไป / เจ้าน้องชาย จงดูแลพ่อแม่ให้ดีนะ /เพื่อนสนิทของหนู (เสี่ยวหัว) เขาทำกุญแจบ้านหาย และกลัวว่า ถ้ากลับบ้านแล้วจะต้องถูกดุแน่ เธอบอกว่าอยากตาย หนูเลยตัดสินใจจะตายเป็นเพื่อนเธอ อย่าห่วงนะ เพราะถึงแม้เราจะไม่ได้เกิดเดือนเดียวปีเดียวกัน เราจะตายวันเดียวกัน ในชีวิตนี้ หนูมีความลับสองเรื่อง เรื่องแรกคืออยากจะย้อนกลับไปยุคราชวงศ์ชิง เพื่อสร้างหนังเกี่ยวกับฮ่องเต้ และเรื่องที่สองคืออยากจะไปท่องอวกาศ&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13316592511331659448l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: right; width: 260px; height: 173px; &quot; /&gt;&lt;br /&gt;\r\n	&amp;nbsp;ส่วนเสี่ยวหัวเขียนว่า &amp;quot;พ่อ แม่ และพี่สาว หนูขอโทษที่ต้องจากไป ดูแลสุขภาพดีๆ นะ ปกติหนูชอบทะเลาะกับพี่สาว แต่หนูรักพี่มากนะ ความจริงพ่อแม่มีพี่คนเดียวก็พอแล้ว เพราะหนูชอบยั่วโมโหและทำให้แม่ไม่มีความสุข ขอโทษจริงๆ ค่ะ พี่จ๊ะ&amp;nbsp;ดูแลแม่และพ่อให้ดีนะ&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;หลังข่าวการเสียชีวิตของทั้งสองเผยแพร่ออกไป&amp;nbsp;สังคมจีนวิจารณ์กันอย่างกว้างขวางถึงปัญหาครอบครัวและโรงเรียนที่ไม่ให้ความรักความเข้าใจกับเด็ก&amp;nbsp;และบางส่วนมองว่า เด็กคงมีปัญหาทางจิตใจที่รับปัญหาเล็กๆ อย่างการทำกุญแจหายยังไม่ได้ ขณะที่คนในพื้นที่ต่างพูดว่า เป็นเพราะเด็กทั้งสองดูซีรีส์แนวเจาะเวลาหาอดีตมากเกินไป จนไม่อยู่ในความจริง ถึงขั้นฆ่าตัวตายหวังจะได้ไปอยู่ในยุคราชวงศ์ชิง&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13316592511331659456l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: right; width: 260px; height: 363px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;สำหรับละครที่เด็กทั้งสองชื่นชอบนั้นเป็นเรื่อง ปู้ปู้จิงซิน หรือ Startling by Each Step นำแสดงโดย อู๋ฉีหลง พระเอกไต้หวัน เจิ้งเจียอิ่ง พระเอกฮ่องกง และ หลิว สือซื่อ นางเอกจีนแผ่นดินใหญ่ เป็นเรื่องราวของสาวยุคปัจจุบันที่ข้ามภพไปอยู่ในสมัยราชวงศ์ชิง และพบรักสามเส้ากับองค์ชายสองพระองค์ ซึ่งหลังเกิดเหตุสถานีที่ฉายละครเรื่องนี้อยู่ต้องปรับเวลาออกไปให้ดึกขึ้น และบางแห่งต้องหยุดฉายไปเลย&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;ซุน หยุนเซียว รองผู้อำนวยการสถาบันวิจัยเด็กและเยาวชน กล่าวว่า โศกนาฏกรรมจากการเลียนแบบเช่นนี้เกิดขึ้นบ่อย อย่างกรณีที่เด็กกระโดดจากตึกสูง เพราะอยากทำแบบตัวละครที่เหาะเหินได้แบบในทีวีหรือในหนัง จึงจำเป็นมากที่ต้องคอยเฝ้าระวังการชมทีวีของเด็ก โดยเฉพาะเด็กวัยนี้ที่อยากรู้อยากเห็นมาก แต่มักตัดสินอะไรไม่ได้&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;ก่อนหน้านี้ สำนักงานกำกับดูแลวิทยุโทรทัศน์ของจีน หรือ SARFT ออกแถลงการณ์เตือนผู้ผลิตทางโทรทัศน์ให้หลีกเลี่ยงการสร้างหนังแฟนตาซีเพ้อผัน ย้อนเวลาหาอดีต และเรื่องที่ลึกลับ ไม่มีเหตุผล&amp;nbsp;รวมถึงเรื่องที่ไม่มีมุมมองในแง่บวก&lt;br /&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13316592511331659462l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: left; width: 260px; height: 195px; &quot; /&gt;&lt;/p&gt;', '1331769905', '1331769905', '1', '3');
INSERT INTO `news` VALUES ('10', 'มิคเผยยังอยู่แค่ขั้นทำคะแนนชนะใจแม่เบนซ์', '&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	คู่รักวงการบันเทิง&amp;nbsp;มิค บรมวุฒิ หิรัณยัษฐิติ เบนซ์ พรชิตา ณ สงขลา ให้สัมภาษณ์ระหว่างออกงานคู่กัน&amp;nbsp;ในงานสเตร็ปซิล วอร์ม ที่เซ็นทรัลเวิลด์ ว่า เห็นว่างานน่ารักๆ แบบนี้ก็โอเค แต่ถ้างานหวานมากๆ ก็คงไม่รับ เพราะคู่เราไม่เหมาะกับงานหวานๆ เรารู้สึกว่าถ้ามาขำๆโอเค เพราะมันเป็นสไตล์ของคู่เราเพราะเราไม่ค่อยหวานเท่าไหร่ แต่ก็ยังมีความสนิทกันมากกว่า คงไม่มีใครตบหัวผมออกทีวีแบบเบนซ์แล้วละครับ (หัวเราะ)&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13316706661331670739l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: right; width: 260px; height: 252px; &quot; /&gt;&lt;br /&gt;\r\n	&amp;nbsp;ถามว่าเมื่อไหร่ทั้งสองจะมีข่าวดี&amp;nbsp;มิคกล่าวว่า &amp;quot;ต้องเอาชนะใจแม่เบนซ์ให้ได้ก่อนครับ เพราะรอพร้อมก่อน ตอนนี้ทั้งคู่ยังทำงานอยู่ เรายังสนุกกับการทำงานอยู่เลย&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;ส่วนเบนซ์กล่าวว่า &amp;quot;จริงๆก็ยังอยากจะเก็บตังค์ก่อน อย่างที่บอกว่าเราแต่งงานไปเราก็อยากจะสบาย ไม่อยากแต่งงานไปแล้วดูทรมารจิตใจ ดูไม่มีจะกินก็ไม่เอานะ เราก็เลยเอาสบายๆ นิดนึงดีกว่า เรายังอยากให้เวลาตัวเองทั้งคู่ก่อน เก็บตังค์กันก่้อน&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;เมื่อถามฝ่ายชายว่า มีวิธีเอาใจคุณแม่เบนซ์อย่างไร ได้คำตอบว่า ก็พยายามเป็นตัวเรา พยายามให้เขาเห็น เพราะด้วยลุกส์เราและบทละครที่เล่นเป้นคนขี้เล่น เลยทำให้ดูเจ้าชู้ เลยทำให้มีคนไม่รู้ว่ารักหรือว่าหวังดี เราเลยต้องใช้เวลาหลายปีมากกว่าแม่เบนซ์จะเริ่มคุยน่ารักกับเรา เพราะก่อนหน้านี้เขาตั้งกำแพงไว้สูง หลายปีนะกว่าเราจะโชว์ให้แม่เห้นว่ามันเป็นคาแรกเตอร์แบบนั้นเฉยๆ แต่พอหลังออกมาจริงๆมันไม่ใช่แบบนั้น&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;แสดงว่าตอนนี้แม่เริ่มใจอ่อนแล้ว &amp;nbsp;มิคกล่าวว่า&amp;nbsp;&amp;quot;หน่อยนึงแล้ว นิดนึง (ยิ้ม)&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;ขณะที่เบนซ์เสริมว่า &amp;quot;ก็ดีขึ้นๆ แต่เขาคงไม่เปิดไปซะทีเดียวหรอก แต่ว่าเท่าที่เคยคุยกับพี่มิคว่าอย่าไปเปรียบเทียบกับพ่อแม่คนอื่นนะ เพราะว่าแม่เบนซ์ก็เป็นแบบแม่เบนซ์ แม่พี่ก็คือแม่พี่ อย่าคิดว่าทำไมแม่เราดุจัง ทำไมค่อนข้างมีมาตรฐานสูง อันนี้เราต้องยอมรับว่าแม่เราเป็นแบบนั้น เราต้องหาทางที่จะเข้าไปได้เอง ตอนนี้ก้เริ่มจะโอเคเพราะหลายปีแล้ว จะ 7 ปีแล้ว ตอนนี้ท่านก็โอเคในระดับนึง แต่ถ้าจะคุยเรื่องแต่งงานเลยคงจะต้องเรื่องใหญ่ เพราะเราเองก็ยังไม่กล้าเข้าไปคุยเลยนะ&amp;quot;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;ถามว่าจะมีโอกาสแต่งเร็วๆ นี้หรือไม่&amp;nbsp;มิคกล่าวว่า &amp;quot;ปีนี้ยังครับ เพราะว่าโชคดีมีชาย(ชาตโยดม) ม้ามืดมา คุณแม่เลยคงจะปวดหัวเรื่องงานชายก่อนช่วงนี้ ปีก่อนก็ปวดหัวเรื่องพี่ชายคนโต เขาก็โอเค ปีนี้ข้ามไปก่อน&amp;quot;&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;เมื่อถามว่าแล้วแม่ถามๆเรื่องข่าวมั้ย&amp;nbsp;เบนซ์กล่าวว่า &amp;quot;เขาบอกว่า &amp;prime;ถามฉันหรือยัง&amp;prime; งานเข้ามิคเลย (หัวเราะ) เราก็ไม่ได้ไปปรึกษาเขา บางทีเพื่อนแม่ก็มาถามแม่ว่าลูกจะแต่งงานแล้วเหรอ เขาก็เลยยังไม่รู้เรื่องเลย เอาไว้ให้เราสองคนพร้อมจริงๆแล้วจะเข้าไปคุยกับแม่ กลัวเขาน่าแหกกลับมา&amp;quot;&lt;/p&gt;', '1331769991', '1331769991', '1', '6');
INSERT INTO `news` VALUES ('11', 'แป้งคุยแล้วทีมซิตคอม\"เป็นข่าว\" คาใจฉากลงอ่างอยู่คนเดียว', '&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เมื่อ 13 มี.ค. ดาราสาว แป้ง-อรจิรา แหลมวิไล ให้สัมภาษณ์ระหว่างมางานแฟชั่นโชว์ชุดชั้นใน Cris&amp;rsquo;s Collection by Sabina&amp;nbsp;ถึงเรื่องหวานใจ &amp;ldquo;ผู้กองต้อม&amp;rdquo;ร.ต.อ.ทรงพันธ์ กุลดิลก&amp;nbsp;ว่า ตอนนี้สึกแล้ว หลังจากที่บวชได้ 1 เดือน&amp;nbsp; ช่วงที่บวช แป้งก็ไปวัดบ่อยเหมือนกัน&amp;nbsp;แต่พอพระไปที่วัดป่าก็ไม่ได้ตามไป และพอกลับมาก็ไม่มีเวลาไป แต่ตอนนี้สึกแล้ว สึกออกมาก็ดูนิ่งขึ้น&amp;rdquo;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13316697131331671049l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: left; width: 260px; height: 298px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;ถามว่า ฝ่ายชายบวชแล้วจะเบียดเลยไหม แป้งกล่าวว่า&amp;nbsp;&amp;ldquo;ยังค่ะ ยังไม่ใช่ใกล้ๆ ปล่อยให้คนอื่นเขาไปก่อนเดี๋ยวแขกไม่รู้จะไปงานไหนดีเยอะไป ถามว่าเคยคุยเรื่องนี้กันบ้างมั้ย เพราะจริงๆ ก็รู้อยู่แล้วว่าเราไม่ได้คบกันเหมือนวัยรุ่นคบๆ เลิกๆ ก็ดูๆ ไป&amp;rdquo;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เมื่อถามว่า ผู้ใหญ่ทั้งสองฝ่ายไฟเขียวแล้วหรือเปล่า นางเอกสาวตอบ &amp;ldquo; อันนี้ไม่รู้เหมือนกัน แต่ตอนวันเกิดแป้งพ่อ-แม่แป้งก็ไปทำบุญด้วยนะ ถามว่าความรักแฮปปี้มั้ยก็ค่อยๆ เดินทางสายกลางไม่มากไปไม่น้อยไปเอาแบบพอดีๆ ส่วนจะเรียกแฟนได้หรือยังไม่บอก เอาเป็นว่าคุยๆ กันค่ะ&amp;rdquo;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เปลี่ยนถามถึงฟีดแบ็กละคร &amp;ldquo;เป็นข่าว&amp;rdquo;&amp;nbsp;แป้งกล่าวว่า &amp;ldquo;ดีค่ะ ส่วนตัวรู้สึกว่าสนุกดี ชอบ แต่วันๆ&amp;nbsp; นึงตอนนี้ไม่ได้ไปไหนอยู่กองละครต่างจังหวัดมากกว่า เลยไม่รู้ว่าคนภายนอกว่ายังไง และในเรื่องที่เห็นเหวี่ยง วีนเยอะแต่พอดูๆ ไปจะเฉลยเอง เพราะคนเรามนุษย์ต้องมีอารมณ์โมโห อารมณ์ดี เวลาเราทะเลาะกับแฟน หรือเจอใครด่าเรามาเราก็แสดงออก แต่ในคาแรกเตอร์เรื่องนี้จะแสดงออกชัดเจน กัดก็กัดตรงๆ&amp;rdquo;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เมื่อถามถึงฉากต้องเซ็กซี่ค่อนข้างเยอะ แถมลงอ่างบ่อยด้วย นางเอกสาวกล่าวว่า &amp;ldquo;เรื่องนี้ไปเถียงกับเขามาแล้ว ถามว่าเยอะเกินไปหรือเปล่า สำหรับตัวแป้งเองเป็นเด็กในสังกัดเขาให้ทำอะไรก็ทำได้ แต่หลังๆ ก็เริ่มมีพูดแล้วว่าถ้าจะให้ต้องมาลงอ่างเยอะขนาดนี้ให้คนอื่นมาลงบ้างมั้ย เพราะรู้สึกว่าไม่แฟร์สำหรับเราที่ลงอยู่คนเดียว แล้วคนอื่นก็ไม่เห็นมีใครลง แล้วทำไมเราต้องลงทุกตอนทุกฉาก มีการพูดคุยกันไปแล้วซึ่งทีมงานก็เข้าใจเรา เขาสนุกในการเขียนบทจนบางครั้งลืมไป&amp;rdquo;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13316697131331671062l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: right; width: 260px; height: 171px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;ส่วนซัมเมอร์นี้จะมีถ่ายแบบเซ็กซี่ให้เห็นหรือไม่ แป้งกล่าวว่า&amp;nbsp;&amp;ldquo;ก็กำลังคุยๆ อยู่ และไม่เกี่ยวว่าใครหวงด้วย คือ แป้งผ่านมาหมดแล้วอยู่วงการมากี่ปีแล้ว เซ็กซี่ก็รับแล้ว ไม่ใช่ว่าพอถึงอายุเท่านี้แล้วเราจะรับไม่ได้เพราะยังไงงานก็คืองานดูที่ความพอเหมาะพอดีมากกว่า อะไรมากเกินไปก็ดูไม่ดี แต่อะไรที่เรียบนิ่งไปก็ดูไม่ตื่นเต้น ฉะนั้นเอาอะไรที่พอดีๆ ค่ะ&amp;rdquo;&lt;/p&gt;', '1331770142', '1331770142', '1', '8');
INSERT INTO `news` VALUES ('12', 'เชลซีหืดจับ-ต่อเวลาบดนาโปลี 5-4 ควงคู่มาดริดทะลุรอบ 8 ทีมชปล.', '&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13317674781331767549l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: left; width: 260px; height: 169px; &quot; /&gt;ฟุตบอลยูฟ่า แชมเปี้ยนส์ลีก รอบ 16 ทีมสุดท้าย นัดสอง เมื่อวันพุธที่ 14 มี.ค. &amp;quot;สิงโตน้ำเงิน&amp;quot;เชลซี ทะลุเข้าสู่รอบก่อนรองชนะเลิศสำเร็จ หลังเปิดสนามสแตมฟอร์ดบริดจ์ เฉือนชนะ นาโปลี จากอิตาลีแบบสุดมันในช่วงต่อเวลาพิเศษ 5-4 แม้ว่าเกมนี้นาโปลี พกความได้เปรียบมา หลังเอาชนะในนัดแรก 3-1&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;ประตูแรกของเกมเกิดขึ้นในนาทีที่ 28 จากจังหวะที่รามิเรซ โยนบอลจากทางกราบขวา ให้ดิดิเยร์ ดร็อกบา โหม่งตุงตาข่าย ให้เชลซีนำ 1-0 ครึ่งหลังเชลซียังบุกหนัก และมาได้ประตูที่สองจากจอห์น เทอร์รี่ นาที 47 อย่างไรก็ตามอีก 7 นาทีถัดมา นาโปลี ได้ประตูไล่ขึ้นมาจาก โกคาน อินเลอร์ ทำให้ประตูรวมตอนนี้นาโปลีนำ 4-3&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13317674781331767567l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: left; width: 200px; height: 145px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เชลซีไม่มีทางเลือกต้องบุกแหลก และมาได้ประตูสำคัญ เมื่อได้จุดโทษจากการทำแฮนด์บอลของผู้เล่นนาโปลี และเป็นแฟรงก์ แลมพาร์ด ที่รับหน้าที่สังการเข้าไปในนาที 75 ทำให้จบ 90 นาที เสมอกัน 4-4 ต้องต่อเวลาพิเศษ เกมดำเนินเข้าสู่นาที 105 เชลซี มาได้ประตูชัยจาก บรานิสลาฟ อิวาโนวิช ที่เติมขึ้นมาพังประตูช่วยให้เชลซีผ่านเข้ารอบ 8 ทีมสุดท้ายสำเร็จ&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.khaosod.co.th/view_resizing_images.php?filename=online/2012/03/13317674781331767574l.jpg&amp;amp;width=260&amp;amp;height=260&quot; style=&quot;float: right; width: 260px; height: 177px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;ส่วน &amp;quot;ราชันชุดขาว&amp;quot;รีล มาดริด ที่เสมอซีเอสเคเอ มอสโก มาในนัดแรกเปิดสนามซานติอาโก้ เบอร์นาบิว เอาชนะไป 4-1 ทำให้รวมผลสองนัดรีล มาดริด เข้ารอบด้วยสกอร์รวม 5-2&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(0, 0, 64); font-family: \'Times New Roman\'; text-align: -webkit-left; font-size: medium; &quot;&gt;\r\n	&amp;nbsp;เกมนี้คริสเตียโน่ โรนัลโด้ เหมาคนเดียวสองประตูในนาที 55 และ 90, กอนซาโล่ อิกัวอิน นาที 26 และคาริม เบนเซม่า นาที 70 ส่วนทีมเยือนได้จาก โซรัน โทซิช นาที 77&lt;br /&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://i.dailymail.co.uk/i/pix/2012/03/14/article-2115030-122C2963000005DC-150_634x393.jpg&quot; style=&quot;width: 634px; height: 393px; &quot; /&gt;&lt;/p&gt;', '1331772010', '1331772165', '1', '22');
INSERT INTO `news` VALUES ('13', 'ขวัญใจชาวไทย', '&lt;img src=&quot;http://www.thairath.co.th/media/content/2012/08/07/281730/hr1667/275.jpg&quot; style=&quot;margin: 0px; color: rgb(102, 102, 102); font-family: Tahoma, \'Lucida Grande\', Verdana, Arial, sans-serif; text-align: left; background-color: rgb(239, 245, 229); &quot; /&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma, \'Lucida Grande\', Verdana, Arial, sans-serif; text-align: left; background-color: rgb(239, 245, 229); &quot;&gt;นายนินัสชัย อินทนนท์ และนางคำผัน สุวรรณศาลา พ่อแม่ของ &amp;ldquo;น้องเมย์&amp;rdquo; นักแบดมินตันสาวไทยที่แพ้คู่ต่อสู้มือ 2 ของโลกอย่างประทับใจชาวไทย เข้าสวมกอดลูกสาวที่เดินทางกลับไทยแล้วหลังจบศึกลอนดอนเกมส์.&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;', '1344331273', '1344331273', '1', '4');
INSERT INTO `news` VALUES ('15', 'เครือข่ายคุก', '&lt;img src=&quot;http://www.thairath.co.th/media/content/2012/08/07/281729/hr1667/275.jpg&quot; style=&quot;margin: 0px; color: rgb(102, 102, 102); font-family: Tahoma, \'Lucida Grande\', Verdana, Arial, sans-serif; text-align: left; background-color: rgb(239, 245, 229); &quot; /&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma, \'Lucida Grande\', Verdana, Arial, sans-serif; text-align: left; background-color: rgb(239, 245, 229); &quot;&gt;พล.ต.ท.คำรณวิทย์ ธูปกระจ่าง แถลงชุดจู่ โจม สน.บางซื่อ จับผู้ต้องหา 3 คน พร้อมแท่นปั๊มยาบ้า และสารประกอบจำนวนมาก ที่บ้านเช่าย่านพุทธมณฑลสาย 3 สารภาพหัวหน้าแก๊งอยู่คุกบางขวาง.&lt;/span&gt;', '1344331493', '1344331493', '1', '9');
INSERT INTO `test` VALUES ('9', '8', '&lt;span style=&quot;font-size:16px;&quot;&gt;รถบัสกำลังเคลื่อนที่ออกจากป้ายด้วยความเร่ง 1.0 m/s&lt;sup&gt;2&lt;/sup&gt; ชายผู้หนึ่งวิ่งไล่กวดรถบัสจากระยะห่าง 6.0 m ด้วยความเร็วคงที่ 3.5 m/s จะต้องไล่กวดนานกี่วินาทีจึงจะทันรถบัส&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '1', 'A');
INSERT INTO `test` VALUES ('10', '8', '&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;/s49/49042380148/upload/userfiles/images/Physics/Question-1/9-8-2555%2018-24-00.jpg&quot; style=&quot;width: 524px; height: 386px; &quot; /&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;ข้อใดในภาพเป็นคำตอบที่ถูกต้อง เลือกข้อที่คิดว่าถูก&lt;/span&gt;', '1', 'A');
INSERT INTO `test` VALUES ('11', '8', '&lt;span style=&quot;font-size:16px;&quot;&gt;เด็กคนหนึ่งโยนพวงกุญแจขึ้นไปแนวดิ่งเพื่อให้เพื่อนที่อยู่บนระเบียงสูงขึ้นไป และพบว่าเพื่อนรับกูญแจได้ในเวลา 2 วินาทีต่อมา ถ้าจุดที่รับอยู่สูงกว่าจุดที่โยน 4 เมตร พวงกุญแจถึงมือผู้รับด้วยความเร็วเท่าใด&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '3', 'A');
INSERT INTO `test` VALUES ('12', '8', '&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;ในขณะถ่ายภาพยนต์กล้องวีดีโอได้ถ่ายภาพการเคลื่อนที่ของกระถางที่ตกจากระเบียงของตึกสูงแห่งหนึ่ง ซึ่งพบว่ากระถางใช้เวลาในการเคลื่อนที่ผ่านหน้าต่างชั้นล่าง ซึ่งมีความสูง 1.5 เมตร ในเวลา 0.03 วินาที จงคำนวนว่าจุดที่กระถางตกลงมามีความสูง จากหน้าต่างชั้นล่างประมา&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '2', 'A');
INSERT INTO `test` VALUES ('13', '8', '&lt;br /&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;/s49/49042380148/upload/userfiles/images/Physics/Question-1/9-8-2555%2021-29-50.jpg&quot; style=&quot;width: 600px; height: 386px; &quot; /&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;ข้อใดในภาพเป็นคำตอบที่ถูกต้อง เลือกข้อที่คิดว่าถูก&lt;/span&gt;', '4', 'A');
INSERT INTO `test` VALUES ('14', '9', '&lt;span style=&quot;font-size:16px;&quot;&gt;นักบินอวกาศจะมีน้ำหนักกี่เท่าของน้ำหนักที่ชั่งบนโลกถ้าอยู่บนดาวเคราะห์ที่มีรัศมีหนึ่งหนึ่งของโลกและมีมวลเป็น 18 ของมวลโลก&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '2', 'A');
INSERT INTO `test` VALUES ('15', '9', '&lt;span style=&quot;font-size:16px;&quot;&gt;รถบรรทุกมีมวล 5000 กิโลกรัม เคลื่อนที่บนพื้นราบในแนวเส้นตรงด้วยความเร็ว 20 เมตร/วินาที ถ้าต้องการให้รถนี้หยุดสนิทในระยะทาง 50 เมตร จะต้องใช้แรงต้านทานเท่าใด&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '3', 'A');
INSERT INTO `test` VALUES ('16', '9', '&lt;span style=&quot;font-size:16px;&quot;&gt;แรงคงที่ขนาดหนึ่งผลักมวลวัตถุมวล 80 กิโลกรัม บนพื้นราบที่ไม่มีความฝืดสามารถเปลี่ยนความเร็ว 3 เมตร/วินาที เป็น 4 เมตร/วินาที ในทิศเดิมและใน 1 วินาที จงหาว่าหากใช้แรงขนาดเดียวกัน จะทำให้วัตถุเร็วเพิ่มขึ้นเท่าไรในเวลา 1 วินาที เท่ากัน&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '4', 'A');
INSERT INTO `test` VALUES ('17', '9', '&lt;img alt=&quot;&quot; src=&quot;/s49/49042380148/upload/userfiles/images/Physics/Question-2/9-8-2555%2019-43-28.jpg&quot; style=&quot;width: 659px; height: 426px; &quot; /&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;ข้อใดในภาพเป็นคำตอบที่ถูกต้อง เลือกข้อที่คิดว่าถูก&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '3', 'A');
INSERT INTO `test` VALUES ('19', '8', '&lt;span style=&quot;font-size:16px;&quot;&gt;ลิฟต์เครื่องหนึ่งสามารถเคลื่อนที่ด้วยความเร่งในทิศขึ้นหรือลงได้เท่ากับ 12 m/s&lt;sup&gt;2 &amp;nbsp;&lt;/sup&gt;และสามารถทำอัตราเร็วสูงสุดได้เท่ากับ 4.8 เมตรต่วินาที ถ้าต้องการขนของจากชั้นล่างขึ้นไปยังชั้นที่ 16 ซึ่งมีความสูง 48 เมตร จงหาช่วงเวลาที่สั้นที่สุดในการขนของด้วยลิฟต์ตัวนี้&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;', '2', 'B');
